/**
 * Initiates a download.
 *
 * <p>Queues up a download to the download manager, and saves the DownloadManager's assigned
 * download ID for this download to a file.
 *
 * @throws Exception if unsuccessful
 */
public void initiateDownload() throws Exception {
  String filename = DOWNLOAD_5MB_FILENAME;
  mContext.deleteFile(DOWNLOAD_STARTED_FLAG);
  FileOutputStream fileOutput = mContext.openFileOutput(DOWNLOAD_STARTED_FLAG, 0);
  DataOutputStream outputFile = null;
  doCommonDownloadSetup();

  try {
    long dlRequest = -1;

    // Make sure there are no pending downloads currently going on
    removeAllCurrentDownloads();

    Uri remoteUri = getExternalFileUri(filename);
    Request request = new Request(remoteUri);

    dlRequest = mDownloadManager.enqueue(request);
    waitForDownloadToStart(dlRequest);
    assertTrue(dlRequest != -1);

    // Store ID of download for later retrieval
    outputFile = new DataOutputStream(fileOutput);
    outputFile.writeLong(dlRequest);
  } finally {
    if (outputFile != null) {
      outputFile.flush();
      outputFile.close();
    }
  }
}

private final void speedTestStream(int bufferSize) throws IOException {
  final Channel.ID tmpChannel = ioManager.createChannel();
  final IntegerRecord rec = new IntegerRecord(0);

  File tempFile = null;
  DataOutputStream daos = null;
  DataInputStream dais = null;

  try {
    tempFile = new File(tmpChannel.getPath());

    FileOutputStream fos = new FileOutputStream(tempFile);
    daos = new DataOutputStream(new BufferedOutputStream(fos, bufferSize));

    long writeStart = System.currentTimeMillis();

    int valsLeft = NUM_INTS_WRITTEN;
    while (valsLeft-- > 0) {
      rec.setValue(valsLeft);
      rec.write(daos);
    }
    daos.close();
    daos = null;

    long writeElapsed = System.currentTimeMillis() - writeStart;

    // ----------------------------------------------------------------

    FileInputStream fis = new FileInputStream(tempFile);
    dais = new DataInputStream(new BufferedInputStream(fis, bufferSize));

    long readStart = System.currentTimeMillis();

    valsLeft = NUM_INTS_WRITTEN;
    while (valsLeft-- > 0) {
      rec.read(dais);
    }
    dais.close();
    dais = null;

    long readElapsed = System.currentTimeMillis() - readStart;

    LOG.info(
        "File-Stream with buffer "
            + bufferSize
            + ": write "
            + writeElapsed
            + " msecs, read "
            + readElapsed
            + " msecs.");
  } finally {
    // close if possible
    if (daos != null) {
      daos.close();
    }
    if (dais != null) {
      dais.close();
    }
    // try to delete the file
    if (tempFile != null) {
      tempFile.delete();
    }
  }
}

