/**
 * @param inFile Name of a tag map file in the format supplied by Harpreet.
 * @param taxonNameFile Name of a file which lists biological line names present in infile, one
 *     per line.
 * @param outFile Name of a file in which to write new tag map.
 */
public void convertCBSUFile(File inFile, File taxonNameFile, File outFile) {
  System.out.println("Reading tag alignment from:" + inFile.toString());
  String[] inputLine = {"Not", "Started"};
  int numTaxonNames = 0; // Number of distinct taxon names from taxonNameFile
  int currLine = 0;

  try {
    BufferedReader taxonNameCounter = new BufferedReader(new FileReader(taxonNameFile), 65536);
    while (taxonNameCounter.readLine() != null) {
      numTaxonNames++;
    } // 1st pass through file counts lines
    taxonNameCounter.close();
  } catch (Exception e) {
    System.out.println("Catch in counting taxon names: " + e);
  }

  String[] taxonNames = new String[numTaxonNames];
  try {
    BufferedReader taxonNameReader = new BufferedReader(new FileReader(taxonNameFile), 65536);
    for (int i = 0; i < numTaxonNames; i++) {
      taxonNames[i] = taxonNameReader.readLine().trim();
    } // Trim each line name and add to array
    taxonNameReader.close();
  } catch (Exception e) {
    System.out.println("Catch in reading taxon names: " + e);
  }
  System.out.println("Taxa names read. TaxaNumber:" + numTaxonNames);
  try {
    BufferedReader br = new BufferedReader(new FileReader(inFile), 65536);
    BufferedReader tagCounter = new BufferedReader(new FileReader(inFile), 65536);
    DataOutputStream fw =
        new DataOutputStream(new BufferedOutputStream(new FileOutputStream(outFile), 65536));
    int tagNum = 0;
    int tagLengthInLong = 2;
    ReadBarcodeResult tagProcessingResults = null;
    byte tagLength;
    String tagSequence;
    long[] binaryTagSequence = new long[tagLengthInLong];
    System.out.print("Tag counting....");
    while (tagCounter.readLine() != null) {
      tagNum++;
    }
    tagCounter.close(); // Loop through tag map file, count #tags (i.e. #lines) and then close
    System.out.println(" TagNumber:" + tagNum);
    try {
      fw.writeInt(tagNum);
      fw.writeInt(tagLengthInLong);
      fw.writeInt(numTaxonNames);
      for (int t = 0; t < numTaxonNames; t++) {
        fw.writeUTF(taxonNames[t]);
      }
      for (int row = 0; row < tagNum; row++) { // Fill byte arrays
        if (row % 100000 == 0) System.out.println("Processed Row:" + row);
        inputLine = br.readLine().split("\t");
        String corrSeq = inputLine[5];

        if (!corrSeq.startsWith("CAGC")
            && !corrSeq.startsWith("CTGC")
            && !corrSeq.endsWith("CGTC")
            && !corrSeq.endsWith("CGAC")) {
          continue;
        }

        if (cleanCutSites) {
          if (ParseBarcodeRead.getInitialCutSiteRemnant() == null) {
            ParseBarcodeRead.chooseEnzyme("ApeKI");
          }
          tagProcessingResults =
              ParseBarcodeRead.removeSeqAfterSecondCutSite(
                  corrSeq, (byte) (tagLengthInLong * 32));
        } // Process tag sequence to find cut sites
        if (tagProcessingResults.processedSequence != null) {
          tagLength =
              tagProcessingResults
                  .getLength(); // If cut site was found, write length of processed tag...
          tagSequence = tagProcessingResults.paddedSequence; // And sequence of processed tag...
        } else {
          tagLength = (byte) corrSeq.length(); // ...otherwise write length of unprocessed tag.
          tagSequence = corrSeq; // ...and sequence of unprocessed tag.
        }

        binaryTagSequence =
            BaseEncoder.getLongArrayFromSeq(
                tagSequence); // Convert sequence from string to long[]

        for (int j = 0; j < tagLengthInLong; j++) {
          fw.writeLong(binaryTagSequence[j]); // Write long[] array of sequence to disk
        }
        fw.writeByte(tagLength);

        OpenBitSet obs =
            new OpenBitSet(numTaxonNames); // New binary object to store presence/absence data
        for (int t = 0; t < numTaxonNames; t++) { // Create a marker presence/absence bitmap
          // if(inputLine[9].substring(t, t+1).matches("1")){
          // obs.set(t);
          // }
          if (inputLine[9].charAt(t) == '1') {
            obs.set(t);
          }
        }

        long[] obsInLong = obs.getBits();
        for (int t = 0; t < obsInLong.length; t++) {
          fw.writeLong(obsInLong[t]); // Write presence/absence bitmap to file
        }
      }
      fw.close();
      br.close();
    } catch (Exception e) {
      System.out.println("Catch in writing output file: " + e);
      e.printStackTrace();
    }
  } catch (Exception e) {
    System.out.println("Catch in reading tag map file: " + e);
  }
}

@Override
protected byte[] doTransform(
    ClassLoader loader,
    String className,
    Class classBeingRedefined,
    ProtectionDomain protectionDomain,
    byte[] classfileBuffer) {
  ClassFile classfile;
  try {
    // WARNING: classfile only
    classfile = new ClassFile(new DataInputStream(new ByteArrayInputStream(classfileBuffer)));
  } catch (IOException e) {
    LOG.unableToBuildEnhancementMetamodel(className);
    return classfileBuffer;
  }
  // This is the same as ClassPool.getDefault() but ensures a new ClassPool per
  ClassPool cp = new ClassPool();
  cp.appendSystemPath();
  cp.appendClassPath(new ClassClassPath(this.getClass()));
  cp.appendClassPath(new ClassClassPath(classfile.getClass()));
  try {
    cp.makeClassIfNew(new ByteArrayInputStream(classfileBuffer));
  } catch (IOException e) {
    throw new RuntimeException(e.getMessage(), e);
  }
  FieldTransformer transformer = getFieldTransformer(classfile, cp);
  if (transformer != null) {
    LOG.debugf("Enhancing %s", className);
    DataOutputStream out = null;
    try {
      transformer.transform(classfile);
      ByteArrayOutputStream byteStream = new ByteArrayOutputStream();
      out = new DataOutputStream(byteStream);
      classfile.write(out);
      return byteStream.toByteArray();
    } catch (Exception e) {
      LOG.unableToTransformClass(e.getMessage());
      throw new HibernateException("Unable to transform class: " + e.getMessage());
    } finally {
      try {
        if (out != null) out.close();
      } catch (IOException e) {
        // swallow
      }
    }
  }
  return classfileBuffer;
}

public static void main(String argv[]) throws IOException {
  int i, predict_probability = 0;

  // parse options
  for (i = 0; i < argv.length; i++) {
    if (argv[i].charAt(0) != '-') break;
    ++i;
    switch (argv[i - 1].charAt(1)) {
      case 'b':
        predict_probability = atoi(argv[i]);
        break;
      default:
        System.err.print("unknown option\n");
        exit_with_help();
    }
  }
  if (i >= argv.length) exit_with_help();
  try {
    BufferedReader input = new BufferedReader(new FileReader(argv[i]));
    DataOutputStream output = new DataOutputStream(new FileOutputStream(argv[i + 2]));
    svm_model model = svm.svm_load_model(argv[i + 1]);
    if (predict_probability == 1)
      if (svm.svm_check_probability_model(model) == 0) {
        System.err.print("Model does not support probabiliy estimates\n");
        System.exit(1);
      }
    predict(input, output, model, predict_probability);
    input.close();
    output.close();
  } catch (FileNotFoundException e) {
    exit_with_help();
  } catch (ArrayIndexOutOfBoundsException e) {
    exit_with_help();
  }
}

