/**
 * Get a URL encoded version of the string. same as URLEncoder.encode(toEncode, "UTF-8") but the
 * checked exception is caught since UTF-8 is mandatory for all Java virtual machines.
 *
 * @param toEncode the string to URL encode.
 * @return a URL encoded version of toEncode.
 * @see #getURLDecoded(String)
 */
static String getURLEncoded(final String toEncode) {
  try {
    return URLEncoder.encode(toEncode, CHARSET);
  } catch (UnsupportedEncodingException ex) {
    throw new RuntimeException(NO_CHARSET);
  }
}

private static String encode(final String content, final String encoding) {
  try {
    return URLEncoder.encode(content, encoding != null ? encoding : HTTP.DEFAULT_CONTENT_CHARSET);
  } catch (UnsupportedEncodingException problem) {
    throw new IllegalArgumentException(problem);
  }
}

@Override
public void sendText(final TextableEndpoint from, final String text, final String type)
    throws IOException {
  ImifiedEndpointImpl bot = null;
  if (from instanceof ImifiedEndpointImpl) {
    bot = (ImifiedEndpointImpl) from;
  } else {
    throw new IllegalArgumentException("The from endpoint is not an ImifiedEndpoint instance.");
  }

  final HttpPost post = new HttpPost(_driver.getIMifiedURL());

  final String up = bot.getImifiedUserName() + ":" + bot.getImifiedPasswd();
  final String value = "Basic " + new String(Base64.encodeBase64(up.getBytes("UTF-8")), "UTF-8");
  post.addHeader("Authorization", value);

  final StringBuilder sb = new StringBuilder();
  sb.append("botkey=").append(bot.getKey());
  sb.append("&apimethod=send&userkey=").append(getKey());
  sb.append("&msg=").append(URLEncoder.encode(text, "UTF-8"));

  final HttpEntity en = new StringEntity(sb.toString());
  post.setEntity(en);
  post.setHeader("Content-Type", "application/x-www-form-urlencoded");
  post.setHeader("Connection", "keep-alive");

  if (LOG.isDebugEnabled()) {
    LOG.debug(
        "Sending message from "
            + bot.getAddress()
            + " to "
            + getAddress()
            + ", content:"
            + sb.toString());
  }
  HttpEntity resEntity = null;
  try {
    final HttpResponse response = _driver.execute(post);
    resEntity = response.getEntity();
    final BufferedReader reader =
        new BufferedReader(new InputStreamReader(resEntity.getContent()));

    if (resEntity
        .getContentType()
        .getValue()
        .trim()
        .toLowerCase()
        .startsWith("application/xml")) {
      String firstLine = null;
      do {
        firstLine = reader.readLine();
      } while (firstLine.trim().length() == 0);

      if (!(firstLine.indexOf("stat=\"ok\"") > 0)) {
        final StringBuilder output = new StringBuilder();
        output.append(firstLine).append("\r\n");
        String line = null;
        while ((line = reader.readLine()) != null) {
          output.append(line).append("\r\n");
        }
        throw new IOException("Imified returned error:" + output.toString());
      }
    } else {
      throw new IOException("Imified returned error message:" + reader.readLine());
    }

    if (LOG.isDebugEnabled()) {
      LOG.debug("Message sent.");
    }
  } finally {
    if (resEntity != null) {
      resEntity.consumeContent();
    }
  }
}

/**
 * Finish up building the URL, and return it as a string.
 *
 * @param builder StringBuilder that contains the base URL from which to start adding URL
 *     properties.
 * @param hasProperties Does the given URL already contains other properties?
 * @return Returns the URL with the newly added properties for filtering.
 */
public String finish(StringBuilder builder, boolean hasProperties)
    throws UnsupportedEncodingException {

  this.hasProperties = hasProperties;

  List<String> props = new ArrayList<String>(properties.keySet());
  if (sortProperties) {
    Collections.sort(queryable);
    Collections.sort(props);
  }

  // Add all properties to the URL:
  for (String property : props) {
    builder.append(getGlueChar());
    builder.append(URLEncoder.encode(property, strEncoding));

    builder.append("=");
    builder.append(URLEncoder.encode(properties.get(property), strEncoding));
  }

  // Add queryable properties to the URL if needed:
  if (queryable != null && queryable.size() > 0) {
    builder.append(getGlueChar());
    builder.append("queryable=");
    for (int i = 0; i < queryable.size(); i++) {

      builder.append(URLEncoder.encode(queryable.get(i), strEncoding));
      if (i < queryable.size() - 1) {
        builder.append(",");
      }
    }
  }

  // Cleanup:
  properties.clear();
  queryable.clear();
  return builder.toString();
}

