@Override
public void serviceProxy(HttpServletRequest request, HttpServletResponse servletResponse)
    throws ServletException, IOException {
  // TODO make use of findUrl functionality here instead of cloning the EndpointBean
  try {
    String endpointName = request.getParameter(PARAM_ENDPOINT_NAME);
    if (StringUtil.isEmpty(endpointName)) {
      endpointName = TYPE;
    }
    Endpoint bean = EndpointFactory.getEndpoint(endpointName);

    if (bean == null) {
      throw new ServletException("AuthorizationBean not found in application scope");
    }

    // TODO - padraic review with phil what clone is about
    AbstractEndpoint clonedBean = (AbstractEndpoint) bean;
    clonedBean.setUrl("https://api-content.dropbox.com/");

    String path = request.getParameter("path");

    // DropboxFiles Service - https://api-content.dropbox.com/<version>/files/dropbox/<path>
    DropboxService svc = new DropboxService(endpointName);
    String href = buildHref(path, bean);
    HandlerInputStream handler = new HandlerInputStream();
    Object file;
    try {
      file = svc.get(href, handler).getData();
    } catch (ClientServicesException e) {
      throw new FacesExceptionEx(e, "Failed to execute proxy request");
    }

    servletResponse.setContentType(request.getParameter("mimeType"));

    String nameUTF8 = URLEncoder.encode(request.getParameter("path"), "utf-8"); // $NON-NLS-1$
    nameUTF8 = nameUTF8.replaceAll("\\+", "%20"); // $NON-NLS-1$ //$NON-NLS-2$

    // PEDS954UQU
    String userAgent = request.getHeader("User-Agent"); // $NON-NLS-1$
    if (StringUtil.isNotEmpty(userAgent) && userAgent.contains("Firefox")) // $NON-NLS-1$
      // use the format of value of non-acsii charset: filename*="utf8''nameUTF8"
      servletResponse.setHeader(
          "Content-Disposition",
          "attachment; filename*=\"utf8\'\'"
              + nameUTF8
              + '"'); // $NON-NLS-1$ $NON-NLS-2$ $NON-NLS-3$
    else
      servletResponse.setHeader(
          "Content-Disposition",
          "attachment; filename=" + nameUTF8 + ""); // $NON-NLS-1$ $NON-NLS-2$ $NON-NLS-3$

    OutputStream out = servletResponse.getOutputStream();
    try {
      StreamUtil.copyStream((InputStream) file, out);
    } finally {
      out.close();
    }
  } catch (Exception e) {
    throw new ServletException(e);
  }
}

public void sign(SamlRedirectMessage urlEncodedRedirectMessage, PrivateKey signingKey)
    throws IOException, GeneralSecurityException {
  urlEncodedRedirectMessage.setSignatureAlgorithm(
      getXMLSignatureAlgorithmURI(signingKey.getAlgorithm()));

  byte[] signature = computeSignature(urlEncodedRedirectMessage.createQueryString(), signingKey);

  String base64encodedSignature = Base64.encodeBytes(signature, Base64.DONT_BREAK_LINES);

  String urlEncodedSignature = URLEncoder.encode(base64encodedSignature, "UTF-8");

  urlEncodedRedirectMessage.setSignature(urlEncodedSignature);
}

@Override
public boolean handleEnter(Object arg, boolean isReturn) {
  boolean status = super.handleEnter(arg, isReturn);

  // Set the background when entering the screen

  // constants used for screen layout
  double screenHeight = this.app.getHeight();
  double screenWidth = this.app.getWidth();
  double aspectRatio = screenWidth / screenHeight;

  // Display the background image
  if (this.app.isInSimulator()) {
    // We are in the simulator, so set a PNG background.

    // Change the background based on the new aspect ratio.
    if ((aspectRatio > 1.7) && (aspectRatio < 1.8)) {
      // The current aspect ratio is approximately 16:9.
      // Use the high definition background meant for 720p.
      String url = this.getContext().getBaseURI().toString();

      try {
        url += URLEncoder.encode("now_playing_720.png", "UTF-8");
      } catch (UnsupportedEncodingException e) {
      }

      this.app.getRoot().setResource(this.createStream(url, "image/png", true));
    } else {
      // Default background is standard definition 640 x 480 (4:3)
      this.app.getRoot().setResource("now_playing_sd.png");
    }
  } else {
    // We are running on a real TiVo, so use an MPEG background to conserve memory.

    // Change the background based on the new aspect ratio.
    if ((aspectRatio > 1.7) && (aspectRatio < 1.8)) {
      // The current aspect ratio is approximately 16:9.
      // Use the high definition background meant for 720p.
      this.app.getRoot().setResource("now_playing_720.mpg");
    } else {
      // Default background is standard definition 640 x 480 (4:3)
      this.app.getRoot().setResource("now_playing_sd.mpg");
    }
  }

  return status;
}

/**
 * Convertes give value to JSON and encodes the converted value with a proper encoding. Data type
 * is sometimes required, especially for Java generic types, because type information is erased at
 * runtime and the conversion to JSON will not work properly.
 *
 * @param value value to be converted and encoded
 * @param type data type of the value object. Any primitive type, array, non generic or generic
 *     type is supported. Data type is sometimes required to convert a value to a JSON
 *     representation. All data types should be fully qualified. Examples: "boolean" "int"
 *     "long[]" "java.lang.String" "java.util.Date" "java.util.Collection<java.lang.Integer>"
 *     "java.util.Map<java.lang.String, com.durr.FooPair<java.lang.Integer, java.util.Date>>"
 *     "com.durr.FooNonGenericClass" "com.durr.FooGenericClass<java.lang.String,
 *     java.lang.Integer>" "com.durr.FooGenericClass<int[],
 *     com.durr.FooGenericClass<com.durr.FooNonGenericClass, java.lang.Boolean>>".
 * @return String converted and encoded value
 * @throws UnsupportedEncodingException DOCUMENT_ME
 */
public String encodeJson(Object value, String type) throws UnsupportedEncodingException {
  jsonConverter.setType(type);

  String jsonValue;
  if (value == null) {
    jsonValue = "null";
  } else {
    jsonValue = jsonConverter.getAsString(null, null, value);
  }

  return URLEncoder.encode(jsonValue, encoding);
}

