public String getMessageStatus(GatewayResponse response) {
  try {
    postData = "api_id=" + URLEncoder.encode(apiId, "UTF-8");
    postData += "&user=" + URLEncoder.encode(user, "UTF-8");
    postData += "&password=" + URLEncoder.encode(password, "UTF-8");
    postData += "&apimsgid=" + URLEncoder.encode(response.getGatewayMessageId(), "UTF-8");
  } catch (UnsupportedEncodingException ex) {
    logger.fatal("Error constructing request: parameter encoding failed", ex);
    throw new RuntimeException("Error constructing message");
  }

  // Create a url and open a connection to it
  URL url;
  URLConnection conn;

  try {
    url = new URL(baseUrl + "querymsg");
    conn = url.openConnection();
    conn.setRequestProperty("Content-Type", "application/x-www-form-urlencoded");
  } catch (MalformedURLException ex) {
    logger.fatal("Error initializing Clikatell Gateway: invalid url", ex);
    throw new RuntimeException("Invalid gatewat URL");
  } catch (IOException ex) {
    logger.fatal("Error iitializing Clickatell Gateway: unable to open URL connection", ex);
    throw new RuntimeException("Could not connect to gateway");
  }
  // Read in the gateway response
  BufferedReader in;
  String data = "";
  String gatewayResponse = "";

  // Flush the post data to the url
  try {
    conn.setDoOutput(true);
    OutputStreamWriter out = new OutputStreamWriter(conn.getOutputStream());
    out.write(postData);
    out.flush();

    in = new BufferedReader(new InputStreamReader(conn.getInputStream()));
    while ((data = in.readLine()) != null) gatewayResponse += data + "\n";

    // Close the connections to the url reader and writer
    out.close();
    in.close();
  } catch (IOException ex) {
    logger.fatal("Error processing gateway request", ex);
    throw new RuntimeException("Unable to communicate with gateway");
  }

  return gatewayResponse;
}

/**
 * This method will get the course rating based on course name
 *
 * @param course_name
 * @return
 * @throws URISyntaxException
 * @throws HttpException
 * @throws UnsupportedEncodingException
 * @throws ParseException
 */
public double getCourseRatingByName(String course_name)
    throws URISyntaxException, HttpException, UnsupportedEncodingException, ParseException {
  // client.oauth2Login( client.getUserCredentials());
  String newRestUri =
      "/query/?q="
          + URLEncoder.encode(
              "SELECT rating__c "
                  + "FROM course_average_rating__c WHERE courseName__c='"
                  + course_name
                  + "'",
              "UTF-8");

  String response = client.restGet(newRestUri);
  JSONParser parser = new JSONParser();
  Object obj = parser.parse(response);
  JSONObject jsonObject = (JSONObject) obj;

  JSONArray listOfRecords = (JSONArray) jsonObject.get("records");
  if (listOfRecords.size() > 0) {
    JSONObject jsonAttribute = (JSONObject) listOfRecords.get(0);
    return Double.parseDouble(jsonAttribute.get("rating__c").toString());
  }

  return 0;
}

/**
 * Performs the initial blanket search.
 *
 * @param request
 * @param response
 * @return
 */
public ModelAndView handleRequest(HttpServletRequest request, HttpServletResponse response)
    throws Exception {
  String originalRequestParam = request.getParameter(requestParamName);
  if (logger.isDebugEnabled()) {
    logger.debug("Original request parameter: " + originalRequestParam);
  }
  String path = request.getContextPath() + redirectRootUrl;

  if (StringUtils.isNotEmpty(originalRequestParam) && originalRequestParam.trim().length() > 0) {
    originalRequestParam = originalRequestParam.trim();
    if (supportWildcardSubstitution) {
      for (int i = 0; i < supportedWildcards.length; i++) {
        originalRequestParam = originalRequestParam.replace(supportedWildcards[i], wildcard);
      }
    }
    try {
      if (decodeArgument) {
        originalRequestParam = URLDecoder.decode(originalRequestParam, "UTF-8");
        if (logger.isDebugEnabled()) {
          logger.debug("URL decoded value: " + originalRequestParam);
        }
      }
      // replace whitespace with underscores for appearances
      if (whitespace2Underscores) {
        originalRequestParam = QueryHelper.tidyValue(originalRequestParam);
        originalRequestParam = originalRequestParam.replace(" ", "_");
        if (logger.isDebugEnabled()) {
          logger.debug("pretty print value: " + originalRequestParam);
        }
      }

    } catch (Exception e) {
      logger.error(e.getMessage(), e);
    }

    String pathSuffix = originalRequestParam;
    if (encodePath) {
      pathSuffix = URLEncoder.encode(originalRequestParam, "UTF-8");
    }
    path = path + pathSuffix;
    if (logger.isDebugEnabled()) {
      logger.debug("Redirecting to: " + path);
    }
  }
  response.setCharacterEncoding("UTF-8");
  response.sendRedirect(path);
  return null;
}

