public void doFilter(
    final ServletRequest request, final ServletResponse response, final FilterChain chain)
    throws IOException, ServletException {

  final HttpServletRequest req = (HttpServletRequest) request;
  final HttpServletResponse resp = (HttpServletResponse) response;
  final HttpSession session = req.getSession();
  final String url = req.getRequestURL().toString();
  final URL reqURL = new URL(url);

  final SecurityContext secCtx = SecurityContextHolder.getContext();
  final Authentication auth = secCtx.getAuthentication();
  if (LOG.isDebugEnabled())
    LOG.debug(
        "URL="
            + url
            + " Security context authentication="
            + auth
            + " Session id="
            + session.getId());

  if (auth != null
      && (auth instanceof OpenIDAuthenticationToken
          || auth instanceof PreAuthenticatedAuthenticationToken)) {

    final String openid = auth.getName();
    if (LOG.isDebugEnabled()) LOG.debug("User is authenticated, openid=" + openid);

    // retrieve OpenID Exchange Attributes
    if (auth instanceof OpenIDAuthenticationToken) {
      final OpenIDAuthenticationToken token = (OpenIDAuthenticationToken) auth;
      final List<OpenIDAttribute> attributes = token.getAttributes();
      if (LOG.isDebugEnabled()) {
        for (OpenIDAttribute attribute : attributes) {
          LOG.info(
              "Retrieved OpenID attribute="
                  + attribute.getName()
                  + " type="
                  + attribute.getType()
                  + " value="
                  + attribute.getValues().get(0));
        }
      }
    }

    if (WebUtils.getCookie(req, Parameters.OPENID_SAML_COOKIE) == null) {

      try {

        // build signed authentication statement
        final String authstmt = samlStatementFacade.buildSignedAuthenticationStatement(openid);
        if (LOG.isDebugEnabled()) {
          LOG.debug("Authentication statement:");
          LOG.debug(authstmt);
        }

        // set cookie (value must be encoded)
        final Cookie cookie =
            new Cookie(Parameters.OPENID_SAML_COOKIE, URLEncoder.encode(authstmt, "UTF-8"));
        // cookie.setSecure(true); // cookie will be transmitted only over HTTPS
        cookie.setMaxAge(-1); // cookie will be deleted when session is terminated
        cookie.setDomain(reqURL.getHost()); // cookie sent to all applications on this host
        cookie.setPath("/"); // cookie will be sent to all pages in web application
        resp.addCookie(cookie);
        if (LOG.isDebugEnabled()) {
          LOG.debug("Setting authentication cookie:");
          this.printCookie(cookie);
        }

      } catch (Exception e) {
        throw new ServletException(e);
      }

    } else {
      if (LOG.isDebugEnabled()) {
        LOG.debug("Found authentication cookie:");
        this.printCookie(WebUtils.getCookie(req, Parameters.OPENID_SAML_COOKIE));
      }
    }

    // redirect
    if (session.getAttribute(Parameters.OPENID_REDIRECT) != null) {
      final String redirect = (String) session.getAttribute(Parameters.OPENID_REDIRECT);
      if (LOG.isDebugEnabled()) LOG.debug("Redirecting to: " + redirect);
      // remove "redirect" parameter from session
      session.removeAttribute(Parameters.OPENID_REDIRECT);
      resp.sendRedirect(redirect);
    } else {
      chain.doFilter(request, response);
    }

  } else {

    // set HTTP STATUS CODE = 401 while returning login page on redirect
    // note: don't do this for any other URL
    if (url.endsWith(Parameters.LOGIN_URL)
        && StringUtils.hasText((String) session.getAttribute(Parameters.OPENID_REDIRECT))) {
      resp.setStatus(HttpServletResponse.SC_UNAUTHORIZED);
    }

    // keep processing
    chain.doFilter(request, response);
  }
}

@Override
public void respond(
    final HttpServletRequest httpRequest, final HttpServletResponse httpResponse) {

  LOGGER.info("Responding to an OMH read request.");

  // If either request has failed, set the response's status code.
  if (isFailed()) {
    if (ErrorCode.SYSTEM_GENERAL_ERROR.equals(getAnnotator().getErrorCode())) {

      httpResponse.setStatus(HttpServletResponse.SC_INTERNAL_SERVER_ERROR);
    } else {
      httpResponse.setStatus(HttpServletResponse.SC_BAD_REQUEST);
    }

    super.respond(httpRequest, httpResponse, null);
    return;
  } else if (userRequest.isFailed()) {
    if (ErrorCode.SYSTEM_GENERAL_ERROR.equals(userRequest.getAnnotator().getErrorCode())) {

      httpResponse.setStatus(HttpServletResponse.SC_INTERNAL_SERVER_ERROR);
    } else {
      httpResponse.setStatus(HttpServletResponse.SC_BAD_REQUEST);
    }

    // Then, force the request to respond.
    userRequest.respond(httpRequest, httpResponse);
    return;
  } else if (userRequest instanceof OmhReadResponder) {
    OmhReadResponder omhReadResponder = (OmhReadResponder) userRequest;

    // Refresh the token cookie.
    userRequest.refreshTokenCookie(httpResponse);

    // Expire the response, but this may be a bad idea.
    expireResponse(httpResponse);

    // Set the content type to JSON.
    httpResponse.setContentType("application/json");

    // Connect a stream to the response.
    OutputStream outputStream;
    try {
      outputStream = getOutputStream(httpRequest, httpResponse);
    } catch (IOException e) {
      LOGGER.warn("Could not connect to the output stream.", e);
      return;
    }

    // Create the generator that will stream to the requester.
    JsonGenerator generator;
    try {
      generator = JSON_FACTORY.createJsonGenerator(outputStream);
    } catch (IOException generatorException) {
      LOGGER.error("Could not create the JSON generator.", generatorException);

      try {
        outputStream.close();
      } catch (IOException streamCloseException) {
        LOGGER.warn("Could not close the output stream.", streamCloseException);
      }

      return;
    }

    try {
      // Start the response object.
      generator.writeStartObject();

      // Create the "success" message.
      generator.writeStringField("result", "success");

      // Create the metadata.
      generator.writeObjectFieldStart("metadata");

      // Write the count.
      generator.writeNumberField("count", omhReadResponder.getNumDataPoints());

      try {
        // Build the root string for the previous and next
        // pointers.
        StringBuilder rootBuilder = buildRootPreviousNextString();

        // For the previous pointer, add the number to skip.
        if (numToSkip > 0) {
          // Clone the root string for the previous string.
          StringBuilder previousBuilder = new StringBuilder(rootBuilder);

          // The number of results to return in the previous page
          // is either, all of the data points before this page
          // or the same number that were returned from this
          // request.
          long prevNumToReturn = Math.min(numToSkip, numToReturn);

          // Add the number of points to skip.
          previousBuilder
              .append('&')
              .append(URLEncoder.encode(InputKeys.OMH_NUM_TO_SKIP, URL_ENCODING_UTF_8))
              .append('=')
              .append(
                  URLEncoder.encode(
                      Long.toString(numToSkip - prevNumToReturn), URL_ENCODING_UTF_8));

          // Add the number of points to return.
          previousBuilder
              .append('&')
              .append(URLEncoder.encode(InputKeys.OMH_NUM_TO_RETURN, URL_ENCODING_UTF_8))
              .append('=')
              .append(URLEncoder.encode(Long.toString(prevNumToReturn), URL_ENCODING_UTF_8));

          // Write the previous pointer.
          generator.writeStringField("previous", previousBuilder.toString());
        }

        // If we filled this page, then there might be another.
        if (numToReturn == omhReadResponder.getNumDataPoints()) {
          // Clone the root string for the previous string.
          StringBuilder nextBuilder = new StringBuilder(rootBuilder);

          // For the next pointer, add the number to skip.
          nextBuilder
              .append('&')
              .append(URLEncoder.encode(InputKeys.OMH_NUM_TO_SKIP, URL_ENCODING_UTF_8))
              .append('=')
              .append(
                  URLEncoder.encode(Long.toString(numToSkip + numToReturn), URL_ENCODING_UTF_8));

          // For the next pointer, add the number to return.
          nextBuilder
              .append('&')
              .append(URLEncoder.encode(InputKeys.OMH_NUM_TO_RETURN, URL_ENCODING_UTF_8))
              .append('=')
              .append(URLEncoder.encode(Long.toString(numToReturn), URL_ENCODING_UTF_8));

          // Write the next pointer.
          generator.writeStringField("next", nextBuilder.toString());
        }
      } catch (DomainException e) {
        LOGGER.warn("There was an error building the root URL.", e);
      }

      // End the metadata.
      generator.writeEndObject();

      // Start the data.
      generator.writeArrayFieldStart("data");

      // Dispatch the writing of the data to the request.
      omhReadResponder.respond(generator, columns);

      // End the data.
      generator.writeEndArray();

      // End the response object.
      generator.writeEndObject();
    } catch (JsonGenerationException e) {
      LOGGER.error(e);
    } catch (IOException e) {
      LOGGER.error(e);
    } catch (DomainException e) {
      LOGGER.error(e);
    } finally {
      // Flush and close the writer.
      try {
        generator.close();
      } catch (IOException e) {
        LOGGER.info("Could not close the generator.", e);
      }
    }
  } else {
    userRequest.respond(httpRequest, httpResponse);
  }
}

