public HttpResponse execute() throws HttpClientException {
  HttpURLConnection conn = null;
  UncloseableInputStream payloadStream = null;
  try {
    if (parameters != null && !parameters.isEmpty()) {
      final StringBuilder buf = new StringBuilder(256);
      if (HTTP_GET.equals(method) || HTTP_HEAD.equals(method)) {
        buf.append('?');
      }

      int paramIdx = 0;
      for (final Map.Entry<String, String> e : parameters.entrySet()) {
        if (paramIdx != 0) {
          buf.append("&");
        }
        final String name = e.getKey();
        final String value = e.getValue();
        buf.append(URLEncoder.encode(name, CONTENT_CHARSET))
            .append("=")
            .append(URLEncoder.encode(value, CONTENT_CHARSET));
        ++paramIdx;
      }

      if (!contentSet
          && (HTTP_POST.equals(method)
              || HTTP_DELETE.equals(method)
              || HTTP_PUT.equals(method))) {
        try {
          content = buf.toString().getBytes(CONTENT_CHARSET);
        } catch (UnsupportedEncodingException e) {
          // Unlikely to happen.
          throw new HttpClientException("Encoding error", e);
        }
      } else {
        uri += buf;
      }
    }

    conn = (HttpURLConnection) new URL(uri).openConnection();
    conn.setConnectTimeout(hc.getConnectTimeout());
    conn.setReadTimeout(hc.getReadTimeout());
    conn.setAllowUserInteraction(false);
    conn.setInstanceFollowRedirects(false);
    conn.setRequestMethod(method);
    conn.setUseCaches(false);
    conn.setDoInput(true);

    if (headers != null && !headers.isEmpty()) {
      for (final Map.Entry<String, List<String>> e : headers.entrySet()) {
        final List<String> values = e.getValue();
        if (values != null) {
          final String name = e.getKey();
          for (final String value : values) {
            conn.addRequestProperty(name, value);
          }
        }
      }
    }

    if (cookies != null && !cookies.isEmpty()
        || hc.getInMemoryCookies() != null && !hc.getInMemoryCookies().isEmpty()) {
      final StringBuilder cookieHeaderValue = new StringBuilder(256);
      prepareCookieHeader(cookies, cookieHeaderValue);
      prepareCookieHeader(hc.getInMemoryCookies(), cookieHeaderValue);
      conn.setRequestProperty("Cookie", cookieHeaderValue.toString());
    }

    final String userAgent = hc.getUserAgent();
    if (userAgent != null) {
      conn.setRequestProperty("User-Agent", userAgent);
    }

    conn.setRequestProperty("Connection", "close");
    conn.setRequestProperty("Location", uri);
    conn.setRequestProperty("Referrer", uri);
    conn.setRequestProperty("Accept-Encoding", "gzip,deflate");
    conn.setRequestProperty("Accept-Charset", CONTENT_CHARSET);

    if (conn instanceof HttpsURLConnection) {
      setupSecureConnection(hc.getContext(), (HttpsURLConnection) conn);
    }

    if (HTTP_POST.equals(method) || HTTP_DELETE.equals(method) || HTTP_PUT.equals(method)) {
      if (content != null) {
        conn.setDoOutput(true);
        if (!contentSet) {
          conn.setRequestProperty(
              "Content-Type", "application/x-www-form-urlencoded; charset=" + CONTENT_CHARSET);
        } else if (contentType != null) {
          conn.setRequestProperty("Content-Type", contentType);
        }
        conn.setFixedLengthStreamingMode(content.length);

        final OutputStream out = conn.getOutputStream();
        out.write(content);
        out.flush();
      } else {
        conn.setFixedLengthStreamingMode(0);
      }
    }

    for (final HttpRequestHandler connHandler : reqHandlers) {
      try {
        connHandler.onRequest(conn);
      } catch (HttpClientException e) {
        throw e;
      } catch (Exception e) {
        throw new HttpClientException("Failed to prepare request to " + uri, e);
      }
    }

    conn.connect();

    final int statusCode = conn.getResponseCode();
    if (statusCode == -1) {
      throw new HttpClientException("Invalid response from " + uri);
    }
    if (!expectedStatusCodes.isEmpty() && !expectedStatusCodes.contains(statusCode)) {
      throw new HttpClientException(
          "Expected status code " + expectedStatusCodes + ", got " + statusCode);
    } else if (expectedStatusCodes.isEmpty() && statusCode / 100 != 2) {
      throw new HttpClientException("Expected status code 2xx, got " + statusCode);
    }

    final Map<String, List<String>> headerFields = conn.getHeaderFields();
    final Map<String, String> inMemoryCookies = hc.getInMemoryCookies();
    if (headerFields != null) {
      final List<String> newCookies = headerFields.get("Set-Cookie");
      if (newCookies != null) {
        for (final String newCookie : newCookies) {
          final String rawCookie = newCookie.split(";", 2)[0];
          final int i = rawCookie.indexOf('=');
          final String name = rawCookie.substring(0, i);
          final String value = rawCookie.substring(i + 1);
          inMemoryCookies.put(name, value);
        }
      }
    }

    if (isStatusCodeError(statusCode)) {
      // Got an error: cannot read input.
      payloadStream = new UncloseableInputStream(getErrorStream(conn));
    } else {
      payloadStream = new UncloseableInputStream(getInputStream(conn));
    }
    final HttpResponse resp =
        new HttpResponse(
            statusCode,
            payloadStream,
            headerFields == null ? NO_HEADERS : headerFields,
            inMemoryCookies);
    if (handler != null) {
      try {
        handler.onResponse(resp);
      } catch (HttpClientException e) {
        throw e;
      } catch (Exception e) {
        throw new HttpClientException("Error in response handler", e);
      }
    } else {
      final File temp =
          File.createTempFile("httpclient-req-", ".cache", hc.getContext().getCacheDir());
      resp.preload(temp);
      temp.delete();
    }
    return resp;
  } catch (SocketTimeoutException e) {
    if (handler != null) {
      try {
        handler.onTimeout();
        return null;
      } catch (HttpClientException e2) {
        throw e2;
      } catch (Exception e2) {
        throw new HttpClientException("Error in response handler", e2);
      }
    } else {
      throw new HttpClientException("Response timeout from " + uri, e);
    }
  } catch (IOException e) {
    throw new HttpClientException("Connection failed to " + uri, e);
  } finally {
    if (conn != null) {
      if (payloadStream != null) {
        // Fully read Http response:
        // http://docs.oracle.com/javase/6/docs/technotes/guides/net/http-keepalive.html
        try {
          while (payloadStream.read(buffer) != -1) {;
          }
        } catch (IOException ignore) {
        }
        payloadStream.forceClose();
      }
      conn.disconnect();
    }
  }
}

/**
 * Handles both a POST and a GET <br>
 * Note: This is required as the POST will come from a Mobile User, where as the GET will occur on
 * redirect from Google Auth
 *
 * @param req - the request
 * @param resp - the response
 * @throws ServletException
 * @throws IOException
 */
private void doLogic(HttpServletRequest req, HttpServletResponse resp)
    throws ServletException, IOException {

  // Get Writer
  final PrintWriter pw = resp.getWriter();

  try {

    // Get redirection url
    final String redirect = req.getParameter(APIConstants.REDIRECT);

    Logger.getLogger(TAG).info("Final Redirection is: " + redirect);

    // If login was successful (or user is already logged in)
    if (TDUserService.isGoogleUser(req)) {
      Logger.getLogger(TAG).info("User logged in, redirecting to: " + redirect);

      try {

        TDUser user = null;

        try {
          // Get the user
          user = TDUserService.getUser(req.getSession());
        } catch (Exception e) {

          Logger.getLogger(TAG).info(e.getMessage() + " means no user.");
        }

        if (null == user) {
          Logger.getLogger(TAG).info("No user exists, creating a new user");
          final User gUser = UserServiceFactory.getUserService().getCurrentUser();
          final String nickname =
              (null != gUser.getNickname()
                      && !gUser.getNickname().isEmpty()
                      && gUser.getNickname().indexOf("@") >= 0
                  ? (gUser.getNickname().substring(0, gUser.getNickname().indexOf("@")))
                  : gUser.getEmail());
          user = new TDUser(gUser, nickname, gUser.getEmail());
          Datastore.put(user);
        } else {
          Logger.getLogger(TAG).info("User " + user.getKey() + " found.");
        }

        Logger.getLogger(TAG).info("User's API Key is: " + user.getApiKey());

        // Redirect to given url with the TDUser Id
        resp.sendRedirect(
            redirect
                + (redirect.contains("?") ? "&" : "?")
                + UserConstants.TDUSER_ID
                + "="
                + user.getKey().getId()
                + "&"
                + UserConstants.API_KEY
                + "="
                + URLEncoder.encode(user.getApiKey(), "UTF-8"));
      } catch (Exception e) {
        Logger.getLogger(TAG).error(e.getMessage(), e);
        // Ensure some kind of redirect
        resp.sendRedirect(redirect);
      }
    } else {
      // Create a url
      final String url = TDUserService.getGoogleLoginURL("/api/googleAuth?redirect=" + redirect);
      Logger.getLogger(TAG).info("User not logged in. Sending to Google Auth, URL: " + url);

      // Redirect to that url
      resp.sendRedirect(url);
    }
  } catch (Exception e) {
    e.printStackTrace();
    Logger.getLogger(TAG).error(e.getMessage());
    // Notify of error
    pw.write(APIUtils.generateJSONFailureMessage(e));
  } finally {
    pw.flush();
    pw.close();
  }
}

