/**
 * Given a "code" call the Facebook Auth server and returns the values returned by Facebook
 *
 * @param code - the "code" recieved from Facebook Auth
 * @return a map containing name values of all data returned by Facebook
 */
public static Map<String, String> getAccessToken(final String code) throws Exception {

  Logger.getLogger(TAG).info("Running with the Code: " + code);

  final Map<String, String> toBeReturned = new HashMap<String, String>();

  final String redirectURL = FacebookConstants.FACEBOOK_BASE_REDIRECT_URI + "/facebookLogin";

  final String url =
      "https://graph.facebook.com/oauth/access_token?client_id="
          + FacebookConstants.APP_ID
          + "&redirect_uri="
          + redirectURL
          + "&client_secret="
          + FacebookConstants.APP_SECRET
          + "&code="
          + URLEncoder.encode(code, "UTF-8");

  // response.sendRedirect(url);
  Logger.getLogger(TAG).info("URL : " + url);

  URL address = new URL(url);

  // Create the request
  // TODO: Remove when GAE fixing bug
  HTTPRequest httpReq = new HTTPRequest(address, HTTPMethod.GET, doNotValidateCertificate());

  // Get the response
  HTTPResponse httpResp = URLFetchServiceFactory.getURLFetchService().fetch(httpReq);

  // Get the fields from text returned
  final String fields = new String(httpResp.getContent());

  Logger.getLogger(TAG).info("Final: " + fields);

  // Break fields along &
  final String[] brokenUpFields = fields.split("&");

  // Traverse fields
  for (final String field : brokenUpFields) {
    // Split on an equals
    final String[] curFields = field.split("=");
    // Check that it split name value and none are null
    if (curFields.length == 2
        && (null != curFields[0] && !curFields[0].isEmpty())
        && (null != curFields[1] && !curFields[1].isEmpty())) {

      Logger.getLogger(TAG).info("Adding: " + curFields[0] + "\t:\t" + curFields[1]);

      // Put in the map
      toBeReturned.put(curFields[0], curFields[1]);
    }
  }

  return toBeReturned;
}

/**
 * Send F2F accept
 *
 * @param userId
 * @return
 */
public DataHolder sendF2FAccept(int userId) {

  DataHolder result = new DataHolder(AppCAP.HTTP_ERROR, internetConnectionErrorMsg, null);

  client.getParams().setParameter(CoreProtocolPNames.PROTOCOL_VERSION, HttpVersion.HTTP_1_1);

  HttpPost post = new HttpPost(AppCAP.URL_WEB_SERVICE + AppCAP.URL_API);

  List<NameValuePair> params = new ArrayList<NameValuePair>();

  try {
    params.add(new BasicNameValuePair("action", "f2fAccept"));
    params.add(new BasicNameValuePair("greeter_id", URLEncoder.encode(userId + "", "utf-8")));

    post.setEntity(new UrlEncodedFormEntity(params, "UTF-8"));

    // Execute HTTP Post Request
    HttpResponse response = client.execute(post);
    HttpEntity resEntity = response.getEntity();

    String responseString = EntityUtils.toString(resEntity);
    if (Constants.enableApiJsonLogging)
      RootActivity.log("HttpUtil_sendF2FAccept: " + responseString);

    if (responseString != null) {

      JSONObject json = new JSONObject(responseString);
      if (json != null) {

        result.setHandlerCode(AppCAP.HTTP_REQUEST_SUCCEEDED); // change
        // this
        return result;
      }
    }

  } catch (UnsupportedEncodingException e) {
    e.printStackTrace();
    return result;

  } catch (ClientProtocolException e) {
    e.printStackTrace();
    return result;

  } catch (IOException e) {
    e.printStackTrace();
    return result;

  } catch (JSONException e) {
    e.printStackTrace();
    result.setResponseMessage("JSON Parsing Error: " + e);
    return result;
  }
  return result;
}

/** turns a map into a key=value property string for sending to bugnet */
public static String propertyString(Map<String, String> props) throws IOException {
  String propstr = new String();
  String key;
  for (Iterator<String> i = props.keySet().iterator(); i.hasNext(); ) {
    key = i.next();
    propstr =
        propstr
            + URLEncoder.encode(key, "UTF-8")
            + "="
            + URLEncoder.encode((String) props.get(key), "UTF-8");
    if (i.hasNext()) {
      propstr = propstr + "&";
    }
  }
  return propstr;
}

