/**
 * @param newBytes
 * @throws IOException
 */
public void writeEntireFile(ByteBuffer newBytes) throws IOException {
  if (newBytes.capacity() != this.bytesPerTile) {
    throw new IllegalArgumentException(
        "byte buffer is to small, required bytes:"
            + (this.bytesPerTile)
            + ", provided bytes: "
            + newBytes.capacity());
  }
  synchronized (LOCK) {
    FileChannel fileChannel = getWriteChannel();
    FileLock lock = fileChannel.lock();
    fileChannel.position(0);
    newBytes.rewind();
    fileChannel.write(newBytes);
    lock.release();
    closeWriteStream();
  }
}

