/**
 * Drain internal buffers into underlying channels.
 *
 * @throws IOException DOCUMENT ME!
 */
private void drain() throws IOException {
  m_bb.flip();
  m_indexBuffer.flip();

  while (m_bb.remaining() > 0) shpChannel.write(m_bb);

  while (m_indexBuffer.remaining() > 0) shxChannel.write(m_indexBuffer);

  m_bb.flip().limit(m_bb.capacity());
  m_indexBuffer.flip().limit(m_indexBuffer.capacity());
}

