@Override
public int write(ByteBuffer src) throws IOException {
  if (filePointer >= length && filePointer > maxLength) {
    // may need to extend and create files
    long oldFilePointer = filePointer;
    long x = length - (length % maxLength) + maxLength;
    for (; x < filePointer; x += maxLength) {
      if (x > length) {
        // expand the file size
        position(x - 1);
        write(ByteBuffer.wrap(new byte[1]));
      }
      filePointer = oldFilePointer;
    }
  }
  long offset = filePointer % maxLength;
  int len = src.remaining();
  FileChannel channel = getFileChannel();
  channel.position(offset);
  int l = (int) Math.min(len, maxLength - offset);
  if (l == len) {
    l = channel.write(src);
  } else {
    int oldLimit = src.limit();
    src.limit(src.position() + l);
    l = channel.write(src);
    src.limit(oldLimit);
  }
  filePointer += l;
  length = Math.max(length, filePointer);
  return l;
}

/**
 * Write a single dbase record. Useful to update a dbf.
 *
 * @param record The entries to write.
 * @throws IOException If IO error occurs.
 * @throws DbaseFileException If the entry doesn't comply to the header.
 */
public void writeRecord(Object[] record, int numReg) throws IOException {

  if (!(channel instanceof FileChannel)) {
    throw new IOException(
        "DbaseFileWriterNIO: channel is not a FileChannel. Cannot position properly");
  }

  if (record.length != header.getNumFields()) {
    throw new IOException(
        "Wrong number of fields " + record.length + " expected " + header.getNumFields());
  }

  FileChannel fileChannel = (FileChannel) channel;
  long newPos = header.getHeaderLength() + numReg * header.getRecordLength();

  buffer.position(0);

  // put the 'not-deleted' marker
  buffer.put((byte) ' ');

  for (int i = 0; i < header.getNumFields(); i++) {
    String fieldString = fieldString(record[i], i);
    // if ( header.getFieldLength(i) != fieldString.getBytes().length) {
    // System.out.println(i + " : " + header.getFieldName(i));
    // }

    String tmp = fieldString;
    while (fieldString.length() < tmp.getBytes(charset.name()).length) {
      tmp = tmp.substring(0, tmp.length() - 2);
    }
    buffer.put(tmp.getBytes(charset.name()));
  }

  fileChannel.write(buffer, newPos);
}

/**
 * writeToFile
 *
 * @param filename
 * @param contents
 */
public final void writeToFile(String filename, String contents) {
  RandomAccessFile destFile = null;
  File tmpFile;
  try {
    tmpFile = new File(filename);
    if (tmpFile.exists()) {
      tmpFile.delete();
    }
    destFile = new RandomAccessFile(filename, "rw");
  } catch (FileNotFoundException e) {
    log.error("Error accessing file: " + filename + " (" + e.getMessage() + ")");
  }

  ByteBuffer buf = ByteBuffer.allocate(contents.length());
  FileChannel outChannel = destFile.getChannel();

  buf.put(contents.getBytes());
  buf.flip(); // buffer set for read

  try {
    outChannel.write(buf);
    destFile.close();
  } catch (IOException e) {
    log.error("Error writing to file " + filename + " (" + e.getMessage() + ")");
  }
}

public int write(ByteBuffer src) throws IOException {
