/**
 * Closes the id generator flushing defragged ids in memory to file. The file will be truncated to
 * the minimal size required to hold all defragged ids and it will be marked as clean (not
 * sticky).
 *
 * <p>An invoke to the <CODE>nextId</CODE> or <CODE>freeId</CODE> after this method has been
 * invoked will result in an <CODE>IOException</CODE> since the highest returned id has been set
 * to a negative value.
 *
 * @throws IOException If unable to close this id generator
 */
public synchronized void close() {
  if (nextFreeId.get() == -1) {
    return;
  }

  // write out lists
  if (releasedIdList.size() > 0) {
    writeIdBatch();
  }
  if (defragedIdList.size() > 0) {
    while (defragedIdList.size() > 0) {
      releasedIdList.add(defragedIdList.removeFirst());
    }
    writeIdBatch();
  }

  // write header
  try {
    fileChannel.position(0);
    ByteBuffer buffer = ByteBuffer.allocate(HEADER_SIZE);
    buffer.put(STICKY_GENERATOR).putLong(nextFreeId.get());
    buffer.flip();
    fileChannel.write(buffer);
    // move data to remove fragmentation in file
    if (totalBytesRead > HEADER_SIZE) {
      long writePosition = HEADER_SIZE;
      long readPosition = readBlocksTo;
      if (totalBytesRead < readBlocksTo) {
        readPosition = totalBytesRead;
      }
      int bytesRead = -1;
      do {
        writeBuffer.clear();
        fileChannel.position(readPosition);
        bytesRead = fileChannel.read(writeBuffer);
        readPosition += bytesRead;
        writeBuffer.flip();
        fileChannel.position(writePosition);
        writePosition += fileChannel.write(writeBuffer);
      } while (bytesRead > 0);
      // truncate
      fileChannel.truncate(writePosition);
    }
    // flush
    fileChannel.force(false);
    // remove sticky
    buffer.clear();
    buffer.put(CLEAN_GENERATOR);
    buffer.limit(1);
    buffer.flip();
    fileChannel.position(0);
    fileChannel.write(buffer);
    // flush and close
    fileChannel.force(false);
    fileChannel.close();
    fileChannel = null;
    // make this generator unusable
    nextFreeId.set(-1);
  } catch (IOException e) {
    throw new UnderlyingStorageException("Unable to close id generator " + fileName, e);
  }
}

/**
 * If the given resource is an audio file, store it as a file. This is done because we need an
 * audio file to play the sound.
 *
 * @param soundHandle The handle to the sound object. The audio object
 * @param dataOffset Offset into data object where mime type + audio data begins. begins with a
 *     null terminated mime string, then follows audio data.
 */
void storeIfBinaryAudioResource(int soundHandle, int offset) {
  ByteBuffer audioData = mMoSyncThread.getBinaryResource(soundHandle);
  if (null == audioData) {
    SYSLOG("MoSyncAudio.storeIfBinaryAudioResource: " + "Sound data object not found");
    // TODO: What about returning an error code?
    return;
  }

  // Is there a mime type at the offset?
  if (!checkIfMimeAudioType(audioData, offset)) {
    // No it was not.
    SYSLOG("MoSyncAudio.storeIfBinaryAudioResource: " + "Not an audio object!");
    // TODO: What about returning an error code?
    return;
  }

  // Check that we can read the mime string.
  String mimeType = readMimeString(audioData, offset);
  if (mimeType == null) {
    SYSLOG("MoSyncAudio.storeIfBinaryAudioResource: No mime type!");
    // TODO: What about returning an error code?
    return;
  }
  /*
  // Determine the length of the audio data.
  int mimeStringLength = mimeType.length() + 1;
  */
  // Write the entire data object to file.
  try {
    // Create file name.
    String fileName = "MOSYNCTEMP:audio" + soundHandle + ".tmp";

    // Create a temporary audio file.
    FileOutputStream audioFileOutputStream =
        getActivity()
            .openFileOutput(fileName, Context.MODE_WORLD_READABLE | Context.MODE_WORLD_WRITEABLE);

    // Open a file channel to write audio data to.
    FileChannel channel = audioFileOutputStream.getChannel();

    // Set position to start of audio data.
    audioData.position(0);

    // Write data.
    channel.write(audioData);

    // Close the channel, also closes the output stream.
    channel.close();

    // Add entry to audio resource table.
    mAudioStores.put(soundHandle, new AudioStore(fileName));
  } catch (Exception ex) {
    Log.e("MoSyncSound.storeIfBinaryAudioResource", "Unable to save temporary audio file.");
    ex.printStackTrace();
    // TODO: What about returning an error code?
  }
}

@Override
public void run() {
  LinkedList<QueueEntry> toFlush = new LinkedList<QueueEntry>();
  ByteBuffer lenBuff = ByteBuffer.allocate(4);
  try {
    long logId = System.currentTimeMillis();
    FileChannel logFile = openChannel(logId);
    BufferedChannel bc = new BufferedChannel(logFile, 65536);
    zeros.clear();
    long nextPrealloc = preAllocSize;
    long lastFlushPosition = 0;
    logFile.write(zeros, nextPrealloc);
    // TODO: Currently, when we roll over the journal logs, the older
    // ones are never garbage collected. We should remove a journal log
    // once all of its entries have been synced with the entry logs.
    while (true) {
      QueueEntry qe = null;
      if (toFlush.isEmpty()) {
        qe = queue.take();
      } else {
        qe = queue.poll();
        if (qe == null || bc.position() > lastFlushPosition + 512 * 1024) {
          // logFile.force(false);
          bc.flush(true);
          lastFlushPosition = bc.position();
          lastLogMark.setLastLogMark(logId, lastFlushPosition);
          for (QueueEntry e : toFlush) {
            e.cb.writeComplete(0, e.ledgerId, e.entryId, null, e.ctx);
          }
          toFlush.clear();
        }
      }
      if (qe == null) {
        continue;
      }
      lenBuff.clear();
      lenBuff.putInt(qe.entry.remaining());
      lenBuff.flip();
      //
      // we should be doing the following, but then we run out of
      // direct byte buffers
      // logFile.write(new ByteBuffer[] { lenBuff, qe.entry });
      bc.write(lenBuff);
      bc.write(qe.entry);
      if (bc.position() > nextPrealloc) {
        nextPrealloc = (logFile.size() / preAllocSize + 1) * preAllocSize;
        zeros.clear();
        logFile.write(zeros, nextPrealloc);
      }
      toFlush.add(qe);
    }
  } catch (Exception e) {
    LOG.fatal("Bookie thread exiting", e);
  }
}

public void flush(boolean sync) throws IOException {
  synchronized (this) {
    if (writeBuffer == null) {
      return;
    }
    writeBuffer.flip();
    bc.write(writeBuffer);
    writeBuffer.clear();
    writeBufferStartPosition = bc.position();
  }
  if (sync) {
    bc.force(false);
  }
}

private void fixIndexMap(long firstIFD, String fileName) throws IOException {
  long filePosition = firstIFD;
  indexMap_ = new HashMap<String, Long>();
  long progBarMax = (fileChannel_.size() / 2L);
  final ProgressBar progressBar =
      new ProgressBar(
          "Fixing " + fileName,
          0,
          progBarMax >= Integer.MAX_VALUE ? Integer.MAX_VALUE : (int) progBarMax);
  progressBar.setRange(0, progBarMax >= Integer.MAX_VALUE ? Integer.MAX_VALUE : (int) progBarMax);
  progressBar.setProgress(0);
  progressBar.setVisible(true);
  long nextIFDOffsetLocation = 0;
  IFDData data;
  while (filePosition > 0) {
    try {
      data = readIFD(filePosition);
      if (data.nextIFD == 0) {
        break;
      }
      TaggedImage ti = readTaggedImage(data);
      if (ti.tags == null
          || ti.tags.length() == 0) { // Blank placeholder image, dont add to index map
        filePosition = data.nextIFD;
        nextIFDOffsetLocation = data.nextIFDOffsetLocation;
        continue;
      }
      String label;
      label = MDUtils.getLabel(ti.tags);
      if (label == null) {
        break;
      }
      indexMap_.put(label, filePosition);

      final int progress = (int) (filePosition / 2L);
      SwingUtilities.invokeLater(
          new Runnable() {
            @Override
            public void run() {
              progressBar.setProgress(progress);
            }
          });

      if (data.nextIFD <= filePosition || data.nextIFDOffsetLocation <= nextIFDOffsetLocation) {
        break; // so no recoverable data is ever lost
      }
      filePosition = data.nextIFD;
      nextIFDOffsetLocation = data.nextIFDOffsetLocation;
    } catch (Exception e) {
      break;
    }
  }
  progressBar.setVisible(false);

  filePosition += writeIndexMap(filePosition);

  ByteBuffer buffer = ByteBuffer.allocate(4).order(byteOrder_);
  buffer.putInt(0, 0);
  fileChannel_.write(buffer, nextIFDOffsetLocation);

  JSONArray settings = null;
  try {
    settings =
        DisplaySettings.getDisplaySettingsFromSummary(summaryMetadata_).getJSONArray("Channels");
  } catch (Exception ex) {
    ReportingUtils.showError(
        ex, "Problem saving file. PLease test to make sure file can be opened");
  }
  filePosition += writeDisplaySettings(settings, filePosition);

  // raFile_.setLength(filePosition + 8);

  fileChannel_.close();
  raFile_.close();
  // reopen
  createFileChannel(false);
}

@Override
public long writeData(VirtualFile virtualFile, DataExchangeBuffer deb, FileChannel out)
    throws OdetteFtpException {

  int bytesWritten = 0;

  if (out == null) {
    throw new NullPointerException("fileChannel");
  }

  ByteBuffer data = deb.readData();
  Iterator<SubrecordHeader> subrecordHeaders = new SubrecordHeaderIterator(data);

  try {
    while (subrecordHeaders.hasNext()) {

      SubrecordHeader header = subrecordHeaders.next();
      int count = header.getCount();
      boolean compressed = header.isCompressed();

      if (count > 0) {
        byte[] subrecord = new byte[(compressed ? 1 : count)];
        // read the subrecord
        data.get(subrecord);

        // write down to the output file channel
        ByteBuffer bb = ByteBuffer.wrap(subrecord);
        if (compressed) {
          for (int i = 0; i < count; i++) {
            bb.rewind();
            out.write(bb);
          }
        } else {
          out.write(bb);
        }

        bytesWritten += count;
      }

      /*
       * Handle the endOfRecord flag when Virtual File is TEXTFILE format
       * to add line separator at the end of each line/record.
       */
      if (header.isEndOfRecord() && virtualFile.getRecordFormat() == TEXTFILE) {
        out.write(ByteBuffer.wrap(LINE_SEPARATOR));
        bytesWritten += LINE_SEPARATOR.length;
      }
    }

    out.force(false);
  } catch (IOException e) {
    throw new VirtualFileMappingException("Write data operation failed.", e);
  }

  deb.setUnitCount(bytesWritten);

  return bytesWritten;
}

@Override
public Object call() throws Exception {
  final File f = File.createTempFile("foo", "bar", new File("/var/voltdb"));
  f.deleteOnExit();
  try {
    final FileOutputStream fos = new FileOutputStream(f);
    final FileChannel fc = fos.getChannel();
    try {
      synchronized (readyLock) {
        readyThreads++;
        readyLock.notify();
      }

      synchronized (goLock) {
        while (!goNow) {
          goLock.wait();
        }
      }
      for (int ii = 0; ii < 128; ii++) {
        final int percentDone = (int) ((ii / 128.0) * 100.0);
        if (extra_output && percentDone % 25 == 0) {
          System.out.println(
              "Thread "
                  + Thread.currentThread().getId()
                  + " is "
                  + percentDone
                  + "% complete with parallel test");
        }
        ByteBuffer b = m_buffer.duplicate();
        while (b.hasRemaining()) {
          fc.write(b);
        }
      }
      fos.getFD().sync();

      if (extra_output) {
        System.out.println("Thread " + Thread.currentThread().getId() + " is finished");
      }
      synchronized (readyLock) {
        readyThreads--;
        readyLock.notifyAll();
        while (readyThreads > 0) {
          readyLock.wait();
        }
      }
    } finally {
      fos.close();
    }
  } finally {
    f.delete();
  }
  return null;
}

