@SuppressWarnings("resource")
private final void speedTestNIO(int bufferSize, boolean direct) throws IOException {
  final Channel.ID tmpChannel = ioManager.createChannel();

  File tempFile = null;
  FileChannel fs = null;

  try {
    tempFile = new File(tmpChannel.getPath());

    RandomAccessFile raf = new RandomAccessFile(tempFile, "rw");
    fs = raf.getChannel();

    ByteBuffer buf =
        direct ? ByteBuffer.allocateDirect(bufferSize) : ByteBuffer.allocate(bufferSize);

    long writeStart = System.currentTimeMillis();

    int valsLeft = NUM_INTS_WRITTEN;
    while (valsLeft-- > 0) {
      if (buf.remaining() < 4) {
        buf.flip();
        fs.write(buf);
        buf.clear();
      }
      buf.putInt(valsLeft);
    }

    if (buf.position() > 0) {
      buf.flip();
      fs.write(buf);
    }

    fs.close();
    raf.close();
    fs = null;

    long writeElapsed = System.currentTimeMillis() - writeStart;

    // ----------------------------------------------------------------

    raf = new RandomAccessFile(tempFile, "r");
    fs = raf.getChannel();
    buf.clear();

    long readStart = System.currentTimeMillis();

    fs.read(buf);
    buf.flip();

    valsLeft = NUM_INTS_WRITTEN;
    while (valsLeft-- > 0) {
      if (buf.remaining() < 4) {
        buf.compact();
        fs.read(buf);
        buf.flip();
      }
      if (buf.getInt() != valsLeft) {
        throw new IOException();
      }
    }

    fs.close();
    raf.close();

    long readElapsed = System.currentTimeMillis() - readStart;

    LOG.info(
        "NIO Channel with buffer "
            + bufferSize
            + ": write "
            + writeElapsed
            + " msecs, read "
            + readElapsed
            + " msecs.");
  } finally {
    // close if possible
    if (fs != null) {
      fs.close();
      fs = null;
    }
    // try to delete the file
    if (tempFile != null) {
      tempFile.delete();
    }
  }
}

private ReflogWriter log(final String refName, final byte[] rec) throws IOException {
  final File log = logFor(refName);
  final boolean write =
      forceWrite || (isLogAllRefUpdates() && shouldAutoCreateLog(refName)) || log.isFile();
  if (!write) return this;

  WriteConfig wc = getRepository().getConfig().get(WriteConfig.KEY);
  FileOutputStream out;
  try {
    out = new FileOutputStream(log, true);
  } catch (FileNotFoundException err) {
    final File dir = log.getParentFile();
    if (dir.exists()) throw err;
    if (!dir.mkdirs() && !dir.isDirectory())
      throw new IOException(MessageFormat.format(JGitText.get().cannotCreateDirectory, dir));
    out = new FileOutputStream(log, true);
  }
  try {
    if (wc.getFSyncRefFiles()) {
      FileChannel fc = out.getChannel();
      ByteBuffer buf = ByteBuffer.wrap(rec);
      while (0 < buf.remaining()) fc.write(buf);
      fc.force(true);
    } else out.write(rec);
  } finally {
    out.close();
  }
  return this;
}

