private void drain() throws IOException {
  shapeBuffer.flip();
  indexBuffer.flip();
  while (shapeBuffer.remaining() > 0) shpChannel.write(shapeBuffer);
  while (indexBuffer.remaining() > 0) shxChannel.write(indexBuffer);
  shapeBuffer.flip().limit(shapeBuffer.capacity());
  indexBuffer.flip().limit(indexBuffer.capacity());
}

private int writeHeader(FileChannel channel) throws IOException {
  channel.position(0);

  ByteBuffer buffer = ByteBuffer.allocate(8);
  buffer.put((byte) 'M');
  buffer.put((byte) 'M');
  buffer.putShort((short) 42);
  buffer.putInt(firstIFD);

  buffer.flip();
  channel.write(buffer);

  return firstIFD;
}

/**
 * DOCUMENT ME!
 *
 * @param v DOCUMENT ME!
 * @param position DOCUMENT ME!
 * @throws IndexException
 * @see
 *     com.hardcode.gdbms.engine.data.indexes.hashMap.Index#add(com.hardcode.gdbms.engine.values.Value,
 *     int)
 */
public void add(Object v, int position) throws IndexException {
  try {
    int aux;

    // Obtenemos la posicin en el hashtable
    int pos = Math.abs(v.hashCode());
    pos = (pos % positionCount);

    // Leemos el contenido
    buffer.clear();
    channel.position(byteNumber(pos));
    aux = channel.read(buffer);
    buffer.flip();

    int value = buffer.getInt();
    int next = buffer.getInt();

    if (value == -1) {
      // Si la cubeta est por ocupar
      buffer.clear();
      buffer.putInt(position);
      buffer.putInt(-1);
      buffer.flip();
      channel.position(byteNumber(pos));
      aux = channel.write(buffer);
      channel.force(true);

      return;
    } else {
      // Si la cubeta est ocupada

      /*
       * iteramos hasta que encontremos el ltimo nodo de la lista
       */
      while (next != -1) {
        pos = next;

        // Se lee el siguiente registro
        buffer.clear();
        channel.position(byteNumber(pos));
        aux = channel.read(buffer);
        buffer.flip();
        value = buffer.getInt();
        next = buffer.getInt();
      }

      // Modificamos la entrada para que enlace con la nueva
      channel.position(byteNumber(pos));
      buffer.clear();
      buffer.putInt(value);
      buffer.putInt(recordCount);
      buffer.flip();
      aux = channel.write(buffer);

      // Ponemos la nueva
      channel.position(byteNumber(recordCount));
      buffer.clear();
      buffer.putInt(position);
      buffer.putInt(-1);
      buffer.flip();
      aux = channel.write(buffer);

      channel.force(true);

      // Actualizamos el nmero de registros
      recordCount++;
    }
  } catch (IOException e) {
    throw new IndexException(e);
  }
}

/**
 * Write content of index to disk.
 *
 * @throws IOException
 */
public void write() throws IOException {
  checkWriteOk();
  File tmpIndex = new File(cacheFile.getAbsoluteFile() + ".tmp");
  File lock = new File(cacheFile.getAbsoluteFile() + ".lock");
  if (!lock.createNewFile()) throw new IOException(JGitText.get().indexFileIsInUse);
  try {
    FileOutputStream fileOutputStream = new FileOutputStream(tmpIndex);
    FileChannel fc = fileOutputStream.getChannel();
    ByteBuffer buf = ByteBuffer.allocate(4096);
    MessageDigest newMessageDigest = Constants.newMessageDigest();
    header = new Header(entries);
    header.write(buf);
    buf.flip();
    newMessageDigest.update(buf.array(), buf.arrayOffset(), buf.limit());
    fc.write(buf);
    buf.flip();
    buf.clear();
    for (Iterator i = entries.values().iterator(); i.hasNext(); ) {
      Entry e = (Entry) i.next();
      e.write(buf);
      buf.flip();
      newMessageDigest.update(buf.array(), buf.arrayOffset(), buf.limit());
      fc.write(buf);
      buf.flip();
      buf.clear();
    }
    buf.put(newMessageDigest.digest());
    buf.flip();
    fc.write(buf);
    fc.close();
    fileOutputStream.close();
    if (cacheFile.exists()) {
      if (db.getFS().retryFailedLockFileCommit()) {
        // file deletion fails on windows if another
        // thread is reading the file concurrently
        // So let's try 10 times...
        boolean deleted = false;
        for (int i = 0; i < 10; i++) {
          if (cacheFile.delete()) {
            deleted = true;
            break;
          }
          try {
            Thread.sleep(100);
          } catch (InterruptedException e) {
            // ignore
          }
        }
        if (!deleted) throw new IOException(JGitText.get().couldNotRenameDeleteOldIndex);
      } else {
        if (!cacheFile.delete())
          throw new IOException(JGitText.get().couldNotRenameDeleteOldIndex);
      }
    }
    if (!tmpIndex.renameTo(cacheFile))
      throw new IOException(JGitText.get().couldNotRenameTemporaryIndexFileToIndex);
    changed = false;
    statDirty = false;
    lastCacheTime = cacheFile.lastModified();
    db.fireEvent(new IndexChangedEvent());
  } finally {
    if (!lock.delete())
      throw new IOException(JGitText.get().couldNotDeleteLockFileShouldNotHappen);
    if (tmpIndex.exists() && !tmpIndex.delete())
      throw new IOException(JGitText.get().couldNotDeleteTemporaryIndexFileShouldNotHappen);
  }
}

