@Override
public void write(long devOffset, ByteBuffer src) throws IOException {
  checkClosed();

  if (this.readOnly) throw new ReadOnlyException();

  int toWrite = src.remaining();

  if ((devOffset + toWrite) > getSize()) throw new IOException("writing past end of file");

  while (toWrite > 0) {
    final int written = fc.write(src, devOffset);
    if (written < 0) throw new IOException();
    toWrite -= written;
    devOffset += written;
  }
}

/**
 * Recursively copy a directory from a source to a target
 *
 * @param sourceDirectory Source directory
 * @param targetDirectory Destination directory
 * @throws IOException If there is an error copying the files and directories
 * @since blojsom 2.06
 */
public static void copyDirectory(File sourceDirectory, File targetDirectory) throws IOException {
  File[] sourceFiles = sourceDirectory.listFiles(FILE_FILTER);
  File[] sourceDirectories = sourceDirectory.listFiles(DIRECTORY_FILTER);

  targetDirectory.mkdirs();

  // Copy the files
  if (sourceFiles != null && sourceFiles.length > 0) {
    for (int i = 0; i < sourceFiles.length; i++) {
      File sourceFile = sourceFiles[i];

      FileInputStream fis = new FileInputStream(sourceFile);
      FileOutputStream fos =
          new FileOutputStream(targetDirectory + File.separator + sourceFile.getName());
      FileChannel fcin = fis.getChannel();
      FileChannel fcout = fos.getChannel();

      ByteBuffer buf = ByteBuffer.allocateDirect(8192);
      long size = fcin.size();
      long n = 0;
      while (n < size) {
        buf.clear();
        if (fcin.read(buf) < 0) {
          break;
        }
        buf.flip();
        n += fcout.write(buf);
      }

      fcin.close();
      fcout.close();
      fis.close();
      fos.close();
    }
  }

  // Copy the directories
  if (sourceDirectories != null && sourceDirectories.length > 0) {
    for (int i = 0; i < sourceDirectories.length; i++) {
      File directory = sourceDirectories[i];
      File newTargetDirectory = new File(targetDirectory, directory.getName());

      copyDirectory(directory, newTargetDirectory);
    }
  }
}

public void write(String path, ReadableByteChannel data, boolean hasData) throws IOException {
  try {
    File file = new File(basePath, path);
    RandomAccessFile randomAccessFile = null;
    try {
      file.getParentFile().mkdirs();
      randomAccessFile = new RandomAccessFile(file, "rw");
      if (hasData) {
        ByteBuffer intermediateBuffer = ByteBuffer.allocateDirect(1024);
        FileChannel channel = randomAccessFile.getChannel();
        while (data.read(intermediateBuffer) >= 0) {
          intermediateBuffer.flip();
          channel.write(intermediateBuffer);
          intermediateBuffer.clear();
        }
      }
    } finally {
      if (randomAccessFile != null) {
        randomAccessFile.close();
      }
    }
  } catch (Throwable t) {
    t.printStackTrace();
    throw new IOException(t);
  }
}

/**
 * Closes this store. This will cause all buffers and channels to be closed. Requesting an
 * operation from after this method has been invoked is illegal and an exception will be thrown.
 *
 * <p>This method will start by invoking the {@link #closeStorage} method giving the implementing
 * store way to do anything that it needs to do before the fileChannel is closed.
 *
 * @throws IOException If problem when invoking {@link #closeStorage()}
 */
public void close() {
  if (fileChannel == null) {
    return;
  }
  closeStorage();
  if (windowPool != null) {
    windowPool.close();
    windowPool = null;
  }
  if (isReadOnly() && !isBackupSlave()) {
    try {
      fileChannel.close();
    } catch (IOException e) {
      throw new UnderlyingStorageException(e);
    }
    return;
  }
  long highId = idGenerator.getHighId();
  int recordSize = -1;
  if (this instanceof AbstractDynamicStore) {
    recordSize = ((AbstractDynamicStore) this).getBlockSize();
  } else if (this instanceof AbstractStore) {
    recordSize = ((AbstractStore) this).getRecordSize();
  }
  closeIdGenerator();
  boolean success = false;
  IOException storedIoe = null;
  // hack for WINBLOWS
  if (!readOnly || backupSlave) {
    for (int i = 0; i < 10; i++) {
      try {
        fileChannel.position(highId * recordSize);
        ByteBuffer buffer = ByteBuffer.wrap(UTF8.encode(getTypeAndVersionDescriptor()));
        fileChannel.write(buffer);
        fileChannel.truncate(fileChannel.position());
        fileChannel.force(false);
        if (fileLock != null) {
          fileLock.release();
        }
        fileChannel.close();
        fileChannel = null;
        success = true;
        break;
      } catch (IOException e) {
        storedIoe = e;
        System.gc();
      }
    }
  } else {
    try {
      fileChannel.close();
    } catch (IOException e) {
      e.printStackTrace();
    }
  }
  if (!success) {
    throw new UnderlyingStorageException(
        "Unable to close store " + getStorageFileName(), storedIoe);
  }
}

