  endTime = System.currentTimeMillis();
  long diff = endTime - startTime;
  if (stat != null) stat.avg(name + filename, diff);
  if (showEach || stat == null)
    System.out.println(
        "timeWrite to " + rfilename + "; n records = " + n + " took " + diff + " msecs");

  if (check) checkWrite(rfilename);
}

public synchronized void markAsCompleted(Entry... entries) {
  try {
    long position = channel.position();
    try {
      for (Entry entry : entries) {
        channel.position(entry.position());
        if (readNextEntryHeader(true).state == COMPLETED) {
          continue;
        }

        ByteBuffer buffer = getBuffer(HEADER_SIZE);
        buffer.clear();
        buffer.limit(1);
        buffer.put(COMPLETED);
        buffer.flip();
        channel.write(buffer);
        numberOfEntriesReadButNotYetCompleted.decrementAndGet();
      }
      // channel.force( false );
    } finally {
      channel.position(position);
    }
  } catch (IOException e) {
    throw new RuntimeException(e);
  }
}

