private static void flushChannel(FileChannel fc, ByteBuffer bb, boolean force)
    throws IOException {
  if (force || bb.position() == bb.limit()) {
    bb.limit(bb.position());
    bb.rewind();
    fc.write(bb);
    bb.clear();
  }
}

@Override
public void close() throws IOException, InterruptedException {
  try {
    m_outstandingWriteTasksLock.lock();
    try {
      while (m_outstandingWriteTasks.get() > 0) {
        m_noMoreOutstandingWriteTasksCondition.await();
      }
    } finally {
      m_outstandingWriteTasksLock.unlock();
    }
    m_syncTask.cancel(false);
    m_channel.force(false);
  } finally {
    m_bytesAllowedBeforeSync.release(m_bytesWrittenSinceLastSync.getAndSet(0));
  }
  m_channel.position(8);
  ByteBuffer completed = ByteBuffer.allocate(1);
  if (m_writeFailed) {
    completed.put((byte) 0).flip();
  } else {
    completed.put((byte) 1).flip();
  }
  m_channel.write(completed);
  m_channel.force(false);
  m_channel.close();
  if (m_onCloseHandler != null) {
    m_onCloseHandler.run();
  }
}

public void fill(final int position, final int size, final byte fillCharacter) throws Exception {
  ByteBuffer bb = ByteBuffer.allocate(size);

  for (int i = 0; i < size; i++) {
    bb.put(fillCharacter);
  }

  bb.flip();

  channel.position(position);

  channel.write(bb);

  channel.force(false);

  channel.position(0);

  fileSize = channel.size();
}

/**
 * Drain internal buffers into underlying channels.
 *
 * @throws IOException DOCUMENT ME!
 */
private void drain() throws IOException {
  writeBuffer.flip();

  int written = 0;

  while (writeBuffer.remaining() > 0) written += channel.write(writeBuffer, position);

  position += written;

  writeBuffer.flip().limit(writeBuffer.capacity());
}

