@Override
public long writeFile(
    SortedMap<String, InvertedList> postings,
    List<InvertedListDescriptor> invertedListDescriptors) {
  long fileOffset = 0;
  int count = 0;
  try {
    byte[] jvm = new byte[BLOCK_SIZE];
    int offset = 0;
    for (InvertedList list : postings.values()) {
      int length = list.size();
      if (offset + length >= BLOCK_SIZE) {
        direct.put(jvm, 0, offset);
        direct.rewind();
        direct.limit(offset);
        channel.write(direct);
        direct.rewind();
        direct.limit(direct.capacity());
        offset = 0;
      }
      int written = list.encode(jvm, offset);
      assert written == length;
      count++;
      invertedListDescriptors.add(
          new InvertedListDescriptor(
              list.getWord(), fileOffset, length, list.getDocumentFrequency()));
      offset += length;
      fileOffset += length;
    }
    direct.put(jvm, 0, offset);
    direct.rewind();
    direct.limit(offset);
    channel.write(direct);
    direct.rewind();
    direct.limit(direct.capacity());
  } catch (Throwable t) {
    log.fatal("exception writing index file " + t, t);
    throw new RuntimeException(t);
  }
  System.err.println("Words indexed = " + count);
  return fileOffset;
}

@Override
public void addContent(ChannelBuffer buffer, boolean last) throws IOException {
  if (buffer != null) {
    int localsize = buffer.readableBytes();
    if (definedSize > 0 && definedSize < size + localsize) {
      throw new IOException("Out of size: " + (size + localsize) + " > " + definedSize);
    }
    ByteBuffer byteBuffer = buffer.toByteBuffer();
    int written = 0;
    if (file == null) {
      file = tempFile();
    }
    if (fileChannel == null) {
      FileOutputStream outputStream = new FileOutputStream(file);
      fileChannel = outputStream.getChannel();
    }
    while (written < localsize) {
      written += fileChannel.write(byteBuffer);
      fileChannel.force(false);
    }
    size += localsize;
    buffer.readerIndex(buffer.readerIndex() + written);
  }
  if (last) {
    if (file == null) {
      file = tempFile();
    }
    if (fileChannel == null) {
      FileOutputStream outputStream = new FileOutputStream(file);
      fileChannel = outputStream.getChannel();
    }
    fileChannel.close();
    fileChannel = null;
    completed = true;
  } else {
    if (buffer == null) {
      throw new NullPointerException("buffer");
    }
  }
}

/**
 * Wites a tree node to the qix file
 *
 * @param node The node
 * @param order byte order
 * @throws IOException
 */
private void writeNode(Node node, FileChannel channel, ByteOrder order)
    throws IOException, StoreException {
  int offset = this.getSubNodeOffset(node);

  ByteBuffer buf = ByteBuffer.allocate((4 * 8) + (3 * 4) + (node.getNumShapeIds() * 4));

  buf.order(order);
  buf.putInt(offset);

  Envelope env = node.getBounds();
  buf.putDouble(env.getMinX());
  buf.putDouble(env.getMinY());
  buf.putDouble(env.getMaxX());
  buf.putDouble(env.getMaxY());

  buf.putInt(node.getNumShapeIds());

  for (int i = 0; i < node.getNumShapeIds(); i++) {
    buf.putInt(node.getShapeId(i));
  }

  buf.putInt(node.getNumSubNodes());
  buf.flip();

  channel.write(buf);

  for (int i = 0; i < node.getNumSubNodes(); i++) {
    this.writeNode(node.getSubNode(i), channel, order);
  }
}

private void writeIFDEntry(FileChannel channel, IFDEntry ifd, int start) throws IOException {
  channel.position(start);
  ByteBuffer buffer = ByteBuffer.allocate(12);

  buffer.putShort((short) ifd.tag.getCode());
  buffer.putShort((short) ifd.type.code);
  buffer.putInt(ifd.count);

  int size = ifd.count * ifd.type.size;
  if (size <= 4) {
    int done = writeValues(buffer, ifd);
    for (int k = 0; k < 4 - done; k++) // fill out to 4 bytes
    buffer.put((byte) 0);
    buffer.flip();
    channel.write(buffer);

  } else { // write offset
    buffer.putInt(nextOverflowData);
    buffer.flip();
    channel.write(buffer);
    // write data
    channel.position(nextOverflowData);
    // System.out.println(" write offset = "+ifd.tag.getName());
    ByteBuffer vbuffer = ByteBuffer.allocate(size);
    writeValues(vbuffer, ifd);
    vbuffer.flip();
    channel.write(vbuffer);
    nextOverflowData += size;
  }
}

