private void preallocate() throws IOException {
  long position = fc.position();
  if (position + 4096 >= fc.size()) {
    FSNamesystem.LOG.debug("Preallocating Edit log, current size " + fc.size());
    long newsize = position + 1024 * 1024; // 1MB
    fill.position(0);
    int written = fc.write(fill, newsize);
    FSNamesystem.LOG.debug(
        "Edit log size is now "
            + fc.size()
            + " written "
            + written
            + " bytes "
            + " at offset "
            + newsize);
  }
}

@Override
public void commitOffset(final String group, final Collection<TopicPartitionRegInfo> infoList) {
  if (infoList == null || infoList.isEmpty()) {
    return;
  }
  this.groupInfoMap.put(group, (List<TopicPartitionRegInfo>) infoList);
  FileOutputStream out = null;
  FileChannel channel = null;
  try {
    final String json = JSONUtils.serializeObject(this.groupInfoMap);
    // write to temp file
    File tmpFile = new File(this.filePath + ".tmp." + this.counter.incrementAndGet());
    out = new FileOutputStream(tmpFile);
    channel = out.getChannel();
    final ByteBuffer buf = ByteBuffer.wrap(json.getBytes());
    while (buf.hasRemaining()) {
      channel.write(buf);
    }
    this.close(channel);
    this.close(out);
    // rename temp file to target file.
    synchronized (this) {
      if (!tmpFile.renameTo(new File(this.filePath))) {
        throw new IOException("Could not rename temp file to " + this.filePath);
      }
    }
  } catch (final Exception e) {
    log.error("commitOffset failed ", e);
  } finally {
    if (channel != null && channel.isOpen()) {
      this.close(channel);
    }
    this.close(out);
  }
}

/** Performs a protected {@link FileChannel#write(ByteBuffer, long)} call. */
public int write(ByteBuffer buf, long offset) throws IOException {
  while (true) {
    try {
      return fc.write(buf, offset);
    } catch (ClosedByInterruptException e) {
      throw e;
    } catch (ClosedChannelException e) {
      reopen(e);
    }
  }
}

/**
 * Recursively copy a directory from a source to a target
 *
 * @param sourceDirectory Source directory
 * @param targetDirectory Destination directory
 * @throws IOException If there is an error copying the files and directories
 * @since blojsom 2.06
 */
public static void copyDirectory(File sourceDirectory, File targetDirectory) throws IOException {
  File[] sourceFiles = sourceDirectory.listFiles(FILE_FILTER);
  File[] sourceDirectories = sourceDirectory.listFiles(DIRECTORY_FILTER);

  targetDirectory.mkdirs();

  // Copy the files
  if (sourceFiles != null && sourceFiles.length > 0) {
    for (int i = 0; i < sourceFiles.length; i++) {
      File sourceFile = sourceFiles[i];

      FileInputStream fis = new FileInputStream(sourceFile);
      FileOutputStream fos =
          new FileOutputStream(targetDirectory + File.separator + sourceFile.getName());
      FileChannel fcin = fis.getChannel();
      FileChannel fcout = fos.getChannel();

      ByteBuffer buf = ByteBuffer.allocateDirect(8192);
      long size = fcin.size();
      long n = 0;
      while (n < size) {
        buf.clear();
        if (fcin.read(buf) < 0) {
          break;
        }
        buf.flip();
        n += fcout.write(buf);
      }

      fcin.close();
      fcout.close();
      fis.close();
      fos.close();
    }
  }

  // Copy the directories
  if (sourceDirectories != null && sourceDirectories.length > 0) {
    for (int i = 0; i < sourceDirectories.length; i++) {
      File directory = sourceDirectories[i];
      File newTargetDirectory = new File(targetDirectory, directory.getName());

      copyDirectory(directory, newTargetDirectory);
    }
  }
}

public static void saveText(
    ISpaceProbe probe, File destFile, Charset charset, String zTerm, List<String> zlLines)
    throws DiskException {
  if (probe == null) throw new IllegalArgumentException("object is null");
  if (destFile == null) throw new IllegalArgumentException("object is null");
  if (charset == null) throw new IllegalArgumentException("object is null");
  if (zTerm == null) throw new IllegalArgumentException("object is null");
  if (zlLines == null) throw new IllegalArgumentException("object is null");
  final ByteBuffer bb = newEncoded(probe, charset, zTerm, zlLines);
  final FileOutputStream fos = newFileOutputStream(probe, destFile);
  final FileChannel fch = fos.getChannel();
  try {
    while (bb.hasRemaining()) {
      fch.write(bb);
    }
  } catch (final IOException ex) {
    final Ds ds = Ds.triedTo("Write encoded text to file channel", ex);
    ds.a("buffer", bb);
    probe.failFile(ds, destFile);
    throw new DiskException();
  } finally {
    close(probe, destFile, fos);
  }
}

private ReflogWriter log(final String refName, final byte[] rec) throws IOException {
  final File log = logFor(refName);
  final boolean write =
      forceWrite || (isLogAllRefUpdates() && shouldAutoCreateLog(refName)) || log.isFile();
  if (!write) return this;

  WriteConfig wc = getRepository().getConfig().get(WriteConfig.KEY);
  FileOutputStream out;
  try {
    out = new FileOutputStream(log, true);
  } catch (FileNotFoundException err) {
    final File dir = log.getParentFile();
    if (dir.exists()) throw err;
    if (!dir.mkdirs() && !dir.isDirectory())
      throw new IOException(MessageFormat.format(JGitText.get().cannotCreateDirectory, dir));
    out = new FileOutputStream(log, true);
  }
  try {
    if (wc.getFSyncRefFiles()) {
      FileChannel fc = out.getChannel();
      ByteBuffer buf = ByteBuffer.wrap(rec);
      while (0 < buf.remaining()) fc.write(buf);
      fc.force(true);
    } else out.write(rec);
  } finally {
    out.close();
  }
  return this;
}

/**
 * Drain internal buffers into underlying channels.
 *
 * @throws IOException DOCUMENT ME!
 */
private void drain() throws IOException {
  writeBuffer.flip();

  int written = 0;

  while (writeBuffer.remaining() > 0) written += channel.write(writeBuffer, position);

  position += written;

  writeBuffer.flip().limit(writeBuffer.capacity());
}

