public int writeGrid() {

  try {

    if (raster == null) {
      // Create raster
      SampleModel sampleModel =
          RasterFactory.createBandedSampleModel(DataBuffer.TYPE_FLOAT, nCols, nRows, 1);
      dataArray = new float[nCols * nRows];
      DataBuffer db = new DataBufferFloat(dataArray, nCols * nRows);
      java.awt.Point point = new java.awt.Point();
      point.setLocation(xllCorner, yllCorner);
      raster = RasterFactory.createRaster(sampleModel, db, point);
    }

    if (writeHdr() != 0) return -1;

    File fileOut = new File(fltFullFileName);
    FileOutputStream fileOutStream = new FileOutputStream(fileOut);
    FileChannel fileChannelOut = fileOutStream.getChannel();

    ByteBuffer bb = ByteBuffer.allocateDirect(nCols * 4);
    bb.order(ByteOrder.LITTLE_ENDIAN);

    for (int r = 0; r < nRows; r++) {
      for (int c = 0; c < nCols; c++) {
        if (bb.hasRemaining()) {
          bb.putFloat(raster.getSampleFloat(c, r, 0));
        } else {
          c--;
          bb.compact();
          fileChannelOut.write(bb);
          bb.clear();
        }
      }
    }

    bb.compact();
    fileChannelOut.write(bb);
    bb.clear();

    return 0;

  } catch (Exception ex) {
    JOptionPane.showMessageDialog(
        null, "Error while reading flt file: " + ex, "Error", JOptionPane.ERROR_MESSAGE);
    return 1;
  }
}

public long writeDictionary(
    Map<Integer, String> idToDoc,
    Map<String, Integer> prefixes,
    List<InvertedListDescriptor> invertedListDescriptors) {
  long fileOffset = 0;
  DocumentDescriptor doc = new DocumentDescriptor();
  int count = 0;
  try {
    int header = Type.BYTE.length() + Type.LONG.length();
    channel.position(header);
    fileOffset = header;

    byte[] jvm = new byte[BLOCK_SIZE];
    int offset = 0;
    for (Map.Entry<Integer, String> document : idToDoc.entrySet()) {
      doc.setDocId(document.getKey());
      doc.setDoc(document.getValue());
      int length = doc.size();
      if (offset + length >= BLOCK_SIZE) {
        direct.put(jvm, 0, offset);
        direct.rewind();
        direct.limit(offset);
        channel.write(direct);
        direct.rewind();
        direct.limit(direct.capacity());
        offset = 0;
      }
      int written = doc.encode(jvm, offset);
      assert written == length;
      offset += length;
      fileOffset += length;
      count++;
    }
    direct.put(jvm, 0, offset);
    direct.rewind();
    direct.limit(offset);
    channel.write(direct);
    direct.rewind();
    direct.limit(direct.capacity());

    long docIdOffset = fileOffset;
    offset = 0;
    for (InvertedListDescriptor desc : invertedListDescriptors) {
      int length = desc.size();
      if (offset + length >= BLOCK_SIZE) {
        direct.put(jvm, 0, offset);
        direct.rewind();
        direct.limit(offset);
        channel.write(direct);
        direct.rewind();
        direct.limit(direct.capacity());
        offset = 0;
      }
      int written = desc.encode(jvm, offset);
      assert written == length;
      offset += length;
      fileOffset += length;
      count++;
    }
    direct.put(jvm, 0, offset);
    direct.rewind();
    direct.limit(offset);
    channel.write(direct);
    direct.rewind();
    direct.limit(direct.capacity());
    channel.position(0);
    ByteBuffer headerData = ByteBuffer.wrap(jvm);
    Codec.getCodec().encode(docIdOffset, jvm, 0);
    byte postingsCoding = 0;
    if (InvertedList.getTableType() == TableType.VariableByteCodedTable) {
      postingsCoding = 1;
    }
    Codec.getCodec().encode(postingsCoding, jvm, Type.LONG.length());
    // Codec.getCodec().encode(prefixLength, jvm, Type.LONG.length()+Type.BYTE.length());
    headerData.limit(Type.LONG.length() + Type.BYTE.length() + Type.INTEGER.length());
    channel.write(headerData);
    System.err.println("Dictionary items " + count);
  } catch (Throwable t) {
    log.fatal("exception writing index file " + t, t);
    throw new RuntimeException(t);
  }
  return fileOffset;
}

