/**
 * Write out any unsaved data to disk.
 *
 * @throws IOException If there is an error writing to disk.
 */
private void sync() throws IOException {
  if (!writeable) return;

  // Ensure that a complete block is written out.
  int bs = blockManager.getBlockSize();
  long rem = bs - (file.position() % bs);

  ByteBuffer buf = ByteBuffer.allocate(blockManager.getBlockSize());

  // Complete any partial block.
  for (int i = 0; i < rem; i++) buf.put((byte) 0);

  buf.flip();
  file.write(buf);
}

private void writeIFDEntry(FileChannel channel, IFDEntry ifd, int start) throws IOException {
  channel.position(start);
  ByteBuffer buffer = ByteBuffer.allocate(12);

  buffer.putShort((short) ifd.tag.getCode());
  buffer.putShort((short) ifd.type.code);
  buffer.putInt(ifd.count);

  int size = ifd.count * ifd.type.size;
  if (size <= 4) {
    int done = writeValues(buffer, ifd);
    for (int k = 0; k < 4 - done; k++) // fill out to 4 bytes
    buffer.put((byte) 0);
    buffer.flip();
    channel.write(buffer);

  } else { // write offset
    buffer.putInt(nextOverflowData);
    buffer.flip();
    channel.write(buffer);
    // write data
    channel.position(nextOverflowData);
    // System.out.println(" write offset = "+ifd.tag.getName());
    ByteBuffer vbuffer = ByteBuffer.allocate(size);
    writeValues(vbuffer, ifd);
    vbuffer.flip();
    channel.write(vbuffer);
    nextOverflowData += size;
  }
}

public int write(ByteBuffer src) throws IOException {
  byte[] buff = src.array();
  int len = src.remaining();
  if (src.position() != 0 || len != buff.length) {
    byte[] b = new byte[len];
    System.arraycopy(buff, src.position(), b, 0, len);
    buff = b;
  }
  int result = channel.write(src);
  rec.log(Recorder.WRITE, name, buff, channel.position());
  return result;
}

public static void fillWith(byte whichOne, FileChannel channel, long position, long sizeInBytes)
    throws IOException {

  channel.position(position);

  byte[] b = new byte[(int) (512 * ByteUtil.KB)];
  Arrays.fill(b, whichOne);
  ByteBuffer buffer = ByteBuffer.wrap(b);

  int filled = 0;
  long remaining;

  do {
    remaining = sizeInBytes - filled;

    if (remaining >= b.length) {
      buffer.position(0);
      channel.write(buffer);
      filled += b.length;
    } else {
      assert remaining <= Integer.MAX_VALUE;

      buffer = ByteBuffer.wrap(b, 0, (int) remaining);
      channel.write(buffer);
      filled += remaining;
    }
  } while (remaining > 0L);
}

private int writeIndexMap(long filePosition) throws IOException {
  // Write 4 byte header, 4 byte number of entries, and 20 bytes for each entry
  int numMappings = indexMap_.size();
  ByteBuffer buffer = ByteBuffer.allocate(8 + 20 * numMappings).order(byteOrder_);
  buffer.putInt(0, MultipageTiffWriter.INDEX_MAP_HEADER);
  buffer.putInt(4, numMappings);
  int position = 2;
  for (String label : indexMap_.keySet()) {
    String[] indecies = label.split("_");
    for (String index : indecies) {
      buffer.putInt(4 * position, Integer.parseInt(index));
      position++;
    }
    buffer.putInt(4 * position, indexMap_.get(label).intValue());
    position++;
  }
  fileChannel_.write(buffer, filePosition);

  ByteBuffer header = ByteBuffer.allocate(8).order(byteOrder_);
  header.putInt(0, MultipageTiffWriter.INDEX_MAP_OFFSET_HEADER);
  header.putInt(4, (int) filePosition);
  fileChannel_.write(header, 8);
  return buffer.capacity();
}

/**
 * Wites a tree node to the qix file
 *
 * @param node The node
 * @param channel DOCUMENT ME!
 * @param order byte order
 * @throws IOException
 * @throws StoreException DOCUMENT ME!
 */
private void writeNode(Node node, FileChannel channel, ByteOrder order)
    throws IOException, StoreException {
  int offset = this.getSubNodeOffset(node);

  ByteBuffer buf = ByteBuffer.allocate((4 * 8) + (3 * 4) + (node.getNumShapeIds() * 4));

  buf.order(order);
  buf.putInt(offset);

  Envelope env = node.getBounds();
  buf.putDouble(env.getMinX());
  buf.putDouble(env.getMinY());
  buf.putDouble(env.getMaxX());
  buf.putDouble(env.getMaxY());

  buf.putInt(node.getNumShapeIds());

  for (int i = 0; i < node.getNumShapeIds(); i++) {
    buf.putInt(node.getShapeId(i));
  }

  buf.putInt(node.getNumSubNodes());
  buf.flip();

  channel.write(buf);

  for (int i = 0; i < node.getNumSubNodes(); i++) {
    this.writeNode(node.getSubNode(i), channel, order);
  }
}
