/**
 * Issues the specified request, calling its specified handler as appropriate
 *
 * <p>This should not be run from a UI thread
 *
 * @return
 * @throws APIException
 */
public void issue(Database db, ServerCredentials cred) throws APIException {

  URI uri;

  // Add the API key, if missing and we have it
  if (!query.contains("key=") && key != null) {
    String suffix = (query.contains("?")) ? "&key=" + key : "?key=" + key;
    query = query + suffix;
  }

  // Force lower-case
  method = method.toLowerCase();

  Log.i(TAG, "Request " + method + ": " + query);

  try {
    uri = new URI(query);
  } catch (URISyntaxException e1) {
    throw new APIException(APIException.INVALID_URI, "Invalid URI: " + query, this);
  }

  HttpClient client = new DefaultHttpClient();
  // The default implementation includes an Expect: header, which
  // confuses the Zotero servers.
  client.getParams().setParameter("http.protocol.expect-continue", false);
  // We also need to send our data nice and raw.
  client.getParams().setParameter("http.protocol.content-charset", "UTF-8");

  HttpGet get = new HttpGet(uri);
  HttpPost post = new HttpPost(uri);
  HttpPut put = new HttpPut(uri);
  HttpDelete delete = new HttpDelete(uri);

  for (HttpRequest request : Arrays.asList(get, post, put, delete)) {
    request.setHeader("Zotero-API-Version", "1");
  }

  // There are several shared initialization routines for POST and PUT
  if ("post".equals(method) || "put".equals(method)) {
    if (ifMatch != null) {
      post.setHeader("If-Match", ifMatch);
      put.setHeader("If-Match", ifMatch);
    }
    if (contentType != null) {
      post.setHeader("Content-Type", contentType);
      put.setHeader("Content-Type", contentType);
    }
    if (body != null) {
      Log.d(TAG, "Request body: " + body);
      // Force the encoding to UTF-8
      StringEntity entity;
      try {
        entity = new StringEntity(body, "UTF-8");
      } catch (UnsupportedEncodingException e) {
        throw new APIException(
            APIException.INVALID_UUID,
            "UnsupportedEncodingException. This shouldn't "
                + "be possible-- UTF-8 is certainly supported",
            this);
      }
      post.setEntity(entity);
      put.setEntity(entity);
    }
  }

  if ("get".equals(method)) {
    if (contentType != null) {
      get.setHeader("Content-Type", contentType);
    }
  }

  /* For requests that return Atom feeds or entries (XML):
   * ITEMS_ALL ]
   * ITEMS_FOR_COLLECTION ]- Except format=keys
   * ITEMS_CHILDREN ]
   *
   * ITEM_BY_KEY
   * COLLECTIONS_ALL
   * ITEM_NEW
   * ITEM_UPDATE
   * ITEM_ATTACHMENT_NEW
   * ITEM_ATTACHMENT_UPDATE
   */
  if ("xml".equals(disposition)) {
    XMLResponseParser parse = new XMLResponseParser(this);
    // These types will always have a temporary key that we've
    // been using locally, and which should be replaced by the
    // incoming item key.
    if (type == ITEM_NEW || type == ITEM_ATTACHMENT_NEW) {
      parse.update(updateType, updateKey);
    }

    try {
      HttpResponse hr;
      if ("post".equals(method)) {
        hr = client.execute(post);
      } else if ("put".equals(method)) {
        hr = client.execute(put);
      } else {
        // We fall back on GET here, but there really
        // shouldn't be anything else, so we throw in that case
        // for good measure
        if (!"get".equals(method)) {
          throw new APIException(
              APIException.INVALID_METHOD, "Unexpected method: " + method, this);
        }
        hr = client.execute(get);
      }

      // Record the response code
      status = hr.getStatusLine().getStatusCode();
      Log.d(TAG, status + " : " + hr.getStatusLine().getReasonPhrase());

      if (status < 400) {
        HttpEntity he = hr.getEntity();
        InputStream in = he.getContent();
        parse.setInputStream(in);
        // Entry mode if the request is an update (PUT) or if it is a request
        // for a single item by key (ITEM_BY_KEY)
        int mode =
            ("put".equals(method) || type == APIRequest.ITEM_BY_KEY)
                ? XMLResponseParser.MODE_ENTRY
                : XMLResponseParser.MODE_FEED;
        try {
          parse.parse(mode, uri.toString(), db);
        } catch (RuntimeException e) {
          throw new RuntimeException(
              "Parser threw exception on request: " + method + " " + query, e);
        }
      } else {
        ByteArrayOutputStream ostream = new ByteArrayOutputStream();
        hr.getEntity().writeTo(ostream);
        Log.e(TAG, "Error Body: " + ostream.toString());
        Log.e(TAG, "Request Body:" + body);

        if (status == 412) {
          // This is: "Precondition Failed", meaning that we provided
          // the wrong etag to update the item. That should mean that
          // there is a conflict between what we're sending (PUT) and
          // the server. We mark that ourselves and save the request
          // to the database, and also notify our handler.
          getHandler().onError(this, APIRequest.HTTP_ERROR_CONFLICT);
        } else {
          Log.e(TAG, "Response status " + status + " : " + ostream.toString());
          getHandler().onError(this, APIRequest.HTTP_ERROR_UNSPECIFIED);
        }
        status = getHttpStatus() + REQ_FAILING;
        recordAttempt(db);

        // I'm not sure whether we should throw here
        throw new APIException(APIException.HTTP_ERROR, ostream.toString(), this);
      }
    } catch (Exception e) {
      StringBuilder sb = new StringBuilder();
      for (StackTraceElement el : e.getStackTrace()) {
        sb.append(el.toString()).append("\n");
      }
      recordAttempt(db);
      throw new APIException(
          APIException.HTTP_ERROR, "An IOException was thrown: " + sb.toString(), this, e);
    }
  } // end if ("xml".equals(disposition)) {..}
  /* For requests that return non-XML data:
   * ITEMS_ALL ]
   * ITEMS_FOR_COLLECTION ]- For format=keys
   * ITEMS_CHILDREN ]
   *
   * No server response:
   * ITEM_DELETE
   * ITEM_MEMBERSHIP_ADD
   * ITEM_MEMBERSHIP_REMOVE
   * ITEM_ATTACHMENT_DELETE
   *
   * Currently not supported; return JSON:
   * ITEM_FIELDS
   * CREATOR_TYPES
   * ITEM_FIELDS_L10N
   * CREATOR_TYPES_L10N
   *
   * These ones use BasicResponseHandler, which gives us
   * the response as a basic string. This is only appropriate
   * for smaller responses, since it means we have to wait until
   * the entire response is received before parsing it, so we
   * don't use it for the XML responses.
   *
   * The disposition here is "none" or "raw".
   *
   * The JSON-returning requests, such as ITEM_FIELDS, are not currently
   * supported; they should have a disposition of their own.
   */
  else {
    BasicResponseHandler brh = new BasicResponseHandler();
    String resp;

    try {
      if ("post".equals(method)) {
        resp = client.execute(post, brh);
      } else if ("put".equals(method)) {
        resp = client.execute(put, brh);
      } else if ("delete".equals(method)) {
        resp = client.execute(delete, brh);
      } else {
        // We fall back on GET here, but there really
        // shouldn't be anything else, so we throw in that case
        // for good measure
        if (!"get".equals(method)) {
          throw new APIException(
              APIException.INVALID_METHOD, "Unexpected method: " + method, this);
        }
        resp = client.execute(get, brh);
      }
    } catch (IOException e) {
      StringBuilder sb = new StringBuilder();
      for (StackTraceElement el : e.getStackTrace()) {
        sb.append(el.toString()).append("\n");
      }
      recordAttempt(db);
      throw new APIException(
          APIException.HTTP_ERROR, "An IOException was thrown: " + sb.toString(), this);
    }

    if ("raw".equals(disposition)) {
      /*
       * The output should be a newline-delimited set of alphanumeric
       * keys.
       */

      String[] keys = resp.split("\n");

      ArrayList<String> missing = new ArrayList<String>();

      if (type == ITEMS_ALL || type == ITEMS_FOR_COLLECTION) {

        // Try to get a parent collection
        // Our query looks like this:
        // /users/5770/collections/2AJUSIU9/items
        int colloc = query.indexOf("/collections/");
        int itemloc = query.indexOf("/items");
        // The string "/collections/" is thirteen characters long
        ItemCollection coll = ItemCollection.load(query.substring(colloc + 13, itemloc), db);

        if (coll != null) {
          coll.loadChildren(db);

          // If this is a collection's key listing, we first look
          // for any synced keys we have that aren't in the list
          ArrayList<String> keyAL = new ArrayList<String>(Arrays.asList(keys));
          ArrayList<Item> notThere = coll.notInKeys(keyAL);
          // We should then remove those memberships
          for (Item i : notThere) {
            coll.remove(i, true, db);
          }
        }

        ArrayList<Item> recd = new ArrayList<Item>();
        for (String key1 : keys) {
          Item got = Item.load(key1, db);
          if (got == null) {
            missing.add(key1);
          } else {
            // We can update the collection membership immediately
            if (coll != null) coll.add(got, true, db);
            recd.add(got);
          }
        }

        if (coll != null) {
          coll.saveChildren(db);
          coll.save(db);
        }

        Log.d(TAG, "Received " + keys.length + " keys, " + missing.size() + " missing ones");
        Log.d(TAG, "Have " + (double) recd.size() / keys.length + " of list");

        if (recd.size() == keys.length) {
          Log.d(TAG, "No new items");
          succeeded(db);
        } else if ((double) recd.size() / keys.length < REREQUEST_CUTOFF) {
          Log.d(TAG, "Requesting full list");
          APIRequest mReq;
          if (type == ITEMS_FOR_COLLECTION) {
            mReq = fetchItems(coll, false, cred);
          } else {
            mReq = fetchItems(false, cred);
          }

          mReq.status = REQ_NEW;
          mReq.save(db);
        } else {
          Log.d(TAG, "Requesting " + missing.size() + " items one by one");
          APIRequest mReq;
          for (String key : missing) {
            // Queue request for the missing key
            mReq = fetchItem(key, cred);
            mReq.status = REQ_NEW;
            mReq.save(db);
          }
          // Queue request for the collection again, by key
          // XXX This is not the best way to make sure these
          // items are put in the correct collection.
          if (type == ITEMS_FOR_COLLECTION) {
            fetchItems(coll, true, cred).save(db);
          }
        }
      } else if (type == ITEMS_CHILDREN) {
        // Try to get a parent item
        // Our query looks like this:
        // /users/5770/items/2AJUSIU9/children
        int itemloc = query.indexOf("/items/");
        int childloc = query.indexOf("/children");
        // The string "/items/" is seven characters long
        Item item = Item.load(query.substring(itemloc + 7, childloc), db);

        ArrayList<Attachment> recd = new ArrayList<Attachment>();
        for (String key1 : keys) {
          Attachment got = Attachment.load(key1, db);
          if (got == null) missing.add(key1);
          else recd.add(got);
        }

        if ((double) recd.size() / keys.length < REREQUEST_CUTOFF) {
          APIRequest mReq;
          mReq = cred.prep(children(item));
          mReq.status = REQ_NEW;
          mReq.save(db);
        } else {
          APIRequest mReq;
          for (String key : missing) {
            // Queue request for the missing key
            mReq = fetchItem(key, cred);
            mReq.status = REQ_NEW;
            mReq.save(db);
          }
        }
      }
    } else if ("json".equals(disposition)) {
      // TODO
    } else {
      /* Here, disposition should be "none" */
      // Nothing to be done.
    }

    getHandler().onComplete(this);
  }
}

/** Run the http method. */
public void run() {
  long startCall = System.currentTimeMillis();
  String message = null;
  try {
    if (handler != null) handler.callStart(id);
    Log.d(TAG, "Start call " + id + " :" + String.valueOf(startCall));
    URI uri = new URI(this.uri);

    if (mStopped) return;

    synchronized (lock) {
      switch (method) {
        case GET:
          this.request = new HttpGet(uri);
          break;
        case POST:
          request = new HttpPost(uri);
          if (jSonObj != null)
            ((HttpPost) request).setEntity(new StringEntity(jSonObj, CHARSET_NAME));
          break;
        case PUT:
          request = new HttpPut(uri);
          if (jSonObj != null)
            ((HttpPut) request).setEntity(new StringEntity(jSonObj, CHARSET_NAME));
          break;
        case DELETE:
          request = new HttpDelete(uri);
          break;
        default:
          request = new HttpGet(uri);
      }
    }
    // Log.i(TAG, " REQUEST : "+request.getURI());
    HttpResponse response = httpClient.execute(request);

    synchronized (lock) {
      request = null;
    }

    HttpEntity entity = response.getEntity();
    // Log.i(TAG," STATUS "+String.valueOf(response.getStatusLine().getStatusCode()));
    String responseContent =
        convertStreamToString(entity.getContent(), entity.getContentLength());
    if (responseContent == null) throw new InternalClientException("Response empty.");
    entity.consumeContent();
    // Log.i(TAG," RESPONSE : "+responseContent);
    int statusCode = response.getStatusLine().getStatusCode();
    switch (statusCode) {
      case HttpStatus.SC_INTERNAL_SERVER_ERROR:
      case HttpStatus.SC_NOT_FOUND:
      case HttpStatus.SC_CONFLICT: // TODO Check if this error code is really used.
      case HttpStatus.SC_FORBIDDEN:
        JSONObject object = (JSONObject) new JSONTokener(responseContent).nextValue();
        if (object.has("description")) {
          // JSONObject errObj = object.getJSONObject("error");
          message = object.getString("description");
        } else {
          message = "";
        }
        if (statusCode == HttpStatus.SC_INTERNAL_SERVER_ERROR) {
          message = "Internal server error. " + message;
          throw new InternalBackEndException(message);
        } else {
          throw new IOBackEndException(message, 404);
        }
      case HttpStatus.SC_OK:
        if (handler != null) handler.callSuccess(id, responseContent);
        break;
      default:
        throw new InternalClientException("Unknown status code.");
    }
  } catch (AbstractMymedException e) {
    if (mStopped) {
      message = CALL_INTERRUPTED + id;
      handler.callInterrupted(id);
    } else {
      handler.callError(id, e.getStatus(), message);
    }
  } catch (Exception e) {
    if (mStopped) {
      message = CALL_INTERRUPTED + id;
      handler.callInterrupted(id);
    } else {
      message = CALL_ERROR + e.toString();
      handler.callError(id, 400, message);
    }
  } finally {
    long endCall = System.currentTimeMillis();
    if (message != null) Log.d(TAG, "Result: " + message);
    Log.d(
        TAG,
        "End call "
            + id
            + " :"
            + String.valueOf(endCall)
            + " Duration: "
            + String.valueOf(endCall - startCall));
  }
}

/**
 * MÃ©todo que pega a resposta do servidor no tipo HttpResponse e transforma em string,
 *
 * @param response Resposta em HttpResponse do servidor.
 * @return Resposta transformada em String.
 */
private static String responseToString(HttpResponse response) {
  if (response == null) {
    return null;
  }
  String res = null;
  try {
    res = EntityUtils.toString(response.getEntity(), HTTP.UTF_8);
    res = new String(res.getBytes("UTF-8"), "UTF-8");
  } catch (ParseException e) {
    e.printStackTrace();
  } catch (IOException e) {
    e.printStackTrace();
  }
  return res;
}

private void perform(ZLNetworkRequest request, int socketTimeout, int connectionTimeout)
    throws ZLNetworkException {
  boolean success = false;
  DefaultHttpClient httpClient = null;
  HttpEntity entity = null;
  try {
    final HttpContext httpContext = new BasicHttpContext();
    httpContext.setAttribute(ClientContext.COOKIE_STORE, myCookieStore);

    request.doBefore();
    final HttpParams params = new BasicHttpParams();
    HttpConnectionParams.setSoTimeout(params, socketTimeout);
    HttpConnectionParams.setConnectionTimeout(params, connectionTimeout);
    httpClient = new DefaultHttpClient(params);
    final HttpRequestBase httpRequest;
    if (request.PostData != null) {
      httpRequest = new HttpPost(request.URL);
      ((HttpPost) httpRequest).setEntity(new StringEntity(request.PostData, "utf-8"));
      /*
      httpConnection.setRequestProperty(
      "Content-Length",
      Integer.toString(request.PostData.getBytes().length)
      );
      httpConnection.setRequestProperty(
      "Content-Type",
      "application/x-www-form-urlencoded"
      );
      */
    } else if (!request.PostParameters.isEmpty()) {
      httpRequest = new HttpPost(request.URL);
      final List<BasicNameValuePair> list =
          new ArrayList<BasicNameValuePair>(request.PostParameters.size());
      for (Map.Entry<String, String> entry : request.PostParameters.entrySet()) {
        list.add(new BasicNameValuePair(entry.getKey(), entry.getValue()));
      }
      ((HttpPost) httpRequest).setEntity(new UrlEncodedFormEntity(list, "utf-8"));
    } else {
      httpRequest = new HttpGet(request.URL);
    }
    httpRequest.setHeader("User-Agent", ZLNetworkUtil.getUserAgent());
    httpRequest.setHeader("Accept-Encoding", "gzip");
    httpRequest.setHeader("Accept-Language", Locale.getDefault().getLanguage());
    httpClient.setCredentialsProvider(new MyCredentialsProvider(httpRequest, request.isQuiet()));
    HttpResponse response = null;
    IOException lastException = null;
    for (int retryCounter = 0; retryCounter < 3 && entity == null; ++retryCounter) {
      try {
        response = httpClient.execute(httpRequest, httpContext);
        entity = response.getEntity();
        lastException = null;
        if (response.getStatusLine().getStatusCode() == HttpURLConnection.HTTP_UNAUTHORIZED) {
          final AuthState state =
              (AuthState) httpContext.getAttribute(ClientContext.TARGET_AUTH_STATE);
          if (state != null) {
            final AuthScopeKey key = new AuthScopeKey(state.getAuthScope());
            if (myCredentialsCreator.removeCredentials(key)) {
              entity = null;
            }
          }
        }
      } catch (IOException e) {
        lastException = e;
      }
    }
    if (lastException != null) {
      throw lastException;
    }
    final int responseCode = response.getStatusLine().getStatusCode();

    InputStream stream = null;
    if (entity != null
        && (responseCode == HttpURLConnection.HTTP_OK
            || responseCode == HttpURLConnection.HTTP_PARTIAL)) {
      stream = entity.getContent();
    }

    if (stream != null) {
      try {
        final Header encoding = entity.getContentEncoding();
        if (encoding != null && "gzip".equalsIgnoreCase(encoding.getValue())) {
          stream = new GZIPInputStream(stream);
        }
        request.handleStream(stream, (int) entity.getContentLength());
      } finally {
        stream.close();
      }
      success = true;
    } else {
      if (responseCode == HttpURLConnection.HTTP_UNAUTHORIZED) {
        throw new ZLNetworkException(ZLNetworkException.ERROR_AUTHENTICATION_FAILED);
      } else {
        throw new ZLNetworkException(true, response.getStatusLine().toString());
      }
    }
  } catch (ZLNetworkException e) {
    throw e;
  } catch (IOException e) {
    e.printStackTrace();
    final String code;
    if (e instanceof UnknownHostException) {
      code = ZLNetworkException.ERROR_RESOLVE_HOST;
    } else {
      code = ZLNetworkException.ERROR_CONNECT_TO_HOST;
    }
    throw new ZLNetworkException(code, ZLNetworkUtil.hostFromUrl(request.URL), e);
  } catch (Exception e) {
    e.printStackTrace();
    throw new ZLNetworkException(true, e.getMessage(), e);
  } finally {
    request.doAfter(success);
    if (httpClient != null) {
      httpClient.getConnectionManager().shutdown();
    }
    if (entity != null) {
      try {
        entity.consumeContent();
      } catch (IOException e) {
      }
    }
  }
}

@Override
protected Void doInBackground(Boolean... params) {

  try {

    /* Prepare variables for remote data check */
    HttpClient client = new DefaultHttpClient();
    String postURL = ConstantValues.URL + "/ws/ws-perfildetalle.php";
    HttpPost post = new HttpPost(postURL);
    List<NameValuePair> param = new ArrayList<NameValuePair>();
    param.add(new BasicNameValuePair("iduser", id));
    param.add(new BasicNameValuePair("tipo", tipo));
    UrlEncodedFormEntity ent = new UrlEncodedFormEntity(param);
    post.setEntity(ent);
    HttpResponse responsePOST = client.execute(post);
    StatusLine status = responsePOST.getStatusLine();
    /* Filter what kind of response was obtained */
    /* Filtering http response 200 */
    if (status.getStatusCode() == HttpStatus.SC_OK) {
      HttpEntity new_entity = responsePOST.getEntity();
      InputStream inputStream = new_entity.getContent();
      BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream));
      String line = null;
      stringBuilder = new StringBuilder();
      while ((line = reader.readLine()) != null) {
        stringBuilder.append(line);
      }
      JSONObject SnackjsonObject = new JSONObject(stringBuilder.toString());
      statusResponse = SnackjsonObject.getString("status");

      if (Integer.parseInt(statusResponse) == 1) {
        /* Sin error */
        SnackjsonArray = new JSONArray(SnackjsonObject.getString("datos"));
        for (int i = 0; i < SnackjsonArray.length(); i++) {
          JSONObject jsonObject = SnackjsonArray.getJSONObject(i);
          pathPhoto = jsonObject.getString("rutafoto");
          String urls = ConstantValues.URL + "/ws/productphoto/" + pathPhoto;
          LifeGuard tmpLG = new LifeGuard();
          tmpLG.setPath(urls);
          tmpLG.setImage(imageArray[i]);
          tmpLG.setProgress(progressArray[i]);
          SnackImageLoad imageLoader = new SnackImageLoad();
          imageLoader.execute(tmpLG);
        }

      } else {
        // Hubo un error
      }
      reader.close();
      inputStream.close();

    } else {
      /* Check Other Status Code */
    }
  } catch (Exception e) {
    e.printStackTrace();
  }
  return null;
}

/**
 * Marshall and send the request.
 *
 * @param headers
 * @param entityString
 * @return
 * @throws SendRequestFailedException
 * @throws SSLException
 * @throws AuthenticationFailure
 * @throws ConnectionFailedException
 * @throws ConnectionPoolTimeoutException
 */
private synchronized InputStream sendRequest(Header[] headers, String entityString)
    throws SendRequestFailedException, SSLException, AuthenticationFailure,
        ConnectionFailedException, ConnectionPoolTimeoutException {
  long down = 0;
  long up = 0;
  long start = System.currentTimeMillis();

  if (!initialised) throw new IllegalStateException("AcalRequestor has not been initialised!");
  statusCode = -1;
  try {
    // Create request and add headers and entity
    request = new DavRequest(method, this.fullUrl());
    // request.addHeader(new BasicHeader("User-Agent", AcalConnectionPool.getUserAgent()));
    if (headers != null) for (Header h : headers) request.addHeader(h);

    if (authRequired && authType != Servers.AUTH_NONE) request.addHeader(buildAuthHeader());
    else if (authRequired) {
      // Assume basicAuth
      request.addHeader(basicAuthHeader());
    }

    if (entityString != null) {
      request.setEntity(new StringEntity(entityString.toString(), "UTF-8"));
      up = request.getEntity().getContentLength();
    }

    // This trick greatly reduces the occurrence of host not found errors.
    try {
      InetAddress.getByName(this.hostName);
    } catch (UnknownHostException e1) {
      Thread.sleep(100);
      try {
        InetAddress.getByName(this.hostName);
      } catch (UnknownHostException e2) {
        Thread.sleep(100);
      }
    }

    int requestPort = -1;
    if (this.protocol == null) this.protocol = PROTOCOL_HTTP;
    String requestProtocol = this.protocol;
    if ((this.protocol.equals(PROTOCOL_HTTP) && this.port != 80)
        || (this.protocol.equals(PROTOCOL_HTTPS) && this.port != 443)) {
      requestPort = this.port;
    }

    if (Constants.LOG_DEBUG || debugThisRequest) {
      Log.println(
          Constants.LOGD,
          TAG,
          String.format(
              "Method: %s, Protocol: %s, Hostname: %s, Port: %d, Path: %s",
              method, requestProtocol, hostName, requestPort, path));
    }
    HttpHost host = new HttpHost(this.hostName, requestPort, requestProtocol);

    if (debugThisRequest) logRequest(Constants.LOGV);

    // Send request and get response
    response = null;

    if (Constants.debugHeap) AcalDebug.heapDebug(TAG, "Making HTTP request");
    try {
      response = httpClient.execute(host, request);
    } catch (ConnectionPoolTimeoutException e) {
      Log.println(
          Constants.LOGI,
          TAG,
          e.getClass().getSimpleName() + ": " + e.getMessage() + " to " + fullUrl());
      Log.println(Constants.LOGI, TAG, "Retrying...");
      response = httpClient.execute(host, request);
    }
    if (Constants.debugHeap) AcalDebug.heapDebug(TAG, "Finished HTTP request");

    this.responseHeaders = response.getAllHeaders();
    this.statusCode = response.getStatusLine().getStatusCode();

    HttpEntity entity = response.getEntity();
    down = (entity == null ? 0 : entity.getContentLength());

    long finish = System.currentTimeMillis();
    double timeTaken = ((double) (finish - start)) / 1000.0;

    if (Constants.LOG_DEBUG || debugThisRequest)
      Log.println(
          Constants.LOGD,
          TAG,
          "Response: "
              + statusCode
              + ", Sent: "
              + up
              + ", Received: "
              + down
              + ", Took: "
              + timeTaken
              + " seconds");

    if (debugThisRequest) {
      return logResponse(Constants.LOGV);
    } else if (entity != null) {
      if (entity.getContentLength() > 0) return entity.getContent();

      // Kind of admitting defeat here, but I can't track down why we seem
      // to end up in never-never land if we just return entity.getContent()
      // directly when entity.getContentLength() is -1 ('unknown', apparently).
      // Horribly inefficient too.
      //
      // @todo: Check whether this problem was caused by failing to close the InputStream
      // and this hack can be removed... Need to find a server which does not send Content-Length
      // headers.
      //
      String tmpEntity = entityToString(entity);
      return new ByteArrayInputStream(tmpEntity.getBytes());
    }

  } catch (SSLProtocolException e) {
    Log.i(TAG, e.getClass().getSimpleName() + ": " + e.getMessage() + " to " + fullUrl());
    return null;
  } catch (SSLHandshakeException e) {
    Log.i(TAG, e.getClass().getSimpleName() + ": " + e.getMessage() + " to " + fullUrl());
    throw e;
  } catch (SSLException e) {
    if (debugThisRequest) Log.println(Constants.LOGD, TAG, Log.getStackTraceString(e));
    throw e;
  } catch (AuthenticationFailure e) {
    if (debugThisRequest) Log.println(Constants.LOGD, TAG, Log.getStackTraceString(e));
    throw e;
  } catch (ConnectionPoolTimeoutException e) {
    Log.i(TAG, e.getClass().getSimpleName() + ": " + e.getMessage() + " to " + fullUrl());
    throw e;
  } catch (SocketTimeoutException e) {
    Log.i(TAG, e.getClass().getSimpleName() + ": " + e.getMessage() + " to " + fullUrl());
    return null;
  } catch (ConnectTimeoutException e) {
    Log.i(TAG, e.getClass().getSimpleName() + ": " + e.getMessage() + " to " + fullUrl());
    return null;
  } catch (UnknownHostException e) {
    Log.i(TAG, e.getClass().getSimpleName() + ": " + e.getMessage() + " to " + fullUrl());
    return null;
  } catch (IOException e) {
    Log.i(TAG, e.getClass().getSimpleName() + ": " + e.getMessage() + " to " + fullUrl());
    return null;
  } catch (Exception e) {
    Log.println(Constants.LOGD, TAG, Log.getStackTraceString(e));
    if (statusCode < 300 || statusCode > 499)
      throw new SendRequestFailedException(e.getMessage());
  }
  return null;
}

@Override
public void sendText(final TextableEndpoint from, final String text, final String type)
    throws IOException {
  ImifiedEndpointImpl bot = null;
  if (from instanceof ImifiedEndpointImpl) {
    bot = (ImifiedEndpointImpl) from;
  } else {
    throw new IllegalArgumentException("The from endpoint is not an ImifiedEndpoint instance.");
  }

  final HttpPost post = new HttpPost(_driver.getIMifiedURL());

  final String up = bot.getImifiedUserName() + ":" + bot.getImifiedPasswd();
  final String value = "Basic " + new String(Base64.encodeBase64(up.getBytes("UTF-8")), "UTF-8");
  post.addHeader("Authorization", value);

  final StringBuilder sb = new StringBuilder();
  sb.append("botkey=").append(bot.getKey());
  sb.append("&apimethod=send&userkey=").append(getKey());
  sb.append("&msg=").append(URLEncoder.encode(text, "UTF-8"));

  final HttpEntity en = new StringEntity(sb.toString());
  post.setEntity(en);
  post.setHeader("Content-Type", "application/x-www-form-urlencoded");
  post.setHeader("Connection", "keep-alive");

  if (LOG.isDebugEnabled()) {
    LOG.debug(
        "Sending message from "
            + bot.getAddress()
            + " to "
            + getAddress()
            + ", content:"
            + sb.toString());
  }
  HttpEntity resEntity = null;
  try {
    final HttpResponse response = _driver.execute(post);
    resEntity = response.getEntity();
    final BufferedReader reader =
        new BufferedReader(new InputStreamReader(resEntity.getContent()));

    if (resEntity
        .getContentType()
        .getValue()
        .trim()
        .toLowerCase()
        .startsWith("application/xml")) {
      String firstLine = null;
      do {
        firstLine = reader.readLine();
      } while (firstLine.trim().length() == 0);

      if (!(firstLine.indexOf("stat=\"ok\"") > 0)) {
        final StringBuilder output = new StringBuilder();
        output.append(firstLine).append("\r\n");
        String line = null;
        while ((line = reader.readLine()) != null) {
          output.append(line).append("\r\n");
        }
        throw new IOException("Imified returned error:" + output.toString());
      }
    } else {
      throw new IOException("Imified returned error message:" + reader.readLine());
    }

    if (LOG.isDebugEnabled()) {
      LOG.debug("Message sent.");
    }
  } finally {
    if (resEntity != null) {
      resEntity.consumeContent();
    }
  }
}

/**
 * Retrieves all results from the source site.
 *
 * @param criteria the search criteria.
 * @return the providers matched
 * @throws URISyntaxException if the URL could not be correctly constructed
 * @throws IOException for any I/O related errors
 * @throws ParsingException if any error occurs when parsing
 * @throws ServiceException for any other errors encountered
 */
private SearchResult<ProviderProfile> getAllResults(
    HealthCareFacilitySearchCriteria criteria, String providerCatagory)
    throws URISyntaxException, IOException, ServiceException {
  DefaultHttpClient client = new DefaultHttpClient();
  client.setRedirectStrategy(new LaxRedirectStrategy());

  // we need to get a token from the start page, this will be stored in the client
  HttpGet getFrontPage = new HttpGet(new URIBuilder(getSearchURL()).build());
  HttpResponse response = client.execute(getFrontPage);
  verifyAndAuditCall(getSearchURL(), response);
  EntityUtils.consume(response.getEntity()); // releases the connection

  // our client is now valid, pass the criteria to the search page
  String postSearchURL = Util.replaceLastURLPart(getSearchURL(), "showprovideroutput.cfm");
  HttpPost searchPage = new HttpPost(new URIBuilder(postSearchURL).build());
  String whichArea = "", selectCounty = "0", cityToFind = "", providerToFind = "";
  if ("county".equals(criteria.getCriteria())) {
    whichArea = "County";
    selectCounty = criteria.getValue();
  } else if ("city".equals(criteria.getCriteria())) {
    whichArea = "City";
    cityToFind = criteria.getValue();
  } else if ("provider".equals(criteria.getCriteria())) {
    whichArea = "Name";
    providerToFind = criteria.getValue();
  }
  HttpEntity entity =
      postForm(
          postSearchURL,
          client,
          searchPage,
          new String[][] {
            {"ProviderCatagory", providerCatagory},
            {"WhichArea", whichArea},
            {"Submit", "Submit"},
            {"SelectCounty", selectCounty},
            {"CityToFind", cityToFind},
            {"ProviderToFind", providerToFind}
          },
          true);

  List<ProviderProfile> allProviders = new ArrayList<ProviderProfile>();
  // this now holds the search results, parse every row
  Document page = Jsoup.parse(EntityUtils.toString(entity));
  Elements rows = page.select("div#body table tbody tr:gt(0)");
  for (Element row : rows) {
    ProviderProfile profile = parseProfile(row.children());
    if (profile != null) {
      allProviders.add(profile);
    }
  }

  SearchResult<ProviderProfile> results = new SearchResult<ProviderProfile>();
  results.setItems(allProviders);
  return results;
}

public HttpEntity fetch(
    String identifier,
    String acceptHeader,
    UriProvider uriProvider,
    RequestProvider requestProvider)
    throws HttpFetchException {
  int attemptIndex = 0;
  while (true) {
    HttpResponse response =
        attemptFetch(identifier, acceptHeader, uriProvider, requestProvider, attemptIndex);
    StatusLine status = response.getStatusLine();
    if (HttpStatus.SC_OK == status.getStatusCode()) {
      return response.getEntity();
    } else if (attemptIndex
        < settings.getRetries()) { // If retries = 2, then we should attempt 3 times in total
      logger.debug(
          "Retrying identifier '{}' due to response '{}'", identifier, status.getStatusCode());
      EntityUtils.consumeQuietly(response.getEntity());
      attemptIndex++;
      continue;
    } else {
      throw new HttpFetchException(
          String.format(
              "Could not process identifier '%s', got response '%s'",
              identifier, status.toString()));
    }
  }
}

/**
 * Open a stream for the HTTP response entity, handling I/O errors.
 *
 * @return an InputStream to read the response entity
 */
private InputStream openResponseEntity(State state, HttpResponse response) throws StopRequest {
  try {
    return response.getEntity().getContent();
  } catch (IOException ex) {
    logNetworkState();
    throw new StopRequest(
        getFinalStatusForHttpError(state), "while getting entity: " + ex.toString(), ex);
  }
}

