private BaseMandrillResponse performPostRequest(
    BaseMandrillRequest request,
    String serviceMethod,
    Object responseClass,
    TypeReference reference)
    throws RequestFailedException {
  try {
    request.setKey(config.getApiKey());
    HttpPost postRequest = new HttpPost(config.getServiceUrl() + serviceMethod);
    String postData = getPostData(request);
    StringEntity input = new StringEntity(postData);
    input.setContentType("application/json");
    postRequest.setEntity(input);

    HttpResponse response = httpClient.execute(postRequest);

    BufferedReader br =
        new BufferedReader(new InputStreamReader((response.getEntity().getContent())));

    StringBuffer sb = new StringBuffer();
    String output;
    System.out.println("Output from Server .... \n");
    while ((output = br.readLine()) != null) {
      sb.append(output);
      System.out.println(output);
    }

    String responseString = sb.toString();
    if (response.getStatusLine().getStatusCode() != 200) {
      throw new RequestFailedException(
          "Failed : HTTP error code : "
              + response.getStatusLine().getStatusCode()
              + " "
              + responseString);
    }

    // for whatever reason the ping response isn't well-formed
    if (responseString.indexOf("PONG!") > -1) {
      return new BaseMandrillStringResponse(responseString);
    }

    if (reference == null) {
      return convertResponseData(responseString, responseClass);
    } else {
      return convertAnonymousListResponseData(responseString, responseClass, reference);
    }
  } catch (MalformedURLException mURLE) {
    throw new RequestFailedException("Malformed url", mURLE);
  } catch (JsonGenerationException jge) {
    throw new RequestFailedException("Json Generation Exception", jge);
  } catch (JsonMappingException jme) {
    throw new RequestFailedException("Json Mapping Exception", jme);
  } catch (IOException ioe) {
    throw new RequestFailedException("IOException", ioe);
  }
}

/**
 * Download file.
 *
 * @param sourceUrl the source url
 * @param queryParams the query params
 * @param destinationFilePath the destination file path
 */
private void downloadFile(
    final String sourceUrl,
    final Map<String, String> queryParams,
    final String destinationFilePath) {
  final String prefix = "execute() :";

  if (LOGGER.isDebugEnabled()) {
    LOGGER.debug("{} entered", prefix);
    LOGGER.debug("{} sourceUrl={}", prefix, sourceUrl);
    LOGGER.debug("{} destinationFilePath={}", prefix, destinationFilePath);
  }

  try {
    final DefaultHttpClient httpClient = new DefaultHttpClient();
    final HttpGet httpGet = new HttpGet(sourceUrl);
    httpGet.setParams(buildQueryParams(queryParams));
    final HttpResponse response = httpClient.execute(httpGet);
    final int statusCode = response.getStatusLine().getStatusCode();

    if (statusCode != HttpStatus.SC_OK) {
      LOGGER.debug("HTTP status code {} returned", statusCode);
      StatusChangeEventBus.error(HTTP_DOWNLOAD_STATUS_ERROR, getJob(), sourceUrl, statusCode);
      throw new TaskExecuterException(
          ExceptionResourceType.HTTP_DOWNLOAD_STATUS_ERROR, statusCode);
    }

    final HttpEntity entity = response.getEntity();

    if (entity == null) {
      StatusChangeEventBus.error(HTTP_DOWNLOAD_NO_RESPONSE_ERROR, getJob());
      throw new TaskExecuterException(ExceptionResourceType.HTTP_DOWNLOAD_NO_RESPONSE);
    } else {
      final InputStream inputStream = entity.getContent();
      final OutputStream out = new FileOutputStream(destinationFilePath);
      final byte buffer[] = new byte[1024];
      int length;

      while ((length = inputStream.read(buffer)) > 0) {
        out.write(buffer, 0, length);
      }

      out.close();

      StatusChangeEventBus.info(HTTP_DOWNLOAD_FINISHED, getJob(), sourceUrl, destinationFilePath);
    }
  } catch (final ClientProtocolException e) {
    LOGGER.debug("ClientProtocolException caught", e);
    StatusChangeEventBus.error(HTTP_DOWNLOAD_ERROR, getJob(), sourceUrl, destinationFilePath);
    throw new TaskExecuterException(ExceptionResourceType.HTTP_DOWNLOAD_ERROR, e);
  } catch (final IOException e) {
    LOGGER.debug("IOException caught", e);
    StatusChangeEventBus.error(HTTP_DOWNLOAD_ERROR, getJob(), sourceUrl, destinationFilePath);
    throw new TaskExecuterException(ExceptionResourceType.HTTP_DOWNLOAD_ERROR, e);
  } catch (final Exception e) {
    LOGGER.debug("Exception caught", e);
    StatusChangeEventBus.error(HTTP_DOWNLOAD_ERROR, getJob(), sourceUrl, destinationFilePath);
    throw new TaskExecuterException(ExceptionResourceType.HTTP_DOWNLOAD_ERROR, e);
  }

  LOGGER.debug("{} leaving", prefix);
}

