public void put(final String url, String content) throws IOException, AuthenticationException {
  HttpPut method = null;

  try {
    method = new HttpPut(url);
    setAuthHeader(method);
    StringEntity reqEntity = new StringEntity(content);

    reqEntity.setContentType("text/xml");
    method.setEntity(reqEntity);

    HttpResponse response = new DefaultHttpClient().execute(method);

    if (response.getStatusLine().getStatusCode() == HttpStatus.SC_OK) {
      HttpEntity entity = response.getEntity();

      if (entity != null) {
        entity.consumeContent();
      }
    } else {
      throw new IOException(response.getStatusLine().getReasonPhrase());
    }
  } finally {
    if (method != null) {
      method.abort();
    }
  }
}

