/**
 * @param context
 * @param username
 * @param password
 * @return a Bundle containing the user's profile or null if authentication failed.
 * @throws IOException
 * @throws JSONException
 * @throws NetworkProtocolException
 */
public static Bundle authenticate(Context context, String username, String password)
    throws IOException, JSONException, NetworkProtocolException {
  final NetworkClient nc = getInstance(context);
  nc.overrideCredentials(username, password);
  boolean authenticated = false;
  try {
    final HttpResponse res = nc.get(PATH_USER);

    authenticated = nc.checkStatusCode(res, false);

    final HttpEntity ent = res.getEntity();
    JSONObject jo = null;

    if (authenticated) {
      jo = new JSONObject(StreamUtils.inputStreamToString(ent.getContent()));
      ent.consumeContent();
    } else {
      jo = null;
    }
    // ensure that this instance is never reused, as it could have invalid authentication cached.
    nc.clearCredentialsOverride();

    return jsonObjectToBundle(jo, true);
  } catch (final HttpResponseException e) {
    if (e.getStatusCode() == HttpStatus.SC_UNAUTHORIZED) {
      return null;
    } else {
      throw e;
    }
  }
}

/**
 * Connects with google mpa api and request direction
 *
 * @param start Starting point for directions request
 * @param end Endpoint for directions request
 * @param mode travel mode e.g. walking
 */
public Document getDocument(LatLng start, LatLng end, String mode) {
  String url =
      "http://maps.googleapis.com/maps/api/directions/xml?"
          + "origin="
          + start.latitude
          + ","
          + start.longitude
          + "&destination="
          + end.latitude
          + ","
          + end.longitude
          + "&sensor=false&units=metric&mode="
          + mode;

  try {
    HttpClient httpClient = new DefaultHttpClient();
    HttpContext localContext = new BasicHttpContext();
    HttpPost httpPost = new HttpPost(url);
    HttpResponse response = httpClient.execute(httpPost, localContext);
    InputStream in = response.getEntity().getContent();
    DocumentBuilder builder = DocumentBuilderFactory.newInstance().newDocumentBuilder();
    Document doc = builder.parse(in);
    return doc;
  } catch (Exception e) {
    e.printStackTrace();
  }
  return null;
}

@SuppressWarnings({"unchecked"})
private void doRefreshChannel(PodcastChannel channel, boolean downloadEpisodes) {
  InputStream in = null;
  HttpClient client = new DefaultHttpClient();

  try {
    channel.setStatus(PodcastStatus.DOWNLOADING);
    channel.setErrorMessage(null);
    podcastDao.updateChannel(channel);

    HttpConnectionParams.setConnectionTimeout(client.getParams(), 2 * 60 * 1000); // 2 minutes
    HttpConnectionParams.setSoTimeout(client.getParams(), 10 * 60 * 1000); // 10 minutes
    HttpGet method = new HttpGet(channel.getUrl());

    HttpResponse response = client.execute(method);
    in = response.getEntity().getContent();

    Document document = new SAXBuilder().build(in);
    Element channelElement = document.getRootElement().getChild("channel");

    channel.setTitle(channelElement.getChildTextTrim("title"));
    channel.setDescription(channelElement.getChildTextTrim("description"));
    channel.setStatus(PodcastStatus.COMPLETED);
    channel.setErrorMessage(null);
    podcastDao.updateChannel(channel);

    refreshEpisodes(channel, channelElement.getChildren("item"));

  } catch (Exception x) {
    LOG.warn("Failed to get/parse RSS file for Podcast channel " + channel.getUrl(), x);
    channel.setStatus(PodcastStatus.ERROR);
    channel.setErrorMessage(x.toString());
    podcastDao.updateChannel(channel);
  } finally {
    IOUtils.closeQuietly(in);
    client.getConnectionManager().shutdown();
  }

  if (downloadEpisodes) {
    for (final PodcastEpisode episode : getEpisodes(channel.getId(), false)) {
      if (episode.getStatus() == PodcastStatus.NEW && episode.getUrl() != null) {
        downloadEpisode(episode);
      }
    }
  }
}

