public byte[] getContentData() throws IOException {
  if (_res == null) {
    throw new RuntimeException("not setting data yet");
  }

  InputStream is = null;
  ByteArrayOutputStream out = null;
  try {
    HttpEntity entity = _res.getEntity();
    is = entity.getContent();

    out = new ByteArrayOutputStream((int) entity.getContentLength());
    byte[] line = new byte[1024];
    int size = 0;
    while (true) {
      size = is.read(line);
      if (size <= 0) {
        break;
      }
      out.write(line, 0, size);
    }

    return out.toByteArray();
  } finally {
    if (is != null) {
      try {
        is.close();
      } catch (Exception e) {
      }
    }
    if (out != null) {
      try {
        out.close();
      } catch (Exception e) {
      }
    }
  }
}

private ReferenceInputStream processResponse(HttpResponse response) throws IOException {

  HttpEntity entity = response.getEntity();
  Header header;

  header = entity.getContentType();
  String mimeType = header == null ? null : header.getValue();

  header = entity.getContentEncoding();
  String encoding = header == null ? null : header.getValue();

  return new ReferenceInputStream(entity.getContent(), mimeType, encoding);
}

