public ArrayList<Connection> execute() throws GriklyException {
  HttpClient client = new DefaultHttpClient();
  try {
    HttpResponse response = client.execute(prepareRequestMethod(new HttpGet()));
    int statusCode = response.getStatusLine().getStatusCode();
    if (statusCode >= 200 && statusCode < 300) {
      String entity = EntityUtils.toString(response.getEntity());
      ArrayList<Connection> arrayList =
          new Gson().fromJson(entity, new TypeToken<ArrayList<Connection>>() {}.getType());
      return arrayList;
    } else throw new GriklyException(EntityUtils.toString(response.getEntity()));
  } catch (ClientProtocolException e) {
    e.printStackTrace();
  } catch (IOException e) {
    e.printStackTrace();
  }
  return null;
} // end execute method

