/**
 * Send twitter REST requests
 *
 * @throws Exception If a problem occurs
 */
private void sendTwitterRestRequests() throws Exception {

  DefaultHttpClient httpProxyClient = new DefaultHttpClient();

  // Set client to use the HTTP Discovery Proxy
  HttpHost proxy = new HttpHost("localhost", EasySOAConstants.HTTP_DISCOVERY_PROXY_PORT);
  httpProxyClient.getParams().setParameter(ConnRoutePNames.DEFAULT_PROXY, proxy);

  // Send first request - GET request with only path parameters
  HttpResponse response;
  HttpGet lastTweetRequest =
      new HttpGet(
          "http://localhost:" + EasySOAConstants.TWITTER_MOCK_PORT + "/1/tweets/lastTweet/toto");
  response = httpProxyClient.execute(lastTweetRequest);
  // Need to read the response body entirely to be able to send another request
  ContentReader.read(response.getEntity().getContent());

  // Send second request - GET request with path parameters and query parameters
  HttpGet severalTweetRequest =
      new HttpGet(
          "http://localhost:"
              + EasySOAConstants.TWITTER_MOCK_PORT
              + "/1/tweets/severalTweets/toto?tweetNumber=5");
  response = httpProxyClient.execute(severalTweetRequest);
  ContentReader.read(response.getEntity().getContent());

  // Send third request - POST request with form parameters
  HttpPost postTweetRequest =
      new HttpPost(
          "http://localhost:" + EasySOAConstants.TWITTER_MOCK_PORT + "/1/tweets/postNewTweet");
  List<NameValuePair> formparams = new ArrayList<NameValuePair>();
  formparams.add(new BasicNameValuePair("user", "toto"));
  formparams.add(new BasicNameValuePair("tweet", "This is a tweet test"));
  UrlEncodedFormEntity entity = new UrlEncodedFormEntity(formparams, "UTF-8");
  postTweetRequest.setEntity(entity);
  response = httpProxyClient.execute(postTweetRequest);
  ContentReader.read(response.getEntity().getContent());
}

@Override
public NetworkResponse performRequest(Request<?> request) throws VolleyError {
  long requestStart = SystemClock.elapsedRealtime();
  while (true) {
    HttpResponse httpResponse = null;
    byte[] responseContents = null;
    Map<String, String> responseHeaders = new HashMap<String, String>();
    try {
      // Gather headers.
      Map<String, String> headers = new HashMap<String, String>();
      addCacheHeaders(headers, request.getCacheEntry());
      httpResponse = mHttpStack.performRequest(request, headers);
      StatusLine statusLine = httpResponse.getStatusLine();
      int statusCode = statusLine.getStatusCode();

      responseHeaders = convertHeaders(httpResponse.getAllHeaders());
      // Handle cache validation.
      if (statusCode == HttpStatus.SC_NOT_MODIFIED) {
        return new NetworkResponse(
            HttpStatus.SC_NOT_MODIFIED, request.getCacheEntry().data, responseHeaders, true);
      }

      responseContents = entityToBytes(httpResponse.getEntity());
      // if the request is slow, log it.
      long requestLifetime = SystemClock.elapsedRealtime() - requestStart;
      logSlowRequests(requestLifetime, request, responseContents, statusLine);

      // Status code from 200 to 207 are successful requests
      if (statusCode < HttpStatus.SC_OK || statusCode > HttpStatus.SC_MULTI_STATUS) {
        throw new IOException();
      }
      return new NetworkResponse(statusCode, responseContents, responseHeaders, false);
    } catch (SocketTimeoutException e) {
      attemptRetryOnException("socket", request, new TimeoutError());
    } catch (ConnectTimeoutException e) {
      attemptRetryOnException("connection", request, new TimeoutError());
    } catch (MalformedURLException e) {
      throw new RuntimeException("Bad URL " + request.getUrl(), e);
    } catch (IOException e) {
      int statusCode = 0;
      NetworkResponse networkResponse = null;
      if (httpResponse != null) {
        statusCode = httpResponse.getStatusLine().getStatusCode();
      } else {
        throw new NoConnectionError(e);
      }
      VolleyLog.e("Unexpected response code %d for %s", statusCode, request.getUrl());
      if (responseContents != null) {
        networkResponse =
            new NetworkResponse(statusCode, responseContents, responseHeaders, false);
        if (statusCode == HttpStatus.SC_UNAUTHORIZED || statusCode == HttpStatus.SC_FORBIDDEN) {
          attemptRetryOnException("auth", request, new AuthFailureError(networkResponse));
        } else {
          // TODO: Only throw ServerError for 5xx status codes.
          throw new ServerError(networkResponse);
        }
      } else {
        throw new NetworkError(networkResponse);
      }
    }
  }
}

private JSONObject postRequest(String url, List<NameValuePair> parametrosLista) {
  HttpResponse response = null;
  HttpEntity entity = null;
  InputStream inputStream = null;
  JSONObject jsonData = null;
  String json = "";
  try {
    DefaultHttpClient httpClient = new DefaultHttpClient();
    HttpPost post = new HttpPost(url);
    if (parametrosLista != null) {
      post.setEntity(new UrlEncodedFormEntity(parametrosLista));
    }
    response = httpClient.execute(post);
    StatusLine statusLine = response.getStatusLine();
    int statusCode = statusLine.getStatusCode();
    if (statusCode == 200) {
      entity = response.getEntity();
      inputStream = entity.getContent();
      // obtuvimos respuesta, a transformar...
      BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream, "utf-8"), 8);
      StringBuilder sb = new StringBuilder();
      String line = null;
      while ((line = reader.readLine()) != null) {
        sb.append(line).append("\n");
      }
      inputStream.close();
      json = sb.toString();
      jsonData = new JSONObject(json);
    } else {
      Log.e(NOMBRE_SERVICIO, "Failed to download JSON");
      jsonData =
          new JSONObject("{'estado':'0','errors':{'status':'Status code = " + statusCode + "'}}");
    }
  } catch (Exception e) {
    Log.e(NOMBRE_SERVICIO, "Ocurrio un error al parsear los datos" + e.toString());
  }
  return jsonData;
}
