private boolean isOnExternalStorage(Cursor cursor) {
  String localUriString = cursor.getString(mLocalUriColumnId);
  if (localUriString == null) {
    return false;
  }
  Uri localUri = Uri.parse(localUriString);
  if (!localUri.getScheme().equals("file")) {
    return false;
  }
  String path = localUri.getPath();
  String externalRoot = Environment.getExternalStorageDirectory().getPath();
  return path.startsWith(externalRoot);
}

/** initializes the database */
private void initDatabase() {

  //
  // initialize database manager
  //
  DBManager db_man = m_app.getDBManager();

  //
  // check if the default storage directory is registered
  //
  String path = Environment.getExternalStorageDirectory().getAbsolutePath();
  path += File.separator + ConfigurationSettings.TAGSTORE_DIRECTORY;
  path += File.separator + getString(R.string.storage_directory);

  if (!db_man.isDirectoryObserved(path)) {
    //
    // register path
    //
    db_man.addDirectory(path);
  }
}

/**
 * A backwards-compatible version of {@link Context#getExternalFilesDir(String)} which falls back
 * to using {@link Environment#getExternalStorageDirectory()} on API 7 and below.
 *
 * @param context context to get the external files directory for
 * @param type the type of files directory to return (may be null)
 * @return the path of the directory holding application files on external storage, or null if
 *     external storage cannot be accessed
 */
public static File getExternalFilesDir(final Context context, final String type) {
  if (METHOD_GET_EXTERNAL_FILES_DIR == null) {
    final File externalRoot = Environment.getExternalStorageDirectory();
    if (externalRoot == null) {
      return null;
    }

    final String packageName = context.getApplicationContext().getPackageName();
    return new File(externalRoot, "Android/data/" + packageName + "/files");
  } else {
    try {
      return (File) METHOD_GET_EXTERNAL_FILES_DIR.invoke(context, type);
    } catch (Exception e) {
      Log.e(LOG_TAG, "Could not invoke getExternalFilesDir: " + e.getMessage(), e);
      return null;
    }
  }
}

public static Bitmap loadThumbnail(String id) {
  File thumbDir = new File(Environment.getExternalStorageDirectory(), CACHE_DIRECTORY);
  File thumbFile = new File(thumbDir, String.valueOf(id));
  if (thumbFile.exists()) {
    InputStream stream = null;
    try {
      stream = new FileInputStream(thumbFile);
      return BitmapFactory.decodeStream(stream, null, null);
    } catch (FileNotFoundException e) {
      // Ignore
    } finally {
      try {
        if (stream != null) {
          stream.close();
        }
      } catch (IOException ignore) {
      }
    }
  }
  return null;
}

@SuppressWarnings("unchecked")
public void handleMessage(Message msgP) {
  final Synodroid app = (Synodroid) getApplication();
  final DetailMain main = (DetailMain) mAdapter.getItem(MAIN_ITEM);
  DetailFiles files = (DetailFiles) mAdapter.getItem(FILE_ITEM);
  try {
    if (((Synodroid) getApplication()).DEBUG)
      Log.v(Synodroid.DS_TAG, "DetailActivity: Message received with ID = " + msgP.what);
  } catch (Exception ex) {
    /*DO NOTHING*/
  }
  switch (msgP.what) {
    case ResponseHandler.MSG_DETAILS_FILES_RETRIEVED:
      try {
        if (app.DEBUG) Log.v(Synodroid.DS_TAG, "DetailActivity: Receive file listing message.");
      } catch (Exception ex) {
        /*DO NOTHING*/
      }

      List<TaskFile> tfile = (List<TaskFile>) msgP.obj;
      try {
        files.fileAdapter.updateFiles(tfile);

      } catch (Exception e) {
        if (app.DEBUG)
          Log.e(
              Synodroid.DS_TAG,
              "DetailActivity: An error occured while trying to update files list:",
              e);
      }
      break;
    case ResponseHandler.MSG_PROPERTIES_RECEIVED:
      try {
        if (app.DEBUG)
          Log.v(Synodroid.DS_TAG, "DetailActivity: Receive task properties message.");
      } catch (Exception ex) {
        /*DO NOTHING*/
      }

      TaskProperties tp = (TaskProperties) msgP.obj;
      ul_rate = tp.ul_rate;
      dl_rate = tp.dl_rate;
      max_peers = tp.max_peers;
      priority = tp.priority;
      seedingRatio = tp.seeding_ratio;
      seedingTime = tp.seeding_interval;
      destination = tp.destination;

      try {
        showDialog(TASK_PROPERTIES_DIALOG);
      } catch (Exception e) {
      }
      break;
    case ResponseHandler.MSG_SHARED_DIRECTORIES_RETRIEVED:
      try {
        if (app.DEBUG)
          Log.v(Synodroid.DS_TAG, "DetailActivity: Received shared directory listing message.");
      } catch (Exception ex) {
        /*DO NOTHING*/
      }

      AlertDialog.Builder builder = new AlertDialog.Builder(DetailActivity.this);

      if (((Synodroid) getApplication())
          .getServer()
          .getDsmVersion()
          .greaterThen(DSMVersion.VERSION3_0)) {
        final SharedFolderSelection sf = (SharedFolderSelection) msgP.obj;
        final String[] dirNames = new String[sf.childrens.size()];
        final String[] dirIDs = new String[sf.childrens.size()];
        for (int iLoop = 0; iLoop < sf.childrens.size(); iLoop++) {
          Folder sharedDir = sf.childrens.get(iLoop);
          dirNames[iLoop] = sharedDir.name;
          dirIDs[iLoop] = sharedDir.id;
        }
        builder.setTitle(getString(R.string.shared_dir_title) + ":\n" + sf.name);
        builder.setItems(
            dirNames,
            new DialogInterface.OnClickListener() {
              public void onClick(DialogInterface dialog, int item) {
                dialog.dismiss();
                app.executeAsynchronousAction(
                    main, new GetDirectoryListShares(dirIDs[item]), true);
              }
            });
        builder.setNegativeButton(
            R.string.button_cancel,
            new DialogInterface.OnClickListener() {
              public void onClick(DialogInterface dialog, int item) {
                dialog.dismiss();
              }
            });
        if (!sf.name.equals("/")) {
          builder.setPositiveButton(
              R.string.button_ok,
              new DialogInterface.OnClickListener() {
                public void onClick(DialogInterface dialog, int item) {
                  dialog.dismiss();
                  app.executeAsynchronousAction(main, new SetShared(task, sf.name), true);
                }
              });
        }

      } else {
        List<SharedDirectory> newDirs = (List<SharedDirectory>) msgP.obj;
        final String[] dirNames = new String[newDirs.size()];
        int selected = -1;
        for (int iLoop = 0; iLoop < newDirs.size(); iLoop++) {
          SharedDirectory sharedDir = newDirs.get(iLoop);
          dirNames[iLoop] = sharedDir.name;
          if (sharedDir.isCurrent) {
            selected = iLoop;
          }
        }
        builder.setTitle(getString(R.string.shared_dir_title));
        builder.setSingleChoiceItems(
            dirNames,
            selected,
            new DialogInterface.OnClickListener() {
              public void onClick(DialogInterface dialog, int item) {
                dialog.dismiss();
                app.executeAsynchronousAction(main, new SetShared(task, dirNames[item]), true);
              }
            });
      }

      AlertDialog alert = builder.create();
      try {
        alert.show();
      } catch (BadTokenException e) {
        // Unable to show dialog probably because intent has been closed. Ignoring...
      }
      break;
      // Details updated
    case ResponseHandler.MSG_DETAILS_RETRIEVED:
      try {
        if (app.DEBUG) Log.v(Synodroid.DS_TAG, "DetailActivity: Receive task detail message.");
      } catch (Exception ex) {
        /*DO NOTHING*/
      }

      TaskDetail details = (TaskDetail) msgP.obj;
      if (!task.isTorrent && !task.isNZB && files != null) {
        files.updateEmptyValues(getString(R.string.empty_file_list_wrong_type), false);
      }
      if (!task.status.equals(details.status)
          && files != null
          && (task.isTorrent || task.isNZB)) {
        if (details != null
            && details.status != null
            && details.status.equals(TaskStatus.TASK_DOWNLOADING.name())) {
          files.updateEmptyValues(getString(R.string.empty_list_loading), true);
          app.executeAsynchronousAction(main, new GetFilesAction(task), false);
        } else {
          files.updateEmptyValues(getString(R.string.empty_file_list), false);
          files.resetList();
        }
      }
      task.status = details.status;
      task.isTorrent = details.isTorrent;
      task.isNZB = details.isNZB;
      if (main != null) {
        main.genAdapter.updateDetails(buildGeneralDetails(details));
        ((DetailTransfer) mAdapter.getItem(TRANSFER_ITEM))
            .transAdapter.updateDetails(buildTransferDetails(details));
      }
      getIntent().putExtra("com.bigpupdev.synodroid.ds.Details", task);
      destination = details.destination;
      setStatus(details.getStatus());
      updateActionBarTitle(details.fileName);
      menuHelperInvalidate();
      break;
    case ResponseHandler.MSG_ERROR:
      try {
        if (app.DEBUG) Log.w(Synodroid.DS_TAG, "DetailActivity: Receive error message.");
      } catch (Exception ex) {
        /*DO NOTHING*/
      }

      SynoServer server = ((Synodroid) getApplication()).getServer();
      if (server != null) server.setLastError((String) msgP.obj);
      android.view.View.OnClickListener ocl =
          new android.view.View.OnClickListener() {
            @Override
            public void onClick(View v) {
              Crouton.cancelAllCroutons();
              DetailActivity.this.finish();
            }
          };
      Crouton.makeText(
              DetailActivity.this,
              server.getLastError() + "\n\n" + getText(R.string.click_dismiss),
              Synodroid.CROUTON_ERROR)
          .setOnClickListener(ocl)
          .show();
      break;
    case ResponseHandler.MSG_ORIGINAL_FILE_RETRIEVED:
      try {
        if (app.DEBUG)
          Log.v(Synodroid.DS_TAG, "DetailActivity: Receive original file retreived message.");
      } catch (Exception ex) {
        /*DO NOTHING*/
      }

      OriginalFile oriFile = (OriginalFile) msgP.obj;
      File path = Environment.getExternalStorageDirectory();
      path = new File(path, "download");
      File file = new File(path, oriFile.fileName);
      try {
        // Make sure the Pictures directory exists.
        path.mkdirs();
        StringBuffer rawData = oriFile.rawData;
        OutputStream os = new FileOutputStream(file);
        os.write(rawData.toString().getBytes());
        os.close();
        Crouton.makeText(
                DetailActivity.this,
                getString(R.string.action_download_original_saved),
                Synodroid.CROUTON_INFO)
            .show();
      } catch (Exception e) {
        // Unable to create file, likely because external storage is
        // not currently mounted.
        try {
          if (((Synodroid) getApplication()).DEBUG)
            Log.w(Synodroid.DS_TAG, "Error writing " + file + " to SDCard.", e);
        } catch (Exception ex) {
          /*DO NOTHING*/
        }
        Crouton.makeText(
                DetailActivity.this,
                getString(R.string.action_download_original_failed),
                Synodroid.CROUTON_ALERT)
            .show();
      }
      break;
    default:
      try {
        if (app.DEBUG)
          Log.w(Synodroid.DS_TAG, "DetailActivity: Ignored message ID = " + msgP.what);
      } catch (Exception ex) {
        /*DO NOTHING*/
      }
  }
}

