@Override
public boolean onOptionsItemSelected(MenuItem item) {
  switch (item.getItemId()) {
    case R.id.menu_exit:
      AlertMessage.createPositiveNegativeDialog(
              BaseActivity.this,
              false,
              getResources().getDrawable(R.drawable.warningsign),
              getResources().getString(R.string.exitAppTitle),
              getResources().getString(R.string.exitAppMessage),
              getResources().getString(R.string.yes),
              getResources().getString(R.string.no),
              new DialogInterface.OnClickListener() {
                @Override
                public void onClick(DialogInterface dialog, int which) {
                  if (ApplicationManager.rootEntitySelectionActivity != null) {
                    ApplicationManager.rootEntitySelectionActivity.finish();
                  }
                  if (ApplicationManager.recordSelectionActivity != null) {
                    ApplicationManager.recordSelectionActivity.finish();
                  }
                  if (ApplicationManager.formScreenActivityList != null) {
                    for (Activity formScreenActivity :
                        ApplicationManager.formScreenActivityList) {
                      formScreenActivity.finish();
                    }
                  }
                  if (ApplicationManager.formSelectionActivity != null) {
                    ApplicationManager.formSelectionActivity.finish();
                  }
                  ApplicationManager.mainActivity.finish();
                }
              },
              new DialogInterface.OnClickListener() {
                @Override
                public void onClick(DialogInterface dialog, int which) {}
              },
              null)
          .show();
      return true;
    case R.id.menu_save:
      CollectSurvey collectSurveySave = (CollectSurvey) ApplicationManager.getSurvey();
      DataManager dataManagerSave =
          new DataManager(
              collectSurveySave,
              collectSurveySave.getSchema().getRootEntityDefinitions().get(0).getName(),
              ApplicationManager.getLoggedInUser());
      boolean isSuccess = dataManagerSave.saveRecord(this);
      if (isSuccess) {
        AlertMessage.createPositiveDialog(
                BaseActivity.this,
                true,
                null,
                getResources().getString(R.string.savingDataTitle),
                getResources().getString(R.string.savingDataSuccessMessage),
                getResources().getString(R.string.okay),
                new DialogInterface.OnClickListener() {
                  @Override
                  public void onClick(DialogInterface dialog, int which) {}
                },
                null)
            .show();
      } else {
        AlertMessage.createPositiveDialog(
                BaseActivity.this,
                true,
                null,
                getResources().getString(R.string.savingDataTitle),
                getResources().getString(R.string.savingDataFailureMessage),
                getResources().getString(R.string.okay),
                new DialogInterface.OnClickListener() {
                  @Override
                  public void onClick(DialogInterface dialog, int which) {}
                },
                null)
            .show();
      }
      return true;
    case R.id.menu_export:
      CollectSurvey collectSurveyExport = (CollectSurvey) ApplicationManager.getSurvey();
      DataManager dataManagerExport =
          new DataManager(
              collectSurveyExport,
              collectSurveyExport.getSchema().getRootEntityDefinitions().get(0).getName(),
              ApplicationManager.getLoggedInUser());
      dataManagerExport.saveRecordToXml(
          ApplicationManager.currentRecord,
          Environment.getExternalStorageDirectory().toString()
              + getResources().getString(R.string.exported_data_folder));
      return true;
    case R.id.menu_upload:
      startActivity(new Intent(BaseActivity.this, UploadActivity.class));
      return true;
    case R.id.menu_download:
      startActivity(new Intent(BaseActivity.this, DownloadActivity.class));
      return true;
    case R.id.menu_settings:
      startActivity(new Intent(BaseActivity.this, SettingsScreen.class));
      return true;
    case R.id.menu_about:
      String versionName;
      try {
        versionName =
            this.getPackageManager().getPackageInfo(this.getPackageName(), 0).versionName;
      } catch (NameNotFoundException e) {
        versionName = "";
      }
      AlertMessage.createPositiveDialog(
              BaseActivity.this,
              true,
              null,
              getResources().getString(R.string.aboutTabTitle),
              getResources().getString(R.string.lblApplicationName)
                  + getResources().getString(R.string.app_name)
                  + "\n"
                  + getResources().getString(R.string.lblProgramVersionName)
                  + versionName
                  + "\n"
                  + getResources().getString(R.string.lblFormVersionName)
                  + ApplicationManager.getSurvey().getProjectName(null)
                  + " "
                  + ApplicationManager.getSurvey()
                      .getVersions()
                      .get(ApplicationManager.getSurvey().getVersions().size() - 1)
                      .getName(),
              getResources().getString(R.string.okay),
              new DialogInterface.OnClickListener() {
                @Override
                public void onClick(DialogInterface dialog, int which) {}
              },
              null)
          .show();
      return true;

    default:
      return super.onOptionsItemSelected(item);
  }
}

/**
 * Open a copy file from the root part of the phone. The file is copied to
 * /mnt/sccard/aSQLiteManager (this method of opening the file did not work with catalogs
 * containing "."
 *
 * @param dbPath Path to the file
 * @param file The name of the file
 */
private void openRootDatabase(String dbPath, String file) {
  testTempDir();
  // Does not work with "." in temp path (.aSQLiteManager)
  String tmpPath = Environment.getExternalStorageDirectory().toString() + tempDir;
  AShellInterface shc = new AShellInterface(suShell, delay, _cont);
  String cmd = "cat " + dbPath + "/" + file + " > " + tmpPath + "/" + file + ".bck";
  shc.runCommand(cmd);
  cmd = "cat " + dbPath + "/" + file + " > " + tmpPath + "/" + file;
  shc.runCommand(cmd);
  editingDatabase = true;
  databasePath = dbPath + "/" + file;
  databaseTemp = tmpPath + "/" + file;
  try {
    Thread.sleep(delay);
  } catch (InterruptedException e) {
    e.printStackTrace();
  }
  openDatabase(tmpPath, file);
}

