/**
 * Exports the financial calculation to a PDF
 *
 * <p>TODO: talk to customer to check which information he needs in the report
 *
 * @param view
 */
public void exportPDF() {

  String FILE = "/SolarPowerReport.pdf";
  try {
    Document document = new Document();
    boolean mExternalStorageAvailable = false;
    boolean mExternalStorageWriteable = false;
    String state = Environment.getExternalStorageState();
    if (Environment.MEDIA_MOUNTED.equals(state)) {
      // We can read and write the media
      mExternalStorageAvailable = mExternalStorageWriteable = true;
    } else if (Environment.MEDIA_MOUNTED_READ_ONLY.equals(state)) {
      // We can only read the media
      mExternalStorageAvailable = true;
      mExternalStorageWriteable = false;
    } else {
      // Something else is wrong. It may be one of many other states, but all we need
      // to know is we can neither read nor write
      mExternalStorageAvailable = mExternalStorageWriteable = false;
    }
    String file = null;
    if (mExternalStorageWriteable) {
      file = Environment.getExternalStorageDirectory().getPath() + FILE;
    }

    PdfWriter.getInstance(document, new FileOutputStream(file));
    document.open();
    Paragraph p = new Paragraph("Calculation Report");
    document.add(p);
    p = new Paragraph(" ");
    document.add(p);
    PdfPTable table = new PdfPTable(3);
    PdfPCell c1 = new PdfPCell(new Phrase("Year"));
    c1.setHorizontalAlignment(Element.ALIGN_CENTER);
    table.addCell(c1);

    c1 = new PdfPCell(new Phrase("Cumulative Savings ($)"));
    c1.setHorizontalAlignment(Element.ALIGN_CENTER);
    table.addCell(c1);

    c1 = new PdfPCell(new Phrase("ROI"));
    c1.setHorizontalAlignment(Element.ALIGN_CENTER);
    table.addCell(c1);
    table.setHeaderRows(1);
    for (Calculation curCalculation : calculatorList.get(0).getCalculations()) {
      table.addCell("" + df.format(curCalculation.getYear() + 1));
      table.addCell("" + df.format(curCalculation.getCumulativeSaving()));
      table.addCell(
          ""
              + df.format(
                  curCalculation.getCumulativeSaving()
                      / calculatorList.get(0).getEquipment().getCost()));
    }
    document.add(table);

    document.close();

    showDialog(DIALOG_GENERATE_PDF);
  } catch (Exception e) {
    e.printStackTrace();
  }
}

/**
 * Enable caching to the phone's internal storage or SD card.
 *
 * @param context the current context
 * @param storageDevice where to store the cached files, either {@link #DISK_CACHE_INTERNAL} or
 *     {@link #DISK_CACHE_SDCARD})
 * @return
 */
public boolean enableDiskCache(Context context, int storageDevice) {
  Context appContext = context.getApplicationContext();

  String rootDir = null;
  if (storageDevice == DISK_CACHE_SDCARD
      && Environment.MEDIA_MOUNTED.equals(Environment.getExternalStorageState())) {
    // SD-card available
    rootDir =
        Environment.getExternalStorageDirectory().getAbsolutePath()
            + "/Android/data/"
            + appContext.getPackageName()
            + "/cache";
  } else {
    File internalCacheDir = appContext.getCacheDir();
    // apparently on some configurations this can come back as null
    if (internalCacheDir == null) {
      return (mIsDiskCacheEnabled = false);
    }
    rootDir = internalCacheDir.getAbsolutePath();
  }

  setRootDir(rootDir);

  File outFile = new File(mDiskCacheDirectory);
  if (outFile.mkdirs()) {
    File nomedia = new File(mDiskCacheDirectory, ".nomedia");
    try {
      nomedia.createNewFile();
    } catch (IOException e) {
      Log.e(getClass().getName(), "Failed creating .nomedia file");
    }
  }

  mIsDiskCacheEnabled = outFile.exists();

  if (!mIsDiskCacheEnabled) {
    Log.w(getClass().getName(), "Failed creating disk cache directory " + mDiskCacheDirectory);
  } else {
    Log.d(getClass().getName(), "Enabled write-through to " + mDiskCacheDirectory);
    sanitizeDiskCache();
  }

  return mIsDiskCacheEnabled;
}

