boolean mountMedia() {
  // We can't mount emulated storage.
  if (Environment.isExternalStorageEmulated()) {
    return true;
  }

  if (checkMediaState(Environment.MEDIA_MOUNTED)) {
    return true;
  }

  final String path = Environment.getExternalStorageDirectory().toString();
  StorageListener observer = new StorageListener(Environment.MEDIA_MOUNTED);
  StorageManager sm = (StorageManager) mContext.getSystemService(Context.STORAGE_SERVICE);
  sm.registerListener(observer);
  try {
    // Wait on observer
    synchronized (observer) {
      int ret = getMs().mountVolume(path);
      if (ret != StorageResultCode.OperationSucceeded) {
        throw new Exception("Could not mount the media");
      }
      long waitTime = 0;
      while ((!observer.isDone()) && (waitTime < MAX_WAIT_TIME)) {
        observer.wait(WAIT_TIME_INCR);
        waitTime += WAIT_TIME_INCR;
      }
      if (!observer.isDone()) {
        throw new Exception("Timed out waiting for unmount media notification");
      }
      return true;
    }
  } catch (Exception e) {
    Log.e(TAG, "Exception : " + e);
    return false;
  } finally {
    sm.unregisterListener(observer);
  }
}

@Override
public void onClick(View v) {
  // store our image in a temp spot
  String state = Environment.getExternalStorageState();
  if (Environment.MEDIA_MOUNTED.equals(state)) {
    // application directory, per Android Data Storage guidelines
    final String APP_DIRECTORY = "/Android/data/cc.hughes.droidchatty/files/";
    File app_dir = new File(Environment.getExternalStorageDirectory(), APP_DIRECTORY);

    // make sure the directory exists
    if (!app_dir.exists()) {
      if (!app_dir.mkdirs()) {
        ErrorDialog.display(
            ComposePostView.this, "Error", "Could not create application directory.");
        return;
      }
    }

    // our temp file for taking a picture, delete if we already have one
    File file = new File(app_dir, "droidchatty.jpg");
    if (file.exists()) file.delete();

    _cameraImageLocation = Uri.fromFile(file);

    // start the camera
    Intent i = new Intent(MediaStore.ACTION_IMAGE_CAPTURE);
    i.putExtra(MediaStore.EXTRA_OUTPUT, _cameraImageLocation);
    startActivityForResult(i, TAKE_PICTURE);
  } else if (Environment.MEDIA_MOUNTED_READ_ONLY.equals(state)) {
    ErrorDialog.display(
        ComposePostView.this, "Error", "External storage is mounted as read only.");
  } else {
    ErrorDialog.display(
        ComposePostView.this, "Error", "External storage is in an unknown state.");
