protected long getFreeDiskSpace() {
  /*
   * gets the available SD card free space or returns -1 if the SD card is not mounted.
   */
  String status = Environment.getExternalStorageState();
  long freeSpace = 0;
  if (status.equals(Environment.MEDIA_MOUNTED)) {
    try {
      File path = Environment.getExternalStorageDirectory();
      StatFs stat = new StatFs(path.getPath());
      long blockSize = stat.getBlockSize();
      long availableBlocks = stat.getAvailableBlocks();
      freeSpace = availableBlocks * blockSize / 1024;
    } catch (Exception e) {
      e.printStackTrace();
    }
  } else {
    return -1;
  }
  return (freeSpace);
}

private boolean unmountMedia() {
  // We can't unmount emulated storage.
  if (Environment.isExternalStorageEmulated()) {
    return true;
  }

  if (checkMediaState(Environment.MEDIA_UNMOUNTED)) {
    return true;
  }

  final String path = Environment.getExternalStorageDirectory().getPath();
  StorageListener observer = new StorageListener(Environment.MEDIA_UNMOUNTED);
  StorageManager sm = (StorageManager) mContext.getSystemService(Context.STORAGE_SERVICE);
  sm.registerListener(observer);
  try {
    // Wait on observer
    synchronized (observer) {
      getMs().unmountVolume(path, true, false);
      long waitTime = 0;
      while ((!observer.isDone()) && (waitTime < MAX_WAIT_TIME)) {
        observer.wait(WAIT_TIME_INCR);
        waitTime += WAIT_TIME_INCR;
      }
      if (!observer.isDone()) {
        throw new Exception("Timed out waiting for unmount media notification");
      }
      return true;
    }
  } catch (Exception e) {
    Log.e(TAG, "Exception : " + e);
    return false;
  } finally {
    sm.unregisterListener(observer);
  }
}

