/**
 * Stops the current view hierarchy tracing. This method closes the file <code>
 * /EXTERNAL/view-hierarchy/PREFIX.traces</code>.
 *
 * <p>Calling this method creates the file <code>/EXTERNAL/view-hierarchy/PREFIX.tree</code>
 * containing the view hierarchy of the view supplied to {@link #startHierarchyTracing(String,
 * View)}.
 *
 * <p>This method will return immediately if TRACE_HIERARCHY is false.
 *
 * @see #startHierarchyTracing(String, View)
 * @see #trace(View, android.view.ViewDebug.HierarchyTraceType)
 */
public static void stopHierarchyTracing() {
  // noinspection PointlessBooleanExpression,ConstantConditions
  if (!TRACE_HIERARCHY) {
    return;
  }

  if (sHierarhcyRoot == null || sHierarchyTraces == null) {
    throw new IllegalStateException(
        "You must call startHierarchyTracing() before" + " stopHierarchyTracing()!");
  }

  try {
    sHierarchyTraces.close();
  } catch (IOException e) {
    Log.e("View", "Could not write view traces");
  }
  sHierarchyTraces = null;

  File hierarchyDump = new File(Environment.getExternalStorageDirectory(), "view-hierarchy/");
  // noinspection ResultOfMethodCallIgnored
  hierarchyDump.mkdirs();
  hierarchyDump = new File(hierarchyDump, sHierarchyTracePrefix + ".tree");

  BufferedWriter out;
  try {
    out = new BufferedWriter(new FileWriter(hierarchyDump), 8 * 1024);
  } catch (IOException e) {
    Log.e("View", "Could not dump view hierarchy");
    return;
  }

  View view = sHierarhcyRoot.getView();
  if (view instanceof ViewGroup) {
    ViewGroup group = (ViewGroup) view;
    dumpViewHierarchy(group, out, 0);
    try {
      out.close();
    } catch (IOException e) {
      Log.e("View", "Could not dump view hierarchy");
    }
  }

  sHierarhcyRoot = null;
}

