public void moveFile(String file) {
  /* this is a hack to save the file as the specified name */
  File f = new File(this.tempFile);

  if (!file.startsWith("/")) {
    if (Environment.getExternalStorageState().equals(Environment.MEDIA_MOUNTED)) {
      file = Environment.getExternalStorageDirectory().getAbsolutePath() + File.separator + file;
    } else {
      file = "/data/data/" + handler.cordova.getActivity().getPackageName() + "/cache/" + file;
    }
  }

  String logMsg = "renaming " + this.tempFile + " to " + file;
  Log.d(LOG_TAG, logMsg);
  if (!f.renameTo(new File(file))) Log.e(LOG_TAG, "FAILED " + logMsg);
}

public static List<LocationInfo> deserializeFromSDcardJsonStatic(String filename) {
  String path = Environment.getExternalStorageDirectory() + "/" + filename;

  String content = null;
  try {
    content = new Scanner(new File(path)).useDelimiter("\\Z").next();
  } catch (FileNotFoundException e) {
    e.printStackTrace();
  }

  Gson gson = new Gson();
  Type collectionType = new TypeToken<List<LocationInfo>>() {}.getType();
  return gson.fromJson(content, collectionType);
}

@Override
public void onSharedPreferenceChanged(SharedPreferences autocompletion, String key) {
  Log.d(TAG, "onSharedPreferenceChanged");

  // prepare database queries
  if (db != null) db.close();
  db = new DatabaseHelper(getContext());

  StringBuilder builder = new StringBuilder();
  String dir = Environment.getExternalStorageDirectory().getPath() + "/teleporter/";
  builder.append(String.format(SQL, "city", "address", "myplaces"));
  for (String file : autocompletion.getAll().keySet()) {
    if (autocompletion.getBoolean(file, false)) {
      String path = dir + file;
      if (!new File(path).exists()) {
        Log.d(TAG, file + " doesn't exist");
        continue;
      }
      String name = file.split("\\.")[0];
      db.getWritableDatabase()
          .execSQL("ATTACH DATABASE '" + path + "' AS '" + name.replace("-", "_") + "';");
      builder.append("UNION ALL ");
      builder.append(
          String.format(
              SQL,
              "'" + name.substring(name.indexOf("_") + 1) + "'",
              "name",
              name.replace("-", "_") + ".places"));
      // Log.d(TAG, name);
    }
  }
  builder.append(" LIMIT 42");
  sql = builder.toString();
  // Log.d(TAG, sql);
}

