private void sendDebugLogs(String logs) {
  File out_path = Environment.getExternalStorageDirectory();
  out_path = new File(out_path, "Android/data/com.bigpupdev.synodroid/cache/");
  File file = new File(out_path, "debug_log.txt");
  try {
    // Make sure the Pictures directory exists.
    out_path.mkdirs();
    OutputStream os = new FileOutputStream(file);
    os.write(logs.getBytes());
    os.close();
    try {
      final Intent emailIntent = new Intent(android.content.Intent.ACTION_SEND);
      emailIntent.putExtra(
          android.content.Intent.EXTRA_EMAIL, new String[] {"synodroid@gmail.com"});
      emailIntent.putExtra(
          android.content.Intent.EXTRA_SUBJECT, "Synodroid Professional - Debug log");
      emailIntent.setType("plain/text");
      emailIntent.putExtra(Intent.EXTRA_STREAM, Uri.parse("file://" + file.getAbsolutePath()));
      startActivity(emailIntent);
    } catch (Exception e) {
      AlertDialog.Builder builder = new AlertDialog.Builder(this);
      builder.setMessage(R.string.err_noemail);
      builder
          .setTitle(getString(R.string.connect_error_title))
          .setCancelable(false)
          .setPositiveButton(
              "OK",
              new DialogInterface.OnClickListener() {
                public void onClick(DialogInterface dialog, int id) {
                  dialog.cancel();
                }
              });
      AlertDialog errorDialog = builder.create();
      try {
        errorDialog.show();
      } catch (BadTokenException ex) {
        // Unable to show dialog probably because intent has been closed. Ignoring...
      }
    }
  } catch (Exception e) {
    // Unable to create file, likely because external storage is
    // not currently mounted.
    try {
      Log.e(Synodroid.DS_TAG, "Error writing " + file + " to SDCard.", e);
    } catch (Exception ex) {
      /*DO NOTHING*/
    }
  }
}

public void downloadData(String url, Button button) {
  try {

    URL URL = new URL(url);
    URLConnection conn = URL.openConnection();
    conn.connect();

    InputStream is = URL.openStream();

    File testDirectory = new File(Environment.getExternalStorageDirectory() + "/ARC/");

    FileOutputStream fos = new FileOutputStream(testDirectory + "/" + button.getTag());

    byte data[] = new byte[1024];

    int count = 0;

    while ((count = is.read(data)) != -1) {
      fos.write(data, 0, count);
    }

    is.close();
    fos.close();

  } catch (Exception e) {
    e.printStackTrace();
  }
}
