/**
 * Authentication mechanism on the source side. It first sends an AuthenticationMessage. Then it
 * waits for the AuthenticationAckMessage of the target. If AuthenticationAckMessage was positive
 * it sends AuthenticationAckMessage to the target. If authentication is successful all hole
 * punching threads get stopped.
 */
@Override
public boolean authenticate(
    final Socket toBeAuthenticated,
    final Set<CancelableTask> relatedHolePunchingTasks,
    final CancelableTask ownTask,
    final Object sharedLock)
    throws IOException {
  boolean result = false;
  logger.info("Trying to authenticate socket: {}", toBeAuthenticated); // $NON-NLS-1$
  final MessageWriter messageWriter = new MessageWriter(toBeAuthenticated.getOutputStream());
  final MessageReader messageReader = this.hpUtil.getCustomHolePunchingMessageReader();
  // sending authentication message
  final Message authenticationMessage =
      MessageStaticFactory.newSTUNMessageInstance(
          STUNMessageClass.REQUEST, STUNMessageMethod.AUTHENTICATE);
  authenticationMessage.addAttribute(new Token(this.authentikationToken));
  logger.info("Sending AuthenticationMessage: {}", authenticationMessage); // $NON-NLS-1$
  messageWriter.writeMessage(authenticationMessage);
  // receiving authentication acknowledgment message
  final Message reveivedAckMessage =
      messageReader.readSTUNMessage(toBeAuthenticated.getInputStream());
  if (this.hpUtil.isAuthenticationAcknowledgmentMessage(reveivedAckMessage)) {
    synchronized (sharedLock) {
      // sending second authentication acknowledgment message
      final Message secondAckMessage = reveivedAckMessage.buildSuccessResponse();
      messageWriter.writeMessage(secondAckMessage);
      logger.debug("Authentication successfull, stopping hole punching threads"); // $NON-NLS-1$
      this.hpUtil.stopCancableTasksExceptOwnTask(relatedHolePunchingTasks, ownTask);
      result = true;
    }
  }
  logger.debug("Authentication failed"); // $NON-NLS-1$
  return result;
}

/**
 * Calls a method on a remote object. It sends a POST request to the server (via an http proxy
 * server if needed).
 *
 * <p>This method is called by only proxy objects.
 */
public Object call(int objectid, int methodid, Object[] args) throws RemoteException {
  boolean result;
  Object rvalue;
  String errmsg;

  try {
    /* This method establishes a raw tcp connection for sending
     * a POST message. Thus the object cannot communicate a
     * remote object beyond a fire wall. To avoid this problem,
     * the connection should be established with a mechanism
     * collaborating a proxy server. Unfortunately, java.lang.URL
     * does not seem to provide such a mechanism.
     *
     * You might think that using HttpURLConnection is a better
     * way than constructing a raw tcp connection. Unfortunately,
     * URL.openConnection() does not return an HttpURLConnection
     * object in Netscape's JVM. It returns a
     * netscape.net.URLConnection object.
     *
     * lookupObject() has the same problem.
     */
    Socket sock = new Socket(servername, port);
    OutputStream out = new BufferedOutputStream(sock.getOutputStream());
    out.write(rmiCommand);
    out.write(endofline);
    out.write(endofline);

    ObjectOutputStream dout = new ObjectOutputStream(out);
    dout.writeInt(objectid);
    dout.writeInt(methodid);
    writeParameters(dout, args);
    dout.flush();

    InputStream ins = new BufferedInputStream(sock.getInputStream());
    skipHeader(ins);
    ObjectInputStream din = new ObjectInputStream(ins);
    result = din.readBoolean();
    rvalue = null;
    errmsg = null;
    if (result) rvalue = din.readObject();
    else errmsg = din.readUTF();

    din.close();
    dout.close();
    sock.close();

    if (rvalue instanceof RemoteRef) {
      RemoteRef ref = (RemoteRef) rvalue;
      rvalue = createProxy(ref.oid, ref.classname);
    }
  } catch (ClassNotFoundException e) {
    throw new RemoteException(e);
  } catch (IOException e) {
    throw new RemoteException(e);
  } catch (Exception e) {
    throw new RemoteException(e);
  }

  if (result) return rvalue;
  else throw new RemoteException(errmsg);
}

private void writeTCP(Socket s, byte[] out) throws IOException {
  if (Options.check("verbosemsg")) System.err.println(hexdump.dump("TCP write", out));
  OutputStream outStream = s.getOutputStream();
  byte[] lengthArray = new byte[2];
  lengthArray[0] = (byte) (out.length >>> 8);
  lengthArray[1] = (byte) (out.length & 0xFF);
  outStream.write(lengthArray);
  outStream.write(out);
}

public static void handleCommand(String command) {
  if (command.equals("quit")) {
    try {
      mySocket.close();
      System.exit(0);
    } catch (IOException ex) {
      System.out.println("ex = " + ex);
    }
  } else if (command.startsWith("connect")) {
    try {

      String[] split = command.split(" ");

      if (split.length == 3) {

        mySocket = new Socket(InetAddress.getByName(split[1]), Integer.parseInt(split[2]));
      } else {
        mySocket = new Socket(InetAddress.getLocalHost(), 4555);
      }

      System.out.println("Connection Established");

      socketin = new BufferedReader(new InputStreamReader(mySocket.getInputStream()));
      socketout = new PrintWriter(new OutputStreamWriter(mySocket.getOutputStream()));

      DeviceServerMessage newMessage = new DeviceServerMessage();

      System.out.print("Device Id: ");
      String devId = br.readLine();

      newMessage.messageType = DeviceServerMessage.DEVICE_CONFIG;
      newMessage.data = devId + ":KABOUTER_TEST_DEVICE";

      String jsonString = JSONUtils.ToJSON(newMessage);

      System.out.println("Sending Config Message");

      System.out.println("jsonString = " + jsonString);

      mySocket.getOutputStream().write(jsonString.getBytes());

    } catch (Exception ex) {
      System.out.println("ex = " + ex);
    }
  } else if (command.startsWith("changeio")) {
    try {
      String[] split = command.split(" ");
      int newValue = Integer.parseInt(split[1]);

      Integer[] newIo = new Integer[] {newValue, newValue};

      DeviceServerMessage newMessage = new DeviceServerMessage();
      newMessage.data = JSONUtils.ToJSON(newIo);
      newMessage.messageType = DeviceServerMessage.IO_STATE_UPDATE;

      String jsonString = JSONUtils.ToJSON(newMessage);

      System.out.println("Sending IO Change Message");

      mySocket.getOutputStream().write(jsonString.getBytes());

      String newIOMessage = socketin.readLine();

      System.out.println("newIOMessage = " + newIOMessage);
      ServerDeviceMessage FromJSON = JSONUtils.FromJSON(newIOMessage, ServerDeviceMessage.class);
      Integer[] newIOStates = JSONUtils.FromJSON(FromJSON.data, Integer[].class);

      System.out.println("newIOStates = " + newIOStates[0] + ", " + newIOStates[1]);

    } catch (IOException ex) {
      System.out.println("ex = " + ex);
    } catch (com.google.gson.JsonSyntaxException jex) {
      System.out.println("Exception: " + jex);
    }
  } else if (command.startsWith("generatepower")) {

    String[] split = command.split(" ");
    int newValue = Integer.parseInt(split[1]);

    myPowerGenerator = new PowerGenerator(newValue, mySocket);
    myPowerGenerator.start();

  } else if (command.startsWith("setbase")) {

    String[] split = command.split(" ");
    int newValue = Integer.parseInt(split[1]);

    myPowerGenerator.setBaseLevel(newValue);
  }
}

private GateTalk.Command sendRequest(GateTalk.Command cmd) throws Exception {
  Socket s = null;
  try {
    s = new Socket("127.0.0.1", MISTD_PORT);
    Packet pack = new Packet();
    pack.setPayload(cmd.toByteArray());
    pack.write(new BufferedOutputStream(s.getOutputStream()));
    pack.read(new BufferedInputStream(s.getInputStream()));
    return GateTalk.Command.parseFrom(pack.getPayload());
  } catch (Exception e) {
    throw e;
  } finally {
    if (s != null) {
      try {
        s.close();
      } catch (Exception e) {
      }
    }
  }
}

