public void sendRequestHeader(byte[] header) throws IOException, MessageFormatException {
  if (state == State.RESPONSE_CONTINUE) {
    throw new IllegalStateException(
        "Cannot start a new request when the "
            + "previous request content has not yet been sent");
  }
  if (state != State.CONNECTED && state != State.RESPONSE_CONTENT_READ) {
    throw new IllegalStateException(
        "Illegal state. Can't send request headers when state is " + state);
  }
  OutputStream os = new BufferedOutputStream(socket.getOutputStream());

  int resourceStart = -1;
  String method = null;
  for (int i = 0; i < header.length; i++) {
    if (method == null && Character.isWhitespace(header[i])) {
      method = AsciiString.create(header, 0, i - 1);
    }
    if (method != null && !Character.isWhitespace(header[i]) && resourceStart == -1) {
      resourceStart = i;
      break;
    }
    if (header[i] == '\r' || header[i] == '\n') {
      throw new MessageFormatException("Encountered CR or LF when parsing the URI!", header);
    }
  }
  expectResponseContent = !"HEAD".equals(method);
  if (!direct) {
    if (resourceStart > 0) {
      os.write(header, 0, resourceStart);
      os.write(("http://" + target.getHostName() + ":" + target.getPort()).getBytes());
      os.write(header, resourceStart, header.length - resourceStart);
    } else {
      throw new MessageFormatException("Couldn't parse the URI!", header);
    }
  } else {
    os.write(header);
  }
  os.flush();
  state = State.REQUEST_HEADER_SENT;
  requestSubmissionTime = System.currentTimeMillis();
}

