    String line;
    while ((line = bin.readLine()) != null) System.out.println(line);

    // Need to scrape a value from the last line?
    returnValue = "1";
    return returnValue;
  } catch (Exception ex) {
    System.err.println("Error communicating with server: " + ex);
    return returnValue;
  }
}

private void httpProxyEngineGoodRequestTests(AbstractTCPProxyEngine engine) throws Exception {

  final AcceptAndEcho echoer = new AcceptAndEcho();

  final Socket clientSocket =
      new Socket(engine.getListenEndPoint().getHost(), engine.getListenEndPoint().getPort());

  final PrintWriter clientWriter = new PrintWriter(clientSocket.getOutputStream(), true);

  final String message0 =
      "GET http://"
          + echoer.getEndPoint()
          + "/foo HTTP/1.1\r\n"
          + "foo: bah\r\n"
          + "\r\n"
          + "A \u00e0 message";
  clientWriter.print(message0);
  clientWriter.flush();

  final String response0 = readResponse(clientSocket, "message$");

  AssertUtilities.assertStartsWith(response0, "GET /foo HTTP/1.1\r\n");
  AssertUtilities.assertContainsHeader(response0, "foo", "bah");
  AssertUtilities.assertContainsPattern(response0, "\r\n\r\nA \u00e0 message$");

  verify(m_requestFilter).connectionOpened(m_connectionDetailsCaptor.capture());

  final ConnectionDetails requestConnectionDetails = m_connectionDetailsCaptor.getValue();
  assertEquals(echoer.getEndPoint(), requestConnectionDetails.getRemoteEndPoint());

  verify(m_requestFilter)
      .handle(same(requestConnectionDetails), isA(new byte[0].getClass()), isA(Integer.class));

  verify(m_responseFilter).connectionOpened(m_connectionDetailsCaptor.capture());

  final ConnectionDetails responseConnectionDetails = m_connectionDetailsCaptor.getValue();
  assertEquals(requestConnectionDetails.getOtherEnd(), responseConnectionDetails);

  verify(m_responseFilter)
      .handle(same(responseConnectionDetails), isA(new byte[0].getClass()), isA(Integer.class));

  final String message1Headers =
      "POST http://"
          + echoer.getEndPoint()
          + "/blah?x=123&y=99 HTTP/1.0\r\n"
          + "\r\n"
          + "Another message";
  clientWriter.print(message1Headers);
  clientWriter.flush();

  final String message1PostBody = "Some data, lah 0x810x820x830x84 dah";
  clientWriter.print(message1PostBody);
  clientWriter.flush();

  final String response1 = readResponse(clientSocket, "dah$");

  AssertUtilities.assertStartsWith(response1, "POST /blah?x=123&y=99 HTTP/1.0\r\n");
  AssertUtilities.assertContainsPattern(
      response1, "\r\n\r\nAnother message" + message1PostBody + "$");

  // Do again, but force engine to handle body in two parts.
  clientWriter.print(message1Headers);
  clientWriter.flush();

  final String response2a = readResponse(clientSocket, "Another message$");

  AssertUtilities.assertStartsWith(response2a, "POST /blah?x=123&y=99 HTTP/1.0\r\n");

  clientWriter.print(message1PostBody);
  clientWriter.flush();

  final String response2b = readResponse(clientSocket, "dah$");
  assertNotNull(response2b);

  clientSocket.close();

  waitUntilAllStreamThreadsStopped(engine);

  // handle() must have been called at least 3 further times, but can be
  // called more.
  verify(m_requestFilter, atLeast(4))
      .handle(same(requestConnectionDetails), isA(new byte[0].getClass()), isA(Integer.class));

  verify(m_responseFilter, atLeast(4))
      .handle(same(responseConnectionDetails), isA(new byte[0].getClass()), isA(Integer.class));

  verify(m_requestFilter).connectionClosed(requestConnectionDetails);
  verify(m_responseFilter).connectionClosed(responseConnectionDetails);

  verifyNoMoreInteractions(m_requestFilter, m_responseFilter);
}

