      out.close();
    }
    if (s != null) {
      s.close();
    }
  }
}

public Message sendAXFR(Message query) throws IOException {
  byte[] out, in;
  Socket s;
  int inLength;
  DataInputStream dataIn;
  int soacount = 0;
  Message response;
  boolean first = true;

  try {
    s = new Socket(addr, port);
  } catch (SocketException e) {
    System.out.println(e);
    return null;
  }

  if (tsig != null) tsig.apply(query);

  out = query.toWire();
  new DataOutputStream(s.getOutputStream()).writeShort(out.length);
  s.getOutputStream().write(out);
  s.setSoTimeout(timeoutValue);

  response = new Message();
  response.getHeader().setID(query.getHeader().getID());
  if (tsig != null) tsig.verifyAXFRStart();
  while (soacount < 2) {
    try {
      dataIn = new DataInputStream(s.getInputStream());
      inLength = dataIn.readUnsignedShort();
      in = new byte[inLength];
      dataIn.readFully(in);
    } catch (InterruptedIOException e) {
      s.close();
      System.out.println(";; No response");
      return null;
    }
    Message m = new Message(in);
    if (m.getHeader().getCount(Section.QUESTION) != 0
        || m.getHeader().getCount(Section.ANSWER) <= 0
        || m.getHeader().getCount(Section.AUTHORITY) != 0) {
      StringBuffer sb = new StringBuffer();
      sb.append("Invalid AXFR: ");
      for (int i = 0; i < 4; i++) {
        Enumeration e = m.getSection(i);
        System.out.println("--");
        while (e.hasMoreElements()) {
          Record r;
          r = (Record) e.nextElement();
          System.out.println(r);
        }
        System.out.println();
      }
      System.out.println(sb.toString());
      s.close();
      return null;
    }
    for (int i = 1; i < 4; i++) {
      Enumeration e = m.getSection(i);
      while (e.hasMoreElements()) {
        Record r = (Record) e.nextElement();
        response.addRecord(i, r);
        if (r instanceof SOARecord) soacount++;
      }
    }
    if (tsig != null) {
      boolean required = (soacount > 1 || first);
      boolean ok = tsig.verifyAXFR(m, in, query.getTSIG(), required, first);
      System.out.println("TSIG verify: " + ok);
    }
    first = false;
  }

  s.close();
  return response;
}

