public void receiveMessage(String symbol, Object... args) {
  if (symbol.equals("connect")
      && (args.length == 2)
      && args[0].getClass().equals(String.class)
      && args[1].getClass().equals(Float.class)) {
    if (port != 0) {
      Log.d(TAG, "already connected");
      return;
    }
    try {
      host = InetAddress.getByName(args[0].toString());
      port = (int) (float) (Float) args[1];
      ClientThread = new Thread(ClientRun);
      ClientThread.start();
    } catch (UnknownHostException e) {
      // TODO Auto-generated catch block
      e.printStackTrace();
    }
  } else if (symbol.equals("disconnect")) {
    if (port == 0) return;
    if (connection == null) return;
    try {
      connection.close();
    } catch (IOException e) {
      // TODO Auto-generated catch block
      e.printStackTrace();
      port = 0;
    }

  } else if (symbol.equals("send")) {
    if (connection == null) return;
    if (!connection.isConnected()) return;
    OutputStream out;
    try {
      out = connection.getOutputStream();
    } catch (IOException e) {
      // TODO Auto-generated catch block
      e.printStackTrace();
      return;
    }
    try {
      for (Object o : args) {
        out.write(o.toString().getBytes());
        out.write(' ');
      }
      out.write(';');
      out.write('\n');

    } catch (IOException e) {
      // TODO Auto-generated catch block
      e.printStackTrace();
    }
  }
}

