private static boolean waitForServerDown(int port, long timeout) {
  long start = System.currentTimeMillis();
  while (true) {
    try {
      Socket sock = new Socket("localhost", port);
      try {
        OutputStream outstream = sock.getOutputStream();
        outstream.write("stat".getBytes());
        outstream.flush();
      } finally {
        sock.close();
      }
    } catch (IOException e) {
      return true;
    }

    if (System.currentTimeMillis() > start + timeout) {
      break;
    }
    try {
      Thread.sleep(250);
    } catch (InterruptedException e) {
      // ignore
    }
  }
  return false;
}

/**
 * Saves the given socket to the given recorder.
 *
 * @param curi the curi that owns the recorder
 * @param socket the socket whose streams to save
 * @param recorder the recorder to save them to
 * @throws IOException if a network or file error occurs
 * @throws InterruptedException if the thread is interrupted
 */
private void saveToRecorder(CrawlURI curi, Socket socket, Recorder recorder)
    throws IOException, InterruptedException {
  recorder.inputWrap(socket.getInputStream());
  recorder.outputWrap(socket.getOutputStream());
  recorder.markContentBegin();

  // Read the remote file/dir listing in its entirety.
  long softMax = 0;
  long hardMax = getMaxLengthBytes();
  long timeout = (long) getTimeoutSeconds() * 1000L;
  int maxRate = getMaxFetchKBSec();
  RecordingInputStream input = recorder.getRecordedInput();
  input.setLimits(hardMax, timeout, maxRate);
  input.readFullyOrUntil(softMax);
}

private String sendRequestGetCookie(int iServer, Map mTokens, HttpScript.Item i)
    throws IOException {
  Socket s = new Socket(HOSTS[iServer], PORTS[iServer]);
  // Again with the dodgy charset assumptions
  OutputStreamWriter osw = new OutputStreamWriter(s.getOutputStream());
  String sRequest = i.getRequest();
  sRequest = XML.replaceTokens(sRequest, "%%", mTokens);
  osw.write(sRequest + "\r\n");
  if (i.getData() != null) osw.write(i.getData());
  osw.flush();

  // Get input
  InputStream is = s.getInputStream();
  String sLine = "";
  int iContentLength = 0;
  String sCookie = null;
  boolean bFirst = true;
  while (true) {
    int iChar = is.read();
    if (iChar == -1) throw new IOException("Unexpected EOF in socket input");
    if (iChar == 10) {
      if (sLine.equals("")) break; // End of headers
      Matcher m = HttpScript.CONTENTLENGTH.matcher(sLine);
      if (m.matches()) iContentLength = Integer.parseInt(m.group(1));
      m = SETCOOKIE.matcher(sLine);
      if (m.matches()) sCookie = m.group(1);
      if (bFirst) {
        bFirst = false;
        Matcher mStatus = HttpScript.STATUSCODE.matcher(sLine);
        if (!mStatus.matches()) throw new IOException("Unexpected HTTP status format: " + sLine);
        if (Integer.parseInt(mStatus.group(1)) != i.getExpectedResponse()) {
          throw new IOException(
              "Unexpected response code: "
                  + sLine
                  + " (expecting "
                  + i.getExpectedResponse()
                  + ")\n"
                  + IO.loadString(is));
        }
      }
      // System.err.println(sLine);
      sLine = "";
    } else if (iChar != 13) {
      sLine += (char) iChar;
    }
  }

  for (int iContent = 0; iContent < iContentLength; iContent++) {
    is.read();
  }

  s.close();

  if (sCookie == null) throw new IOException("Didn't obtain cookie");
  return sCookie;
}

public boolean createOnServer(TaskContainer taskMap) throws IOException {

  boolean isAvailable = false;
  logger.debug("Creating socket to '" + controllerIP + "' on port " + controllerPort);
  boolean loading = true;
  Map<Long, String> myCurrentProjects = null;
  while (loading) {
    try {
      Thread.currentThread().sleep(1000);
    } catch (InterruptedException ex) {
      logger.error(ex);
    }
    socketConnection = new Socket(controllerIP, controllerPort);
    output = new ObjectOutputStream(socketConnection.getOutputStream());
    output.flush();

    input = new ObjectInputStream(socketConnection.getInputStream());
    logger.debug("Input- and outputstreams are ready...");

    try {
      output.writeInt(0);
      output.writeObject(taskMap);
      output.flush();
      loading = false;
    } catch (IOException IOExc) {
      IOExc.printStackTrace();
      logger.debug("Could not send results...");
      loading = false;
    }
    try {
      isAvailable = (boolean) input.readBoolean();
      loading = false;
    } catch (IOException IOExc) {
      IOExc.printStackTrace();
      loading = false;
    }
    if (!loading) {
      logger.debug("Tasks were sent !");
      socketConnection.close();
      break;
    }
  }

  return isAvailable;
}

/** Sends given response to the socket. */
private void sendResponse(String status, String mime, Properties header, InputStream data) {
  try {
    if (status == null) throw new Error("sendResponse(): Status can't be null.");

    OutputStream out = mySocket.getOutputStream();
    PrintWriter pw = new PrintWriter(out);
    pw.print("HTTP/1.0 " + status + " \r\n");

    if (mime != null) pw.print("Content-Type: " + mime + "\r\n");

    if (header == null || header.getProperty("Date") == null)
      pw.print("Date: " + gmtFrmt.format(new Date()) + "\r\n");

    if (header != null) {
      Enumeration<Object> e = header.keys();
      while (e.hasMoreElements()) {
        String key = (String) e.nextElement();
        String value = header.getProperty(key);
        pw.print(key + ": " + value + "\r\n");
      }
    }

    pw.print("\r\n");
    pw.flush();

    if (data != null) {
      byte[] buff = new byte[2048];
      while (true) {
        int read = data.read(buff, 0, 2048);
        if (read <= 0) break;
        out.write(buff, 0, read);
      }
    }
    out.flush();
    out.close();
    if (data != null) data.close();
  } catch (IOException ioe) {
    // Couldn't write? No can do.
    try {
      mySocket.close();
    } catch (Throwable t) {
    }
  }
}

