@Override
public void run() {
  try {
    cm.registerThread(this);
  } catch (IOException e) {
    stopWorking();
    return;
  }

  while (true) {
    Socket s = null;

    try {
      s = ss.accept();
    } catch (IOException e) {
      stopWorking();
      return;
    }

    Channel cn = null;
    StreamForwarder r2l = null;
    StreamForwarder l2r = null;

    try {
      /* This may fail, e.g., if the remote port is closed (in optimistic terms: not open yet) */

      cn =
          cm.openDirectTCPIPChannel(
              host_to_connect, port_to_connect, s.getInetAddress().getHostAddress(), s.getPort());

    } catch (IOException e) {
      /* Simply close the local socket and wait for the next incoming connection */

      try {
        s.close();
      } catch (IOException ignore) {
      }

      continue;
    }

    try {
      r2l =
          new StreamForwarder(
              cn, null, null, cn.stdoutStream, s.getOutputStream(), "RemoteToLocal");
      l2r = new StreamForwarder(cn, r2l, s, s.getInputStream(), cn.stdinStream, "LocalToRemote");
    } catch (IOException e) {
      try {
        /* This message is only visible during debugging, since we discard the channel immediatelly */
        cn.cm.closeChannel(
            cn, "Weird error during creation of StreamForwarder (" + e.getMessage() + ")", true);
      } catch (IOException ignore) {
      }

      continue;
    }

    r2l.setDaemon(true);
    l2r.setDaemon(true);
    r2l.start();
    l2r.start();
  }
}

