/**
 * Request a list of current jobs as {@link JobPlan} objects.
 *
 * @param clientSocket the socket to the host.
 * @return the list of jobs.
 * @throws ProtocolException if something went wrong.
 */
public static JobList listJobs(final Socket clientSocket) throws ProtocolException {
  try {
    final ObjectOutputStream oos = (ObjectOutputStream) clientSocket.getOutputStream();
    oos.writeObject(MartRunnerProtocol.LIST_JOBS);
    oos.flush();
    return (JobList) ((ObjectInputStream) clientSocket.getInputStream()).readObject();
  } catch (final ClassNotFoundException e) {
    throw new ProtocolException(e);
  } catch (final Throwable e) {
    throw new ProtocolException(Resources.get("protocolIOProbs"), e);
  }
}

