private void processRequest(HttpRequest request, Socket client)
    throws IllegalStateException, IOException {
  if (request == null) {
    return;
  }
  Log.d(LOG_TAG, "processing");
  String url = request.getRequestLine().getUri();

  String cache = getFileName(title);
  File f = new File(cache);
  if (!f.exists()) f.createNewFile();

  HttpResponse realResponse = download(url);
  if (realResponse == null) {
    return;
  }

  Log.d(LOG_TAG, "downloading...");

  InputStream data = realResponse.getEntity().getContent();

  int totalFileSize = -1;
  for (Header h : realResponse.getAllHeaders()) {
    if (h.getName().toLowerCase().equals("content-length"))
      try {
        totalFileSize = Integer.valueOf(h.getValue());
      } catch (NumberFormatException e) {
        // Ignore
      }
  }
  StringBuilder httpString = new StringBuilder();
  httpString.append("HTTP/1.1 200 OK\r\n");
  httpString.append("Content-Type: audio/mpeg\r\n");
  httpString.append("Connection: close\r\n");
  httpString.append("Accept-Ranges: bytes\r\n");
  if (totalFileSize != -1) httpString.append("Content-Length: " + totalFileSize + "\r\n");
  httpString.append("Content-Disposition: inline; filename=" + cache + ".mp3\r\n\r\n");

  Log.d(LOG_TAG, "headers done");

  OutputStream output = new FileOutputStream(cache);

  try {
    byte[] buffer = httpString.toString().getBytes();
    int readBytes;
    Log.d(LOG_TAG, "writing to client");
    client.getOutputStream().write(buffer, 0, buffer.length);

    // Start streaming content.
    byte[] buff = new byte[1024 * 250];
    while (isRunning && (readBytes = data.read(buff, 0, buff.length)) != -1) {
      client.getOutputStream().write(buff, 0, readBytes);
      output.write(buff, 0, readBytes);
      client.getOutputStream().flush();
      output.flush();
    }
  } catch (Exception e) {
    Log.e("", e.getMessage(), e);
  } finally {
    if (data != null) {
      data.close();
    }
    if (output != null) {
      output.close();
    }
    client.close();
  }
}

@Test
public void testSendData() throws UnknownHostException, IOException, InterruptedException {
  CSThread csThread = new CSThread();
  csThread.start();
  // Wait for CS to bind socket...
  Thread.sleep(1000);
  Socket socket = new Socket();
  try {
    socket.connect(new InetSocketAddress("localhost", 11233));
    OutputStream stream = socket.getOutputStream();

    // Try sending garbage
    String msg = "gfenrwjgfe fbd fbb fwwq";
    stream.write(msg.getBytes());
    Thread.sleep(1000); // Allow for overhead of network
    assertNull(lastVessel); // Should NOT have called update()

    // Try sending netstring-encoded garbage
    msg = "gfenrwjgfe fbd fbb fwwq";
    msg = msg.length() + ":" + msg; // Netstring encoding
    stream.write(msg.getBytes());
    Thread.sleep(1000); // Allow for overhead of network
    assertNull(lastVessel); // Should NOT have called update()

    // Try sending bad vessel values
    msg = "{\"id\":[1,2,3],\"type\":\"WRONGENUMVALUE\",\"coords\":0,\"course\":{}}";
    msg = msg.length() + ":" + msg; // Netstring encoding
    stream.write(msg.getBytes());
    Thread.sleep(1000); // Allow for overhead of network
    assertNull(lastVessel); // Should NOT have called update()

    // Try sending correct data
    Calendar curTime = Calendar.getInstance();
    UpdateData ud =
        new UpdateData(
            "myid", VesselType.CARGO_BOAT, new Coord(10, -10), new Course(20, -20), curTime);
    msg = ud.toJSON();
    msg = msg.length() + ":" + msg + ",";
    stream.write(msg.getBytes()); // Write with netstring encoding
    Thread.sleep(1000); // Allow for overhead of network
    assertNotNull(lastVessel);
    Calendar timestamp = lastVessel.getLastTimestamp();
    assertEquals(curTime, timestamp);
    assertEquals("myid", lastVessel.getId());
    assertEquals(VesselType.CARGO_BOAT, lastVessel.getType());

    try {
      assertEquals(new Coord(10, -10), lastVessel.getCoord(timestamp));
      assertEquals(new Course(20, -20), lastVessel.getCourse(timestamp));
    } catch (Exception e) {
      fail("Caught exception: " + e.getMessage());
    }
  } finally {

    cs.stop();
    socket.close();
    csThread.join(); // Wait for ConnectionServer to cleanly shut down
  }
}

public void run() {
  try {
    socket = new ServerSocket(port);

  } catch (IOException e) {
    log.error("Unable to start server with name {}", name, e);
    return;
  }
  running = true;

  log.info("{} Server started", name);

  // listen for conenctions to the server
  while (running) {
    try {
      /*
       * before we check for new clients, let update the current list
       * of clients
       */
      updateConnectedClients();

      // wait for connections
      Socket clientSocket = socket.accept();
      log.info("User connected to {} server", name);

      // check if server is not full
      if (connectedClients.size() < maxConnections) {
        // create the client, and add to the connected clients
        T rPIClient = createClient(clientSocket);
        connectedClients.put(rPIClient.getId(), rPIClient);
        notifyConnectionListeners(true, rPIClient);
      } else {
        log.info("No more connections allow to server {}", name);
        // we are at max connections.
        // TODO: why is this using a print writer.
        PrintWriter out = new PrintWriter(clientSocket.getOutputStream());
        out.println("No more connections allowed, sorry. Come back later.");
        out.close();
        clientSocket.close();
      }
    } catch (IOException e) {
      log.error("A server error has occured on server {}", name, e);
    }
  }
  /*
   * disconnectClients();
   * log.info("Server {} has stopped", name);
   */
}

