private Message sendAXFR(Message query) throws IOException {
  byte[] out, in;
  Socket s;
  int inLength;
  DataInputStream dataIn;
  int soacount = 0;
  Message m, response;
  boolean first = true;

  s = new Socket(addr, port);

  try {
    query = (Message) query.clone();
    if (tsig != null) tsig.apply(query, null);

    out = query.toWire();
    if (Options.check("verbosemsg")) System.err.println(hexdump.dump("out", out));
    OutputStream sOut = s.getOutputStream();
    new DataOutputStream(sOut).writeShort(out.length);
    sOut.write(out);
    s.setSoTimeout(timeoutValue);

    response = new Message();
    response.getHeader().setID(query.getHeader().getID());
    if (tsig != null) {
      tsig.verifyAXFRStart();
      response.TSIGsigned = true;
      response.TSIGverified = true;
    }
    while (soacount < 2) {
      try {
        InputStream sIn = s.getInputStream();
        dataIn = new DataInputStream(sIn);
        inLength = dataIn.readUnsignedShort();
        in = new byte[inLength];
        dataIn.readFully(in);
      } catch (IOException e) {
        if (Options.check("verbose")) {
          System.err.println(";;" + e);
          System.err.println(";; No response");
        }
        throw e;
      }
      if (Options.check("verbosemsg")) System.err.println(hexdump.dump("in", in));
      try {
        m = new Message(in);
      } catch (IOException e) {
        throw new WireParseException("Error parsing message");
      }
      if (m.getHeader().getRcode() != Rcode.NOERROR) {
        if (soacount == 0) return m;
        else {
          if (Options.check("verbosemsg")) {
            System.err.println("Invalid AXFR packet: ");
            System.err.println(m);
          }
          throw new WireParseException("Invalid AXFR message");
        }
      }
      if (m.getHeader().getCount(Section.QUESTION) > 1
          || m.getHeader().getCount(Section.ANSWER) <= 0
          || m.getHeader().getCount(Section.AUTHORITY) != 0) {
        if (Options.check("verbosemsg")) {
          System.err.println("Invalid AXFR packet: ");
          System.err.println(m);
        }
        throw new WireParseException("Invalid AXFR message");
      }
      for (int i = 1; i < 4; i++) {
        Enumeration e = m.getSection(i);
        while (e.hasMoreElements()) {
          Record r = (Record) e.nextElement();
          response.addRecord(r, i);
          if (r instanceof SOARecord) soacount++;
        }
      }
      if (tsig != null) {
        boolean required = (soacount > 1 || first);
        byte error = tsig.verifyAXFR(m, in, query.getTSIG(), required, first);
        boolean ok = (error == Rcode.NOERROR);
        if (!ok) response.TSIGverified = false;
        if (Options.check("verbose")) {
          String status;
          if (m.getTSIG() == null) {
            if (!ok) status = "expected";
            else status = "<>";
          } else {
            if (!ok) status = "failed";
            else status = "ok";
          }
          System.err.println("TSIG verify: " + status);
        }
      }
      first = false;
    }
  } finally {
    s.close();
  }
  return response;
}

