/**
 * Establish a connection with the Leader found by findLeader. Retries 5 times before giving up.
 *
 * @param addr - the address of the Leader to connect to.
 * @throws IOException - if the socket connection fails on the 5th attempt
 * @throws ConnectException
 * @throws InterruptedException
 */
protected void connectToLeader(InetSocketAddress addr)
    throws IOException, ConnectException, InterruptedException {
  sock = new Socket();
  sock.setSoTimeout(self.tickTime * self.initLimit);
  for (int tries = 0; tries < 5; tries++) {
    try {
      sock.connect(addr, self.tickTime * self.syncLimit);
      sock.setTcpNoDelay(nodelay);
      break;
    } catch (IOException e) {
      if (tries == 4) {
        LOG.error("Unexpected exception", e);
        throw e;
      } else {
        LOG.warn("Unexpected exception, tries=" + tries + ", connecting to " + addr, e);
        sock = new Socket();
        sock.setSoTimeout(self.tickTime * self.initLimit);
      }
    }
    Thread.sleep(1000);
  }
  leaderIs = BinaryInputArchive.getArchive(new BufferedInputStream(sock.getInputStream()));
  bufferedOutput = new BufferedOutputStream(sock.getOutputStream());
  leaderOs = BinaryOutputArchive.getArchive(bufferedOutput);
}

public void start(String[] args) {

  try {

    int port = 11150;
    if (args.length > 0) {
      port = Integer.parseInt(args[0]);
    }
    Socket s = new Socket("localhost", port);
    out = new ObjectOutputStream(s.getOutputStream());
    in = new ObjectInputStream(s.getInputStream());

    out.writeObject(new Message(Message.CMD_IDENTIFY_CLIENT, "Console"));
    Response res = (Response) in.readObject();
    System.out.println(res);

    System.out.println("Enter commands or 'exit' to exit.");
    BufferedReader bis = new BufferedReader(new InputStreamReader(System.in));
    String line;
    try {
      while (true) {
        System.out.println();
        System.out.print(":");
        line = bis.readLine();

        if (line.equals("exit")) {
          break;
        }

        Message msg;
        int idx = line.indexOf(' ');
        if (idx != -1) {
          String cmd = line.substring(0, idx);
          String arg = line.substring(idx + 1);
          msg = new Message(cmd, arg);
        } else {
          msg = new Message(line);
        }

        long start = System.currentTimeMillis();
        System.out.println("Sending " + msg);
        out.writeObject(msg);
        Response response = (Response) in.readObject();

        System.out.println("[" + (System.currentTimeMillis() - start) + "] >> " + response);
        if (response.getData() != null) {
          System.out.println(response.getData());
        }
      }
    } catch (IOException e) {
      e.printStackTrace();
    }

    out.close();
    in.close();
    s.close();

  } catch (Exception e) {
    e.printStackTrace();
  }
}

