/**
 * Finds the object exported by the server with the specified name. It sends a POST request to the
 * server (via an http proxy server if needed).
 *
 * @param name the name of the exported object.
 * @return the proxy object.
 */
public Object lookupObject(String name) throws ObjectNotFoundException {
  try {
    Socket sock = new Socket(servername, port);
    OutputStream out = sock.getOutputStream();
    out.write(lookupCommand);
    out.write(endofline);
    out.write(endofline);

    ObjectOutputStream dout = new ObjectOutputStream(out);
    dout.writeUTF(name);
    dout.flush();

    InputStream in = new BufferedInputStream(sock.getInputStream());
    skipHeader(in);
    ObjectInputStream din = new ObjectInputStream(in);
    int n = din.readInt();
    String classname = din.readUTF();
    din.close();
    dout.close();
    sock.close();

    if (n >= 0) return createProxy(n, classname);
  } catch (Exception e) {
    e.printStackTrace();
    throw new ObjectNotFoundException(name, e);
  }

  throw new ObjectNotFoundException(name);
}

