/**
 * getObject(Key key, String provider) method testing. Tests if the proper exception is thrown in
 * the case of incorrect input parameters and if the object sealed with encryption algorithm can
 * be retrieved by specifying the cryptographic key and provider name.
 */
public void testGetObject3() throws Exception {
  try {
    new SealedObject("secret string", new NullCipher())
        .getObject(new SecretKeySpec(new byte[] {0, 0, 0}, "algorithm"), null);
    fail("IllegalArgumentException should be thrown in the case of " + "null provider.");
  } catch (IllegalArgumentException e) {
  }

  try {
    new SealedObject("secret string", new NullCipher())
        .getObject(new SecretKeySpec(new byte[] {0, 0, 0}, "algorithm"), "");
    fail("IllegalArgumentException should be thrown in the case of " + "empty provider.");
  } catch (IllegalArgumentException e) {
  }

  KeyGenerator kg = KeyGenerator.getInstance("DES");
  Key key = kg.generateKey();

  Cipher cipher = Cipher.getInstance("DES");
  String provider = cipher.getProvider().getName();
  cipher.init(Cipher.ENCRYPT_MODE, key);

  String secret = "secret string";
  SealedObject so = new SealedObject(secret, cipher);

  cipher.init(Cipher.DECRYPT_MODE, key);
  assertEquals(
      "The returned object does not equals to the " + "original object.",
      secret,
      so.getObject(key, provider));

  kg = KeyGenerator.getInstance("DESede");
  key = kg.generateKey();

  try {
    so.getObject(key, provider);
    fail("InvalidKeyException expected");
  } catch (InvalidKeyException e) {
    // expected
  }

  try {
    so.getObject(key, "Wrong provider name");
    fail("NoSuchProviderException expected");
  } catch (NoSuchProviderException e) {
    // expected
  }
}

/**
 * Creates the LM Hash of the user's password.
 *
 * @param password The password.
 * @return The LM Hash of the given password, used in the calculation of the LM Response.
 */
private static byte[] lmHash(String password) throws NTLMEngineException {
  try {
    byte[] oemPassword = password.toUpperCase().getBytes("US-ASCII");
    int length = Math.min(oemPassword.length, 14);
    byte[] keyBytes = new byte[14];
    System.arraycopy(oemPassword, 0, keyBytes, 0, length);
    Key lowKey = createDESKey(keyBytes, 0);
    Key highKey = createDESKey(keyBytes, 7);
    byte[] magicConstant = "KGS!@#$%".getBytes("US-ASCII");
    Cipher des = Cipher.getInstance("DES/ECB/NoPadding");
    des.init(Cipher.ENCRYPT_MODE, lowKey);
    byte[] lowHash = des.doFinal(magicConstant);
    des.init(Cipher.ENCRYPT_MODE, highKey);
    byte[] highHash = des.doFinal(magicConstant);
    byte[] lmHash = new byte[16];
    System.arraycopy(lowHash, 0, lmHash, 0, 8);
    System.arraycopy(highHash, 0, lmHash, 8, 8);
    return lmHash;
  } catch (Exception e) {
    throw new NTLMEngineException(e.getMessage(), e);
  }
}

