/** javax.crypto.Cipher#getOutputSize(int) */
public void test_getOutputSizeI() throws Exception {

  SecureRandom sr = new SecureRandom();
  Cipher cipher = Cipher.getInstance(algorithm + "/ECB/PKCS5Padding");

  try {
    cipher.getOutputSize(25);
    fail();
  } catch (IllegalStateException expected) {
  }

  cipher.init(Cipher.ENCRYPT_MODE, cipherKey, sr);

  // A 25-byte input could result in at least 4 8-byte blocks
  int result = cipher.getOutputSize(25);
  assertTrue("Output size too small", result > 31);

  // A 8-byte input should result in 2 8-byte blocks
  result = cipher.getOutputSize(8);
  assertTrue("Output size too small", result > 15);
}

private void testWriteRead(
    String name, Key key, boolean authenticated, boolean useBc, boolean blocks, byte[] data) {
  ByteArrayOutputStream bOut = new ByteArrayOutputStream();

  try {
    Cipher encrypt = Cipher.getInstance(name, "BC");
    Cipher decrypt = Cipher.getInstance(name, "BC");
    encrypt.init(Cipher.ENCRYPT_MODE, key);
    if (encrypt.getIV() != null) {
      decrypt.init(Cipher.DECRYPT_MODE, key, new IvParameterSpec(encrypt.getIV()));
    } else {
      decrypt.init(Cipher.DECRYPT_MODE, key);
    }

    OutputStream cOut = createOutputStream(bOut, encrypt, useBc);
    if (blocks) {
      int chunkSize = Math.max(1, data.length / 8);
      for (int i = 0; i < data.length; i += chunkSize) {
        cOut.write(data, i, Math.min(chunkSize, data.length - i));
      }
    } else {
      for (int i = 0; i < data.length; i++) {
        cOut.write(data[i]);
      }
    }
    cOut.close();

    byte[] cipherText = bOut.toByteArray();
    bOut.reset();
    InputStream cIn = createInputStream(cipherText, decrypt, useBc);

    if (blocks) {
      byte[] block = new byte[encrypt.getBlockSize() + 1];
      int c;
      while ((c = cIn.read(block)) >= 0) {
        bOut.write(block, 0, c);
      }
    } else {
      int c;
      while ((c = cIn.read()) >= 0) {
        bOut.write(c);
      }
    }
    cIn.close();

  } catch (Exception e) {
    fail("Unexpected exception " + name, e, authenticated, useBc);
  }

  byte[] decrypted = bOut.toByteArray();
  if (!Arrays.areEqual(data, decrypted)) {
    fail("Failed - decrypted data doesn't match: " + name, authenticated, useBc);
  }
}

